/**
 * Destiny Calendar API
 * Saju + Astrology fused yearly important dates
 * AI-assisted calculations (optional backend)
 */

import { NextRequest, NextResponse } from 'next/server'
import { withApiMiddleware, createPublicStreamGuard, type ApiContext } from '@/lib/api/middleware'
import {
  calculateYearlyImportantDates,
  type EventCategory,
} from '@/lib/destiny-map/destinyCalendar'
import { calculateSajuData } from '@/lib/Saju/saju'
import { calculateNatalChart } from '@/lib/astrology/foundation/astrologyService'
import { STEM_TO_ELEMENT_EN as STEM_TO_ELEMENT } from '@/lib/Saju/stemElementMapping'
import koTranslations from '@/i18n/locales/ko'
import enTranslations from '@/i18n/locales/en'
import type { TranslationData } from '@/types/calendar-api'
import { logger } from '@/lib/logger'
import { cacheOrCalculate, CacheKeys, CACHE_TTL } from '@/lib/cache/redis-cache'
import { calendarMainQuerySchema } from '@/lib/api/zodValidation'

// Import from extracted modules
import {
  getPillarStemName,
  getPillarBranchName,
  parseBirthDate,
  formatDateForResponse,
  fetchAIDates,
  LOCATION_COORDS,
} from './lib'

export const dynamic = 'force-dynamic'

import { ALLOWED_LOCALES } from '@/lib/constants/api-limits'
import { TIME_RE, LIMITS } from '@/lib/validation/patterns'
import { HTTP_STATUS } from '@/lib/constants/http'
const VALID_CALENDAR_LOCALES = ALLOWED_LOCALES
const VALID_CALENDAR_GENDERS = new Set(['male', 'female'])
const VALID_CALENDAR_CATEGORIES: readonly EventCategory[] = [
  'wealth',
  'career',
  'love',
  'health',
  'travel',
  'study',
  'general',
]
const _VALID_CALENDAR_PLACES = new Set(Object.keys(LOCATION_COORDS))
const CALENDAR_TIME_RE = TIME_RE
const CALENDAR_YEAR_RE = /^\d{4}$/
const MAX_PLACE_LEN = LIMITS.PLACE

/**
 * GET /api/calendar
 * 중요 날짜 조회 (인증 불필요)
 *
 * Query params:
 * - birthDate: 생년월일 (YYYY-MM-DD) - 필수
 * - birthTime: 출생시간 (HH:MM) - 선택
 * - birthPlace: 출생장소 - 선택
 * - year: 연도 (기본: 현재년도)
 * - category: 카테고리 필터
 * - locale: 언어 (ko, en)
 */
export const GET = withApiMiddleware(
  async (request: NextRequest, _context: ApiContext) => {
    const { searchParams } = new URL(request.url)

    // Validate query params with Zod
    const queryValidation = calendarMainQuerySchema.safeParse({
      birthDate: searchParams.get('birthDate'),
      birthTime: searchParams.get('birthTime') || undefined,
      birthPlace: searchParams.get('birthPlace') || undefined,
      year: searchParams.get('year') || undefined,
      gender: searchParams.get('gender') || undefined,
      locale: searchParams.get('locale') || undefined,
      category: searchParams.get('category') || undefined,
    })
    if (!queryValidation.success) {
      logger.warn('[Calendar] query validation failed', { errors: queryValidation.error.issues })
      return NextResponse.json(
        {
          error: 'validation_failed',
          details: queryValidation.error.issues.map((e) => ({
            path: e.path.join('.'),
            message: e.message,
          })),
        },
        { status: HTTP_STATUS.BAD_REQUEST }
      )
    }

    const {
      birthDate: birthDateParam,
      birthTime: birthTimeParam,
      birthPlace: birthPlaceRaw,
      year: yearFromZod,
      gender,
      locale,
      category,
    } = queryValidation.data
    const year = yearFromZod ?? new Date().getFullYear()
    const birthPlace =
      birthPlaceRaw.length > 0 && birthPlaceRaw.length <= MAX_PLACE_LEN ? birthPlaceRaw : 'Seoul'

    // 생년월일 파싱 (UTC 오프셋 영향 없이 고정)
    const birthDate = parseBirthDate(birthDateParam)
    if (!birthDate) {
      return NextResponse.json({ error: 'Invalid birth date' }, { status: HTTP_STATUS.BAD_REQUEST })
    }
    // birthPlace는 항상 유효한 값이 있음 (기본값: Seoul)
    const coords = LOCATION_COORDS[birthPlace] || LOCATION_COORDS['Seoul']
    const timezone = coords.tz

    // ✅ 정확한 사주 계산 (saju.ts 사용 - 절기 기반 월주, 자시 교차 처리)
    let sajuResult
    try {
      const sajuGender = gender.toLowerCase() === 'female' ? ('female' as const) : ('male' as const)
      sajuResult = calculateSajuData(birthDateParam, birthTimeParam, sajuGender, 'solar', timezone)
    } catch (sajuError) {
      logger.error('[Calendar] Saju calculation error:', sajuError)
      return NextResponse.json(
        { error: 'Failed to calculate saju data' },
        { status: HTTP_STATUS.SERVER_ERROR }
      )
    }

    // 사주 데이터에서 필요한 정보 추출
    // Null safety: pillars 객체가 없을 수 있음
    const sajuPillars = sajuResult?.pillars || {}
    const pillars = {
      year: {
        stem: getPillarStemName(sajuPillars.year),
        branch: getPillarBranchName(sajuPillars.year),
      },
      month: {
        stem: getPillarStemName(sajuPillars.month),
        branch: getPillarBranchName(sajuPillars.month),
      },
      day: {
        stem: getPillarStemName(sajuPillars.day),
        branch: getPillarBranchName(sajuPillars.day),
      },
      hour: {
        stem: getPillarStemName(sajuPillars.time),
        branch: getPillarBranchName(sajuPillars.time),
      },
    }

    const dayMasterStem = pillars.day.stem
    const dayMasterElement = STEM_TO_ELEMENT[dayMasterStem] || 'wood'

    // 대운 추출 - DaeunCycle 타입에 맞춤
    const daeunCycles =
      sajuResult.unse?.daeun
        ?.map((d) => ({
          age: d.age || 0,
          heavenlyStem: d.heavenlyStem || '',
          earthlyBranch: d.earthlyBranch || '',
        }))
        .filter((d) => d.heavenlyStem && d.earthlyBranch) || []

    const sajuProfile = {
      dayMaster: dayMasterStem,
      dayMasterElement,
      dayBranch: pillars.day.branch,
      birthYear: birthDate.getFullYear(),
      yearBranch: pillars.year.branch,
      daeunCycles,
      daeunsu: sajuResult.daeWoon?.startAge ?? 0,
      pillars,
    }

    // ✅ 정확한 점성술 계산 (Swiss Ephemeris 사용)
    const [birthHour, birthMinute] = birthTimeParam.split(':').map(Number)
    let astroProfile
    try {
      const natalChart = await calculateNatalChart({
        year: birthDate.getFullYear(),
        month: birthDate.getMonth() + 1,
        date: birthDate.getDate(),
        hour: birthHour || 12,
        minute: birthMinute || 0,
        latitude: coords.lat,
        longitude: coords.lng,
        timeZone: timezone,
      })

      // 태양 정보 추출
      const sunPlanet = natalChart.planets.find((p) => p.name === 'Sun')
      const sunSign = sunPlanet?.sign || 'Aries'
      const sunLongitude = sunPlanet?.longitude || 0

      // 별자리 → 오행 매핑
      const ZODIAC_TO_ELEMENT: Record<string, string> = {
        Aries: 'fire',
        Leo: 'fire',
        Sagittarius: 'fire',
        Taurus: 'earth',
        Virgo: 'earth',
        Capricorn: 'earth',
        Gemini: 'metal',
        Libra: 'metal',
        Aquarius: 'metal', // air → metal
        Cancer: 'water',
        Scorpio: 'water',
        Pisces: 'water',
      }

      astroProfile = {
        sunSign,
        sunElement: ZODIAC_TO_ELEMENT[sunSign] || 'fire',
        sunLongitude,
        birthMonth: birthDate.getMonth() + 1,
        birthDay: birthDate.getDate(),
      }
    } catch (astroError) {
      logger.warn('[Calendar] Astrology calculation fallback:', astroError)
      // 폴백: 단순 계산
      const month = birthDate.getMonth()
      const day = birthDate.getDate()
      let sunSign = 'Aries'
      if ((month === 2 && day >= 21) || (month === 3 && day <= 19)) {
        sunSign = 'Aries'
      } else if ((month === 3 && day >= 20) || (month === 4 && day <= 20)) {
        sunSign = 'Taurus'
      } else if ((month === 4 && day >= 21) || (month === 5 && day <= 20)) {
        sunSign = 'Gemini'
      } else if ((month === 5 && day >= 21) || (month === 6 && day <= 22)) {
        sunSign = 'Cancer'
      } else if ((month === 6 && day >= 23) || (month === 7 && day <= 22)) {
        sunSign = 'Leo'
      } else if ((month === 7 && day >= 23) || (month === 8 && day <= 22)) {
        sunSign = 'Virgo'
      } else if ((month === 8 && day >= 23) || (month === 9 && day <= 22)) {
        sunSign = 'Libra'
      } else if ((month === 9 && day >= 23) || (month === 10 && day <= 21)) {
        sunSign = 'Scorpio'
      } else if ((month === 10 && day >= 22) || (month === 11 && day <= 21)) {
        sunSign = 'Sagittarius'
      } else if ((month === 11 && day >= 22) || (month === 0 && day <= 19)) {
        sunSign = 'Capricorn'
      } else if ((month === 0 && day >= 20) || (month === 1 && day <= 18)) {
        sunSign = 'Aquarius'
      } else {
        sunSign = 'Pisces'
      }

      const ZODIAC_TO_ELEMENT: Record<string, string> = {
        Aries: 'fire',
        Leo: 'fire',
        Sagittarius: 'fire',
        Taurus: 'earth',
        Virgo: 'earth',
        Capricorn: 'earth',
        Gemini: 'metal',
        Libra: 'metal',
        Aquarius: 'metal',
        Cancer: 'water',
        Scorpio: 'water',
        Pisces: 'water',
      }

      astroProfile = {
        sunSign,
        sunElement: ZODIAC_TO_ELEMENT[sunSign] || 'fire',
        birthMonth: birthDate.getMonth() + 1,
        birthDay: birthDate.getDate(),
      }
    }

    // 로컬 계산으로 중요 날짜 가져오기 (Redis 캐싱 적용)
    const cacheKey = CacheKeys.yearlyCalendar(
      birthDateParam,
      birthTimeParam,
      gender,
      year,
      category || undefined
    )
    const localDates = await cacheOrCalculate(
      cacheKey,
      async () => {
        return calculateYearlyImportantDates(year, sajuProfile, astroProfile, {
          minGrade: 5, // grade 5 (최악의 날)까지 포함
        })
      },
      CACHE_TTL.CALENDAR_DATA // 1 day
    )

    // 카테고리 필터링
    let filteredDates = localDates
    if (category) {
      filteredDates = localDates.filter((d) => d.categories.includes(category))
    }

    // AI 백엔드에서 추가 정보 시도
    const sajuData = {
      birth_date: birthDateParam,
      birth_time: birthTimeParam,
      gender: 'unknown',
      day_master: pillars.day.stem,
      pillars,
      elements: sajuProfile,
    }

    const astroData = {
      birth_date: birthDateParam,
      birth_time: birthTimeParam,
      latitude: coords.lat,
      longitude: coords.lng,
      timezone: coords.tz,
      sun_sign: astroProfile.sunSign,
      planets: {
        sun: { sign: astroProfile.sunSign, degree: 15 },
        moon: { sign: astroProfile.sunSign, degree: 15 },
      },
    }

    // AI 백엔드 호출 시도
    const aiDates = await fetchAIDates(sajuData, astroData, category || 'overall')

    // 6등급별 그룹화
    const grade0 = filteredDates.filter((d) => d.grade === 0) // 천운의 날
    const grade1 = filteredDates.filter((d) => d.grade === 1) // 아주 좋은 날
    const grade2 = filteredDates.filter((d) => d.grade === 2) // 좋은 날
    const grade3 = filteredDates.filter((d) => d.grade === 3) // 보통 날
    const grade4 = filteredDates.filter((d) => d.grade === 4) // 나쁜 날
    const grade5 = filteredDates.filter((d) => d.grade === 5) // 최악의 날

    // AI 날짜 병합
    let aiEnhanced = false
    if (aiDates) {
      aiEnhanced = true
      // AI 날짜를 기존 날짜에 병합 가능
    }

    const res = NextResponse.json({
      success: true,
      type: 'yearly',
      year,
      aiEnhanced,
      birthInfo: {
        date: birthDateParam,
        time: birthTimeParam,
        place: birthPlace,
      },
      summary: {
        total: filteredDates.length,
        grade0: grade0.length, // 천운의 날
        grade1: grade1.length, // 아주 좋은 날
        grade2: grade2.length, // 좋은 날
        grade3: grade3.length, // 보통 날
        grade4: grade4.length, // 나쁜 날
        grade5: grade5.length, // 최악의 날
      },
      topDates: (() => {
        // grade0 + grade1 + grade2가 부족하면 grade3 중 높은 점수 날짜도 포함
        const topCandidates = [...grade0, ...grade1, ...grade2]
        if (topCandidates.length < 5) {
          const topGrade3 = grade3
            .sort((a, b) => b.score - a.score)
            .slice(0, 5 - topCandidates.length)
          topCandidates.push(...topGrade3)
        }
        return topCandidates
          .slice(0, 10)
          .map((d) =>
            formatDateForResponse(
              d,
              locale,
              koTranslations as unknown as TranslationData,
              enTranslations as unknown as TranslationData
            )
          )
      })(),
      goodDates: [...grade1, ...grade2]
        .slice(0, 20)
        .map((d) =>
          formatDateForResponse(
            d,
            locale,
            koTranslations as unknown as TranslationData,
            enTranslations as unknown as TranslationData
          )
        ),
      badDates: [...grade5, ...grade4]
        .slice(0, 10)
        .map((d) =>
          formatDateForResponse(
            d,
            locale,
            koTranslations as unknown as TranslationData,
            enTranslations as unknown as TranslationData
          )
        ),
      worstDates: grade5
        .slice(0, 5)
        .map((d) =>
          formatDateForResponse(
            d,
            locale,
            koTranslations as unknown as TranslationData,
            enTranslations as unknown as TranslationData
          )
        ),
      allDates: filteredDates.map((d) =>
        formatDateForResponse(
          d,
          locale,
          koTranslations as unknown as TranslationData,
          enTranslations as unknown as TranslationData
        )
      ),
      ...(aiDates && {
        aiInsights: {
          auspicious: aiDates.auspicious,
          caution: aiDates.caution,
        },
      }),
    })

    res.headers.set('Cache-Control', 'no-store')
    return res
  },
  createPublicStreamGuard({
    route: 'calendar',
    limit: 30,
    windowSeconds: 60,
  })
)

﻿import { NextRequest, NextResponse } from 'next/server'
import { withApiMiddleware, createPublicStreamGuard, type ApiContext } from '@/lib/api/middleware'
import { apiClient } from '@/lib/api/ApiClient'
import { getServerSession } from 'next-auth'
import { authOptions } from '@/lib/auth/authOptions'
import { prisma } from '@/lib/db/prisma'
import { captureServerError } from '@/lib/telemetry'
import { LIMITS } from '@/lib/validation'
import { sanitizeString } from '@/lib/api/sanitizers'
import { logger } from '@/lib/logger'
import { calculateSajuData } from '@/lib/Saju/saju'
import { calculateSajuCompatibilityOnly } from '@/lib/compatibility/cosmicCompatibility'
import type { SajuProfile } from '@/lib/compatibility/cosmicCompatibility'
import type { Relation, PersonInput, CompatibilityBackendResponse } from './types'
import { compatibilityRequestSchema } from '@/lib/api/zodValidation'

function bad(msg: string, status = 400) {
  return NextResponse.json({ error: msg }, { status })
}

function relationWeight(r?: Relation) {
  if (!r) {
    return 1.0
  }
  if (r === 'lover') {
    return 1.0
  }
  if (r === 'friend') {
    return 0.95
  }
  return 0.9 // other
}

const MAX_NOTE = LIMITS.NOTE

function buildSajuProfileFromBirth(
  date: string,
  time: string,
  timezone: string
): SajuProfile | null {
  try {
    const result = calculateSajuData(date, time, 'male', 'solar', timezone)
    return {
      dayMaster: {
        name: result.dayMaster.name,
        element: result.dayMaster.element,
        yin_yang: result.dayMaster.yin_yang === '양' ? 'yang' : 'yin',
      },
      pillars: {
        year: {
          stem: result.yearPillar.heavenlyStem.name,
          branch: result.yearPillar.earthlyBranch.name,
        },
        month: {
          stem: result.monthPillar.heavenlyStem.name,
          branch: result.monthPillar.earthlyBranch.name,
        },
        day: {
          stem: result.dayPillar.heavenlyStem.name,
          branch: result.dayPillar.earthlyBranch.name,
        },
        time: {
          stem: result.timePillar.heavenlyStem.name,
          branch: result.timePillar.earthlyBranch.name,
        },
      },
      elements: result.fiveElements,
    }
  } catch (e) {
    logger.warn('[Compatibility] Saju profile build failed:', e)
    return null
  }
}

export const POST = withApiMiddleware(
  async (req: NextRequest, _context: ApiContext) => {
    const rawBody = await req.json()

    // Validate with Zod
    const validationResult = compatibilityRequestSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[Compatibility] validation failed', { errors: validationResult.error.issues })
      return NextResponse.json(
        {
          error: 'validation_failed',
          details: validationResult.error.issues.map((e) => ({
            path: e.path.join('.'),
            message: e.message,
          })),
        },
        { status: 400 }
      )
    }

    const body = validationResult.data
    const persons: PersonInput[] = body.persons.map(
      (p, i) =>
        ({
          name: sanitizeString(p.name, LIMITS.NAME),
          date: p.date,
          time: p.time,
          latitude: p.latitude,
          longitude: p.longitude,
          timeZone: p.timeZone,
          city: sanitizeString(p.city, LIMITS.CITY),
          relationToP1: i > 0 ? p.relationToP1 : undefined,
          relationNoteToP1: sanitizeString(p.relationNoteToP1, MAX_NOTE),
        }) as PersonInput
    )

    const names = persons.map((p, i) => p.name?.trim() || `Person ${i + 1}`)

    const pairs: [number, number][] = []
    for (let i = 0; i < persons.length; i++) {
      for (let j = i + 1; j < persons.length; j++) {
        pairs.push([i, j])
      }
    }

    // Build Saju profiles once per person (cache to avoid redundant computation)
    const sajuProfiles: (SajuProfile | null)[] = persons.map((p) =>
      buildSajuProfileFromBirth(p.date, p.time, p.timeZone)
    )

    const scores = pairs.map(([a, b]) => {
      const profileA = sajuProfiles[a]
      const profileB = sajuProfiles[b]

      let base = 65 // neutral default if saju calculation fails
      if (profileA && profileB) {
        try {
          const { score: sajuScore } = calculateSajuCompatibilityOnly(profileA, profileB)
          base = sajuScore
        } catch (e) {
          logger.warn('[Compatibility] Saju scoring failed for pair:', { a, b, error: e })
        }
      }

      let weight = 1.0
      if (a === 0) {
        weight = relationWeight(persons[b].relationToP1)
      } else if (b === 0) {
        weight = relationWeight(persons[a].relationToP1)
      } else {
        weight =
          (relationWeight(persons[a].relationToP1) + relationWeight(persons[b].relationToP1)) / 2
      }

      const score = Math.round(base * weight)
      return { pair: [a, b], score }
    })

    const avg = Math.round(scores.reduce((s, x) => s + x.score, 0) / scores.length)

    const lines: string[] = []
    lines.push(`## 종합 궁합 점수`)
    lines.push('')
    lines.push('사주(四柱) 일간 오행 궁합 분석 결과입니다.')
    lines.push('')
    lines.push(`${names.slice(0, 2).join(' & ')}의 궁합 점수: ${avg}점/100`)
    lines.push('')
    lines.push(`## 관계 분석`)
    lines.push('')
    lines.push('사주(四柱) 일간 오행 궁합 분석 결과입니다.')
    lines.push('')
    for (let i = 1; i < persons.length; i++) {
      const r = persons[i].relationToP1!
      const rLabel =
        r === 'lover' ? '연인' : r === 'friend' ? '친구' : persons[i].relationNoteToP1 || '기타'
      lines.push(`${names[0]} ↔ ${names[i]}: ${rLabel}`)
    }
    lines.push('')
    lines.push(`## 상세 점수`)
    lines.push('')
    lines.push('사주(四柱) 일간 오행 궁합 분석 결과입니다.')
    lines.push('')
    scores.forEach(({ pair: [a, b], score }) => {
      lines.push(`${names[a]} & ${names[b]}: ${score}점/100`)
    })
    lines.push('')
    lines.push(`## 조언`)
    lines.push('')
    lines.push('사주(四柱) 일간 오행 궁합 분석 결과입니다.')
    lines.push('')
    lines.push(
      'AI 기반 심화 분석을 위해 잠시 후 다시 시도해주세요. 현재는 기본 분석 결과만 제공됩니다.'
    )

    // ======== AI backend call (GPT + Fusion) ========
    let aiInterpretation = ''
    let aiModelUsed = ''
    let aiScore: number | null = null
    let timing: Record<string, unknown> | null = null
    let actionItems: string[] = []
    let isGroup = false
    let groupAnalysis: Record<string, unknown> | null = null
    let synergyBreakdown: Record<string, unknown> | null = null

    try {
      // Send to backend with full birth data for Saju+Astrology fusion analysis
      const response = await apiClient.post<CompatibilityBackendResponse>(
        '/api/compatibility',
        {
          persons: persons.map((p, i) => ({
            name: names[i],
            birthDate: p.date,
            birthTime: p.time,
            latitude: p.latitude,
            longitude: p.longitude,
            timeZone: p.timeZone,
            relation: i > 0 ? p.relationToP1 : undefined,
            relationNote: i > 0 ? p.relationNoteToP1 : undefined,
          })),
          relationship_type: persons[1]?.relationToP1 || 'lover',
          locale: body?.locale || 'ko',
        },
        { timeout: 60000 }
      )

      if (response.ok && response.data) {
        const aiData = response.data
        // Handle both nested (data.report) and flat response formats
        aiInterpretation =
          ('data' in aiData && aiData.data?.report) ||
          ('interpretation' in aiData && aiData.interpretation) ||
          ('report' in aiData && aiData.report) ||
          ''
        aiModelUsed =
          ('data' in aiData && aiData.data?.model) ||
          ('model' in aiData && aiData.model) ||
          'gpt-4o'
        // Get the AI-calculated overall score (Saju+Astrology fusion)
        aiScore =
          ('data' in aiData && aiData.data?.overall_score) ||
          ('overall_score' in aiData && aiData.overall_score) ||
          null
        // Get timing analysis
        timing =
          ('data' in aiData && aiData.data?.timing) || ('timing' in aiData && aiData.timing) || null
        // Get action items
        actionItems =
          ('data' in aiData && aiData.data?.action_items) ||
          ('action_items' in aiData && aiData.action_items) ||
          []
        // Get group analysis data (for 3+ people)
        isGroup = ('is_group' in aiData && aiData.is_group) || false
        groupAnalysis = ('group_analysis' in aiData && aiData.group_analysis) || null
        synergyBreakdown = ('synergy_breakdown' in aiData && aiData.synergy_breakdown) || null
      }
    } catch (aiErr) {
      logger.warn('[Compatibility API] AI backend call failed:', aiErr)
      aiInterpretation = ''
      aiModelUsed = 'error-fallback'
    }

    // Use AI score if available, otherwise use geo-based fallback
    const finalScore = aiScore ?? avg
    const fallbackInterpretation = lines.join('\n')

    // ======== 기록 저장 (로그인 사용자만) ========
    // 🔒 GDPR/개인정보보호법 준수: 타인의 생년월일/시간은 저장하지 않음
    const session = await getServerSession(authOptions)
    if (session?.user?.id) {
      try {
        await prisma.reading.create({
          data: {
            userId: session.user.id,
            type: 'compatibility',
            title: `${names.slice(0, 2).join(' & ')} 궁합 분석 (${finalScore}점)`,
            content: JSON.stringify({
              // ✅ 결과만 저장: 점수, 해석, 관계 레이블
              score: finalScore,
              pairScores: scores,
              interpretation: aiInterpretation || fallbackInterpretation.substring(0, 500),
              // ✅ 익명화된 레이블만 저장 (생년월일/시간은 저장 안 함)
              personLabels: names.map((name, i) => ({
                label: name || `Person ${i + 1}`,
                relation: i > 0 ? persons[i].relationToP1 : 'self',
              })),
              // ❌ 저장하지 않음: date, time (타인의 개인정보)
            }),
          },
        })
      } catch (saveErr) {
        logger.warn('[Compatibility API] Failed to save reading:', saveErr)
      }
    }

    const res = NextResponse.json({
      interpretation: aiInterpretation || fallbackInterpretation,
      aiInterpretation,
      aiModelUsed,
      pairs: scores,
      average: finalScore,
      overall_score: finalScore,
      timing,
      action_items: actionItems,
      fusion_enabled: !!aiScore,
      is_group: isGroup,
      group_analysis: groupAnalysis,
      synergy_breakdown: synergyBreakdown,
    })
    res.headers.set('Cache-Control', 'no-store')
    return res
  },
  createPublicStreamGuard({
    route: 'compatibility',
    limit: 30,
    windowSeconds: 60,
  })
)

import { NextRequest, NextResponse } from 'next/server'
import { withApiMiddleware, createAuthenticatedGuard, type ApiContext } from '@/lib/api/middleware'
import { prisma } from '@/lib/db/prisma'
import { HTTP_STATUS } from '@/lib/constants/http'
import { enforceBodySize } from '@/lib/http'
import { ALLOWED_LOCALES, BODY_LIMITS } from '@/lib/constants/api-limits'
import { LIMITS } from '@/lib/validation'
import { counselorSessionSaveRequestSchema } from '@/lib/api/zodValidation'
import { logger } from '@/lib/logger'

export const dynamic = 'force-dynamic'

const MAX_MESSAGES = 200
const MAX_MESSAGE_LENGTH = LIMITS.MESSAGE
const MAX_ID_LENGTH = LIMITS.ID

type StoredMessage = {
  role: 'system' | 'user' | 'assistant'
  content: string
  id?: string
  timestamp?: string
}

const ALLOWED_ROLES = new Set<StoredMessage['role']>(['system', 'user', 'assistant'])

function normalizeMessages(raw: unknown): StoredMessage[] {
  if (!Array.isArray(raw)) {
    return []
  }

  const normalized: StoredMessage[] = []
  for (const entry of raw.slice(-MAX_MESSAGES)) {
    if (!entry || typeof entry !== 'object') {
      continue
    }
    const msg = entry as Record<string, unknown>
    const role =
      typeof msg.role === 'string' && ALLOWED_ROLES.has(msg.role as StoredMessage['role'])
        ? (msg.role as StoredMessage['role'])
        : null
    const content = typeof msg.content === 'string' ? msg.content.trim() : ''
    if (!role) {
      continue
    }
    if (!content) {
      continue
    }

    const id = typeof msg.id === 'string' ? msg.id.trim().slice(0, MAX_ID_LENGTH) : undefined
    const timestamp =
      typeof msg.timestamp === 'string' ? msg.timestamp.trim().slice(0, 64) : undefined

    normalized.push({
      role,
      content: content.slice(0, MAX_MESSAGE_LENGTH),
      ...(id ? { id } : {}),
      ...(timestamp ? { timestamp } : {}),
    })
  }

  return normalized
}

export const POST = withApiMiddleware(
  async (req: NextRequest, context: ApiContext) => {
    const userId = context.userId!

    const oversized = enforceBodySize(req, BODY_LIMITS.LARGE)
    if (oversized) {
      return oversized
    }

    // Safe JSON parsing
    let rawBody
    try {
      const text = await req.text()
      if (!text || text.trim() === '') {
        return NextResponse.json({ error: 'empty_body' }, { status: HTTP_STATUS.BAD_REQUEST })
      }
      rawBody = JSON.parse(text)
    } catch {
      return NextResponse.json({ error: 'invalid_json' }, { status: HTTP_STATUS.BAD_REQUEST })
    }

    // Validate with Zod
    const validationResult = counselorSessionSaveRequestSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[Counselor session save] validation failed', {
        errors: validationResult.error.issues,
      })
      return NextResponse.json(
        {
          error: 'validation_failed',
          details: validationResult.error.issues.map((e) => ({
            path: e.path.join('.'),
            message: e.message,
          })),
        },
        { status: HTTP_STATUS.BAD_REQUEST }
      )
    }

    const { sessionId, theme = 'chat', messages, locale = 'ko' } = validationResult.data

    if (!sessionId || !messages.length) {
      return NextResponse.json({ error: 'invalid_request' }, { status: HTTP_STATUS.BAD_REQUEST })
    }

    const existing = await prisma.counselorChatSession.findUnique({
      where: { id: sessionId },
      select: { userId: true },
    })

    if (existing && existing.userId !== userId) {
      return NextResponse.json({ error: 'forbidden' }, { status: HTTP_STATUS.FORBIDDEN })
    }

    const chatSession = existing
      ? await prisma.counselorChatSession.update({
          where: { id: sessionId },
          data: {
            messages: messages as never,
            messageCount: messages.length,
            lastMessageAt: new Date(),
          },
        })
      : await prisma.counselorChatSession.create({
          data: {
            id: sessionId,
            userId,
            theme,
            locale,
            messages,
            messageCount: messages.length,
            lastMessageAt: new Date(),
          },
        })

    return NextResponse.json({ success: true, sessionId: chatSession.id })
  },
  createAuthenticatedGuard({
    route: '/api/counselor/session/save',
    limit: 30,
    windowSeconds: 60,
  })
)

import { NextRequest } from 'next/server'
import {
  withApiMiddleware,
  createAuthenticatedGuard,
  apiSuccess,
  apiError,
  ErrorCodes,
  type ApiContext,
} from '@/lib/api/middleware'
import { prisma } from '@/lib/db/prisma'
import { sendNotification } from '@/lib/notifications/sse'
import { isValidDate, isValidTime } from '@/lib/validation'
import { getNowInTimezone, formatDateString } from '@/lib/datetime'
import { getDailyFortuneScore } from '@/lib/destiny-map/destinyCalendar'
import { logger } from '@/lib/logger'
import { cacheOrCalculate, CacheKeys, CACHE_TTL } from '@/lib/cache/redis-cache'
import { dailyFortuneSchema } from '@/lib/api/zodValidation'

export const dynamic = 'force-dynamic'

interface FortuneData {
  love: number
  career: number
  wealth: number
  health: number
  overall: number
  luckyColor: string
  luckyNumber: number
  date: string
  userTimezone?: string
  alerts?: { type: 'warning' | 'positive' | 'info'; msg: string; icon?: string }[]
  source?: string
}

export const POST = withApiMiddleware(
  async (request: NextRequest, context: ApiContext) => {
    const rawBody = await request.json()

    const validationResult = dailyFortuneSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[Daily fortune] validation failed', { errors: validationResult.error.issues })
      return apiError(
        ErrorCodes.VALIDATION_ERROR,
        `Validation failed: ${validationResult.error.issues.map((e) => e.message).join(', ')}`
      )
    }

    const { birthDate, birthTime, sendEmail, userTimezone } = validationResult.data

    try {
      const userNow = getNowInTimezone(userTimezone)
      const targetDate = new Date(userNow.year, userNow.month - 1, userNow.day)

      const dateKey = formatDateString(userNow.year, userNow.month, userNow.day)
      const cacheKey = CacheKeys.grading(dateKey, `${birthDate}:${birthTime || '12:00'}`)

      const fortuneResult = await cacheOrCalculate(
        cacheKey,
        async () => getDailyFortuneScore(birthDate, birthTime, targetDate),
        CACHE_TTL.GRADING_RESULT
      )

      const fortune: FortuneData = {
        love: fortuneResult.love,
        career: fortuneResult.career,
        wealth: fortuneResult.wealth,
        health: fortuneResult.health,
        overall: fortuneResult.overall,
        luckyColor: fortuneResult.luckyColor,
        luckyNumber: fortuneResult.luckyNumber,
        date: dateKey,
        userTimezone: userTimezone || 'Asia/Seoul',
        alerts: fortuneResult.alerts || [],
        source: 'destinyCalendar',
      }

      if (context.userId) {
        await prisma.dailyFortune
          .create({
            data: {
              userId: context.userId,
              date: fortune.date,
              loveScore: fortune.love,
              careerScore: fortune.career,
              wealthScore: fortune.wealth,
              healthScore: fortune.health,
              overallScore: fortune.overall,
              luckyColor: fortune.luckyColor,
              luckyNumber: fortune.luckyNumber,
            },
          })
          .catch((err: unknown) => {
            const prismaError = err as { code?: string }
            if (prismaError?.code !== 'P2002') {
              logger.error('[Daily Fortune] Failed to save fortune to DB:', err)
            }
          })
      }

      const userEmail = context.session?.user?.email
      if (userEmail) {
        sendNotification(userEmail, {
          type: 'system',
          title: "Today's Fortune Ready!",
          message: `Overall: ${fortune.overall} | Love: ${fortune.love} | Career: ${fortune.career} | Wealth: ${fortune.wealth}`,
          link: '/myjourney',
        }).catch((err: unknown) => {
          logger.warn('[Daily Fortune] Failed to send notification:', err)
        })
      }

      let emailSent = false
      if (sendEmail && userEmail) {
        try {
          await sendFortuneEmail(userEmail, fortune)
          emailSent = true
        } catch (emailErr) {
          logger.error('[Daily Fortune] Failed to send email:', emailErr)
        }
      }

      return apiSuccess({
        fortune,
        message: sendEmail
          ? emailSent
            ? 'Fortune sent to your email!'
            : 'Fortune calculated! (Email delivery failed)'
          : 'Fortune calculated!',
      })
    } catch (err) {
      logger.error('[Daily Fortune Error]:', err)
      return apiError(ErrorCodes.INTERNAL_ERROR, 'Internal Server Error')
    }
  },
  createAuthenticatedGuard({
    route: '/api/daily-fortune',
    limit: 20,
    windowSeconds: 60,
  })
)

async function sendFortuneEmail(email: string, fortune: FortuneData) {
  try {
    const response = await fetch(`${process.env.NEXTAUTH_URL}/api/email/send`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        to: email,
        subject: "DestinyPal | Today's Fortune",
        html: `
          <div style="font-family: Arial, sans-serif; max-width: 640px; margin: 0 auto; padding: 8px;">
            <h1 style="color: #6c5ce7; margin-bottom: 4px;">Today's Fortune</h1>
            <p style="margin-top: 0; color: #444;">${fortune.date}</p>
            <div style="background: #f5f5f8; padding: 16px; border-radius: 12px; margin: 16px 0;">
              <h2 style="color: #1e293b; margin: 0 0 8px;">Overall: ${fortune.overall}/100</h2>
              <p style="margin: 4px 0;">Love: ${fortune.love}/100</p>
              <p style="margin: 4px 0;">Career: ${fortune.career}/100</p>
              <p style="margin: 4px 0;">Wealth: ${fortune.wealth}/100</p>
              <p style="margin: 4px 0;">Health: ${fortune.health}/100</p>
            </div>
            <div style="background: linear-gradient(135deg, #4f46e5, #8b5cf6); color: #fff; padding: 16px; border-radius: 12px;">
              <p style="margin: 0;">Lucky Color: <strong>${fortune.luckyColor}</strong></p>
              <p style="margin: 4px 0 0;">Lucky Number: <strong>${fortune.luckyNumber}</strong></p>
            </div>
            <p style="margin-top: 20px; color: #475569;">Have a great day with DestinyPal.</p>
          </div>
        `,
      }),
    })

    if (!response.ok) {
      throw new Error('Email send failed')
    }

    logger.info('[Daily Fortune] Email sent to:', email)
  } catch (error) {
    logger.warn('[Daily Fortune] Email send failed:', error)
  }
}

// validation.ts
// Zod schemas for destiny-map chat-stream endpoint

import { z } from 'zod'
import { LIMITS } from '@/lib/validation'

// Allow sets for validation
export const ALLOWED_LANG = new Set(['ko', 'en'])
export const ALLOWED_GENDER = new Set(['male', 'female'])
const ALLOWED_ROLE = new Set(['system', 'user', 'assistant'])

/**
 * Chat message schema
 */
export const ChatMessageSchema = z.object({
  role: z.enum(['system', 'user', 'assistant']),
  content: z.string().min(1).max(2000),
})

/**
 * Saju data structure schema (flexible object for now)
 */
export const SajuDataStructureSchema = z
  .object({
    dayMaster: z.unknown().optional(),
    unse: z.unknown().optional(),
  })
  .passthrough() // Allow additional properties

/**
 * Astro data structure schema (flexible object for now)
 */
export const AstroDataStructureSchema = z
  .object({
    sun: z.unknown().optional(),
    moon: z.unknown().optional(),
    mercury: z.unknown().optional(),
    venus: z.unknown().optional(),
    mars: z.unknown().optional(),
    jupiter: z.unknown().optional(),
    saturn: z.unknown().optional(),
    ascendant: z.unknown().optional(),
  })
  .passthrough()

/**
 * Main request body schema for destiny-map chat-stream
 */
export const DestinyMapChatStreamSchema = z.object({
  // Basic user info
  name: z.string().max(LIMITS.NAME).optional(),
  birthDate: z.string().optional(),
  birthTime: z.string().optional(),
  gender: z.enum(['male', 'female']).default('male'),
  latitude: z.number().min(-90).max(90).optional(),
  longitude: z.number().min(-180).max(180).optional(),

  // Theme and language
  theme: z.string().max(LIMITS.THEME).default('chat'),
  lang: z.enum(['ko', 'en']).default('ko'),

  // Chat messages (max 50 messages)
  messages: z.array(ChatMessageSchema).max(50).default([]),

  // Pre-computed chart data (optional)
  saju: SajuDataStructureSchema.optional(),
  astro: AstroDataStructureSchema.optional(),
  advancedAstro: z.unknown().optional(), // CombinedResult partial

  // Prediction and user context
  predictionContext: z.unknown().optional(),
  userContext: z.unknown().optional(),
  cvText: z.string().optional(),
})

export type DestinyMapChatStreamInput = z.infer<typeof DestinyMapChatStreamSchema>

/**
 * Validate and parse request body
 */
export function validateDestinyMapRequest(body: unknown) {
  return DestinyMapChatStreamSchema.safeParse(body)
}

import { NextRequest, NextResponse } from 'next/server'
import { initializeApiContext, createAuthenticatedGuard } from '@/lib/api/middleware'
import { createTransformedSSEStream, createFallbackSSEStream } from '@/lib/streaming'
import { apiClient } from '@/lib/api/ApiClient'
import { containsForbidden, safetyMessage } from '@/lib/textGuards'
import { sanitizeLocaleText } from '@/lib/destiny-map/sanitize'
import { maskTextWithName } from '@/lib/security'
import { enforceBodySize } from '@/lib/http'
import { jsonErrorResponse } from '@/lib/api/errorHandler'
import { isValidDate, isValidTime, isValidLatitude, isValidLongitude } from '@/lib/validation'
import { logger } from '@/lib/logger'
import { parseRequestBody } from '@/lib/api/requestParser'
import { HTTP_STATUS } from '@/lib/constants/http'

// Local modules
import { type ChatMessage, clampMessages, counselorSystemPrompt, loadPersonaMemory } from './lib'
import { loadUserProfile, type ProfileLoadResult } from './lib/profileLoader'
import { validateDestinyMapRequest, type DestinyMapChatStreamInput } from './lib/validation'
import { calculateChartData } from './lib/chart-calculator'
import {
  buildContextSections,
  buildPredictionSection,
  buildLongTermMemorySection,
} from './lib/context-builder'
import type { SajuDataStructure, AstroDataStructure } from './lib/types'
import type { CombinedResult } from '@/lib/destiny-map/astrologyengine'

export const dynamic = 'force-dynamic'
export const runtime = 'nodejs'
export const maxDuration = 60

export async function POST(req: NextRequest) {
  try {
    const oversized = enforceBodySize(req, 256 * 1024) // 256KB for large chart data
    if (oversized) {
      return oversized
    }

    // Apply middleware: authentication + rate limiting + credit consumption
    const guardOptions = createAuthenticatedGuard({
      route: 'destiny-map-chat-stream',
      limit: 60,
      windowSeconds: 60,
      requireCredits: true,
      creditType: 'reading',
      creditAmount: 1,
    })

    const { context, error } = await initializeApiContext(req, guardOptions)
    if (error) {
      return error
    }

    const userId = context.userId

    // Parse and validate request body using Zod
    const body = await parseRequestBody<Record<string, unknown>>(req, {
      context: 'Destiny-map Chat-stream',
    })
    if (!body) {
      return NextResponse.json({ error: 'invalid_body' }, { status: HTTP_STATUS.BAD_REQUEST })
    }

    const validation = validateDestinyMapRequest(body)
    if (!validation.success) {
      const errors = validation.error.issues
        .map((e) => `${e.path.join('.')}: ${e.message}`)
        .join(', ')
      logger.warn('[chat-stream] Validation failed', { errors: validation.error.issues })
      return NextResponse.json(
        { error: 'Validation failed', details: errors, issues: validation.error.issues },
        { status: HTTP_STATUS.BAD_REQUEST }
      )
    }

    const validated = validation.data
    const {
      name,
      birthDate,
      birthTime,
      gender,
      latitude,
      longitude,
      theme,
      lang,
      messages,
      saju,
      astro,
      advancedAstro,
      predictionContext,
      userContext,
      cvText,
    } = validated

    // ========================================
    // 🔄 AUTO-LOAD: Try to load birth info from user profile if missing
    // ========================================
    let effectiveBirthDate = birthDate || ''
    let effectiveBirthTime = birthTime || ''
    let effectiveLatitude = latitude || 0
    let effectiveLongitude = longitude || 0
    let effectiveGender = gender
    let effectiveSaju = saju
    let effectiveAstro = astro

    const needsProfileLoad = userId && (!birthDate || !birthTime || !latitude || !longitude)

    if (needsProfileLoad) {
      try {
        const profileResult: ProfileLoadResult = await loadUserProfile(
          userId,
          birthDate,
          birthTime,
          latitude,
          longitude,
          saju as SajuDataStructure | undefined,
          astro as AstroDataStructure | undefined
        )
        if (profileResult.saju) {
          effectiveSaju = profileResult.saju
        }
        if (profileResult.astro) {
          effectiveAstro = profileResult.astro
        }
        if (profileResult.birthDate) {
          effectiveBirthDate = profileResult.birthDate
        }
        if (profileResult.birthTime) {
          effectiveBirthTime = profileResult.birthTime
        }
        if (profileResult.latitude) {
          effectiveLatitude = profileResult.latitude
        }
        if (profileResult.longitude) {
          effectiveLongitude = profileResult.longitude
        }
        if (profileResult.gender) {
          effectiveGender = profileResult.gender as 'male' | 'female'
        }
      } catch (profileError) {
        logger.warn('[chat-stream] Failed to load user profile, proceeding with provided data', {
          userId,
          error: profileError instanceof Error ? profileError.message : 'Unknown error',
        })
      }
    }

    // Validate effective values
    if (!effectiveBirthDate || !isValidDate(effectiveBirthDate)) {
      return jsonErrorResponse('Invalid or missing birthDate')
    }
    if (!effectiveBirthTime || !isValidTime(effectiveBirthTime)) {
      return jsonErrorResponse('Invalid or missing birthTime')
    }
    if (!isValidLatitude(effectiveLatitude)) {
      return jsonErrorResponse('Invalid or missing latitude')
    }
    if (!isValidLongitude(effectiveLongitude)) {
      return jsonErrorResponse('Invalid or missing longitude')
    }

    // ========================================
    // 🧠 LONG-TERM MEMORY: Load PersonaMemory and recent session summaries
    // ========================================
    let personaMemoryContext = ''
    let recentSessionSummaries = ''

    if (userId) {
      const memoryResult = await loadPersonaMemory(userId, theme, lang)
      personaMemoryContext = memoryResult.personaMemoryContext
      recentSessionSummaries = memoryResult.recentSessionSummaries
    }

    // ========================================
    // 📊 COMPUTE CHART DATA: Saju, Astro, Transits (with caching)
    // ========================================
    const chartResult = await calculateChartData(
      {
        birthDate: effectiveBirthDate,
        birthTime: effectiveBirthTime,
        gender: effectiveGender,
        latitude: effectiveLatitude,
        longitude: effectiveLongitude,
      },
      effectiveSaju as SajuDataStructure | undefined,
      effectiveAstro as AstroDataStructure | undefined
    )

    const finalSaju = chartResult.saju
    const finalAstro = chartResult.astro
    const { natalChartData, currentTransits } = chartResult

    // Messages are already validated by Zod as ChatMessage[]
    const trimmedHistory = clampMessages(messages)

    // Safety check
    const lastUser = [...trimmedHistory].reverse().find((m) => m.role === 'user')
    if (lastUser && containsForbidden(lastUser.content)) {
      const encoder = new TextEncoder()
      return new Response(
        new ReadableStream({
          start(controller) {
            controller.enqueue(encoder.encode(`data: ${safetyMessage(lang)}\n\n`))
            controller.enqueue(encoder.encode('data: [DONE]\n\n'))
            controller.close()
          },
        }),
        {
          headers: {
            'Content-Type': 'text/event-stream',
            'Cache-Control': 'no-cache',
            Connection: 'keep-alive',
          },
        }
      )
    }

    // ========================================
    // 📝 BUILD CONTEXT SECTIONS: Using modular context builder
    // ========================================
    const contextSections = buildContextSections({
      saju: finalSaju,
      astro: finalAstro,
      advancedAstro: advancedAstro as Partial<CombinedResult> | undefined,
      natalChartData,
      currentTransits,
      birthDate: effectiveBirthDate,
      gender: effectiveGender,
      theme,
      lang,
      trimmedHistory,
      lastUserMessage: lastUser?.content,
    })

    const predictionSection = buildPredictionSection(predictionContext, lang)
    const longTermMemorySection = buildLongTermMemorySection(
      personaMemoryContext,
      recentSessionSummaries,
      lang
    )

    // Theme descriptions for context
    const themeDescriptions: Record<string, { ko: string; en: string }> = {
      love: { ko: '연애/결혼/배우자 관련 질문', en: 'Love, marriage, partner questions' },
      career: { ko: '직업/취업/이직/사업 관련 질문', en: 'Career, job, business questions' },
      wealth: { ko: '재물/투자/재정 관련 질문', en: 'Money, investment, finance questions' },
      health: { ko: '건강/체력/웰빙 관련 질문', en: 'Health, wellness questions' },
      family: { ko: '가족/인간관계 관련 질문', en: 'Family, relationships questions' },
      today: { ko: '오늘의 운세/조언', en: "Today's fortune and advice" },
      month: { ko: '이번 달 운세/조언', en: "This month's fortune" },
      year: { ko: '올해 운세/연간 예측', en: "This year's fortune" },
      life: { ko: '인생 전반/종합 상담', en: 'Life overview, general counseling' },
      chat: { ko: '자유 주제 상담', en: 'Free topic counseling' },
    }
    const themeDesc = themeDescriptions[theme] || themeDescriptions.chat
    const themeContext =
      lang === 'ko'
        ? `현재 상담 테마: ${theme} (${themeDesc.ko})\n이 테마에 맞춰 답변해주세요.`
        : `Current theme: ${theme} (${themeDesc.en})\nFocus your answer on this theme.`

    // Build prompt - FULL analysis with all advanced engines
    const chatPrompt = [
      counselorSystemPrompt(lang),
      `Name: ${name || 'User'}`,
      themeContext,
      '',
      // 기본 사주/점성 데이터
      contextSections.v3Snapshot
        ? `[사주/점성 기본 데이터]\n${contextSections.v3Snapshot.slice(0, 5000)}`
        : '',
      // 🔮 고급 분석 섹션들 (모듈화된 빌더 사용)
      contextSections.timingScoreSection ? `\n${contextSections.timingScoreSection}` : '',
      contextSections.enhancedAnalysisSection ? `\n${contextSections.enhancedAnalysisSection}` : '',
      contextSections.daeunTransitSection ? `\n${contextSections.daeunTransitSection}` : '',
      contextSections.advancedAstroSection ? `\n${contextSections.advancedAstroSection}` : '',
      contextSections.tier4AdvancedSection ? `\n${contextSections.tier4AdvancedSection}` : '',
      contextSections.pastAnalysisSection ? `\n${contextSections.pastAnalysisSection}` : '',
      contextSections.lifePredictionSection ? `\n${contextSections.lifePredictionSection}` : '',
      // 🧠 장기 기억 - 이전 상담 컨텍스트
      longTermMemorySection ? `\n${longTermMemorySection}` : '',
      // 📊 인생 예측 컨텍스트 (프론트엔드에서 전달된 경우)
      predictionSection ? `\n${predictionSection}` : '',
      // 📜 대화 히스토리
      contextSections.historyText ? `\n대화:\n${contextSections.historyText}` : '',
      `\n질문: ${contextSections.userQuestion}`,
    ]
      .filter(Boolean)
      .join('\n')

    // Get session_id from header for RAG cache
    const sessionId = req.headers.get('x-session-id') || undefined

    // Call backend streaming endpoint using apiClient
    const streamResult = await apiClient.postSSEStream(
      '/ask-stream',
      {
        theme,
        prompt: chatPrompt,
        locale: lang,
        // Pass pre-computed chart data if available (instant response)
        saju: finalSaju || undefined,
        astro: finalAstro || undefined,
        // Advanced astrology features (draconic, harmonics, progressions, etc.)
        advanced_astro: advancedAstro || undefined,
        // Fallback: Pass birth info for backend to compute if needed
        birth: {
          date: effectiveBirthDate,
          time: effectiveBirthTime,
          gender: effectiveGender,
          lat: effectiveLatitude,
          lon: effectiveLongitude,
        },
        // Conversation history for context-aware responses
        history: trimmedHistory.filter((m) => m.role !== 'system'),
        // Session ID for RAG cache
        session_id: sessionId,
        // Premium: user context for returning users
        user_context: userContext || undefined,
        // CV/Resume text for career-related questions
        cv_text: cvText || undefined,
      },
      { timeout: 60000 }
    )

    if (!streamResult.ok) {
      logger.error('[DestinyMapChatStream] Backend error:', {
        status: streamResult.status,
        error: streamResult.error,
      })

      const fallback =
        lang === 'ko'
          ? 'AI 서비스에 연결할 수 없습니다. 잠시 후 다시 시도해 주세요.'
          : 'Could not connect to AI service. Please try again.'

      return createFallbackSSEStream({
        content: fallback,
        done: true,
        'X-Fallback': '1',
      })
    }

    // Relay the stream from backend to frontend with sanitization
    return createTransformedSSEStream({
      source: streamResult.response,
      transform: (chunk) => {
        const masked = maskTextWithName(sanitizeLocaleText(chunk, lang), name)
        return masked
      },
      route: 'DestinyMapChatStream',
      additionalHeaders: {
        'X-Fallback': streamResult.response.headers.get('x-fallback') || '0',
      },
    })
  } catch (err: unknown) {
    const message = 'Internal Server Error'
    logger.error('[Chat-Stream API error]', err)
    return NextResponse.json({ error: message }, { status: HTTP_STATUS.SERVER_ERROR })
  }
}

// src/app/api/dream/stream/route.ts
// Streaming Dream Interpretation API - Real-time SSE for fast display

import { NextRequest, NextResponse } from 'next/server'
import { getServerSession } from 'next-auth'
import { authOptions } from '@/lib/auth/authOptions'
import { prisma } from '@/lib/db/prisma'
import { initializeApiContext, createPublicStreamGuard } from '@/lib/api/middleware'
import { createSSEStreamProxy, createFallbackSSEStream } from '@/lib/streaming'
import { apiClient } from '@/lib/api/ApiClient'
import { enforceBodySize } from '@/lib/http'
import { cleanStringArray, isRecord } from '@/lib/api'
import { logger } from '@/lib/logger'

import { parseRequestBody } from '@/lib/api/requestParser'
import { dreamStreamSchema } from '@/lib/api/zodValidation'
interface StreamDreamRequest {
  dreamText: string
  symbols?: string[]
  emotions?: string[]
  themes?: string[]
  context?: string[]
  locale?: 'ko' | 'en'
  // Cultural symbols
  koreanTypes?: string[]
  koreanLucky?: string[]
  chinese?: string[]
  islamicTypes?: string[]
  western?: string[]
  hindu?: string[]
  japanese?: string[]
  // Birth data for astrology/saju analysis
  birth?: {
    date: string
    time: string
    timezone?: string
    latitude?: number
    longitude?: number
    gender?: string
  }
  sajuInfluence?: {
    pillars?: Record<string, unknown>
    dayMaster?: Record<string, unknown>
    currentDaeun?: Record<string, unknown> | null
    currentSaeun?: Record<string, unknown> | null
    currentWolun?: Record<string, unknown> | null
    todayIljin?: Record<string, unknown> | null
  }
}

import { ALLOWED_LOCALES, BODY_LIMITS, TEXT_LIMITS, LIST_LIMITS } from '@/lib/constants/api-limits'
import { DATE_RE, TIME_RE, LIMITS } from '@/lib/validation/patterns'
import { HTTP_STATUS } from '@/lib/constants/http'
const MAX_STREAM_BODY = BODY_LIMITS.STREAM
const MAX_TEXT_LEN = TEXT_LIMITS.MAX_DREAM_TEXT
const _MAX_LIST_ITEMS = LIST_LIMITS.MAX_LIST_ITEMS
const _MAX_LIST_ITEM_LEN = 120
const STREAM_LOCALES = ALLOWED_LOCALES
const MAX_TIMEZONE_LEN = LIMITS.TIMEZONE

// cleanStringArray and isRecord imported from @/lib/api

function sanitizeBirth(raw: unknown) {
  if (!isRecord(raw)) {
    return undefined
  }
  const date = typeof raw.date === 'string' && DATE_RE.test(raw.date) ? raw.date : undefined
  const time = typeof raw.time === 'string' && TIME_RE.test(raw.time) ? raw.time : undefined
  const timezone =
    typeof raw.timezone === 'string' ? raw.timezone.trim().slice(0, MAX_TIMEZONE_LEN) : undefined
  const latNum = Number(raw.latitude)
  const lonNum = Number(raw.longitude)
  const latitude = Number.isFinite(latNum) && latNum >= -90 && latNum <= 90 ? latNum : undefined
  const longitude = Number.isFinite(lonNum) && lonNum >= -180 && lonNum <= 180 ? lonNum : undefined
  const gender = typeof raw.gender === 'string' ? raw.gender.trim().slice(0, 20) : undefined

  if (!date && !time && latitude === undefined && longitude === undefined && !timezone && !gender) {
    return undefined
  }

  return { date, time, timezone, latitude, longitude, gender }
}

export async function POST(req: NextRequest) {
  try {
    // Apply middleware: rate limiting + public token auth + credit consumption
    const guardOptions = createPublicStreamGuard({
      route: 'dream-stream',
      limit: 10,
      windowSeconds: 60,
      requireCredits: true,
      creditType: 'reading',
      creditAmount: 1,
    })

    const { context, error } = await initializeApiContext(req, guardOptions)
    if (error) {
      return error
    }

    const oversized = enforceBodySize(req, MAX_STREAM_BODY)
    if (oversized) {
      return oversized
    }

    const rawBody = await parseRequestBody<Partial<StreamDreamRequest>>(req, {
      context: 'Dream Stream',
    })
    if (!rawBody || typeof rawBody !== 'object') {
      return NextResponse.json({ error: 'invalid_body' }, { status: HTTP_STATUS.BAD_REQUEST })
    }

    // Validate with Zod
    const validationResult = dreamStreamSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[Dream stream] validation failed', { errors: validationResult.error.issues })
      return NextResponse.json(
        {
          error: 'validation_failed',
          details: validationResult.error.issues.map((e) => ({
            path: e.path.join('.'),
            message: e.message,
          })),
        },
        { status: HTTP_STATUS.BAD_REQUEST }
      )
    }

    const validated = validationResult.data
    const dreamText = validated.dreamText.slice(0, MAX_TEXT_LEN)
    const symbols = cleanStringArray(validated.symbols)
    const emotions = cleanStringArray(validated.emotions)
    const themes = cleanStringArray(validated.themes)
    const contextArray = cleanStringArray(validated.context)
    const locale = (validated.locale || context.locale) as 'ko' | 'en'
    const koreanTypes = cleanStringArray(validated.koreanTypes)
    const koreanLucky = cleanStringArray(validated.koreanLucky)
    const chinese = cleanStringArray(validated.chinese)
    const islamicTypes = cleanStringArray(validated.islamicTypes)
    const western = cleanStringArray(validated.western)
    const hindu = cleanStringArray(validated.hindu)
    const japanese = cleanStringArray(validated.japanese)
    const birth = sanitizeBirth(validated.birth)
    const sajuInfluence = isRecord(validated.sajuInfluence) ? validated.sajuInfluence : undefined

    // Call backend streaming endpoint using apiClient
    const streamResult = await apiClient.postSSEStream('/api/dream/interpret-stream', {
      dream: dreamText,
      symbols,
      emotions,
      themes,
      context: contextArray,
      locale,
      koreanTypes,
      koreanLucky,
      chinese,
      islamicTypes,
      western,
      hindu,
      japanese,
      birth,
      sajuInfluence,
    })

    if (!streamResult.ok) {
      logger.error('[DreamStream] Backend error:', {
        status: streamResult.status,
        error: streamResult.error,
      })

      return createFallbackSSEStream({
        content:
          locale === 'ko'
            ? '일시적으로 꿈 해석 서비스를 이용할 수 없습니다. 잠시 후 다시 시도해주세요.'
            : 'Dream interpretation service temporarily unavailable. Please try again later.',
        done: true,
        error: streamResult.error,
      })
    }

    // ======== 기록 저장 (로그인 사용자만) ========
    const session = await getServerSession(authOptions)
    if (session?.user?.id || context.userId) {
      try {
        const userId = session?.user?.id || context.userId
        const symbolsStr = symbols.slice(0, 5).join(', ')
        await prisma.reading.create({
          data: {
            userId: userId!,
            type: 'dream',
            title: symbolsStr ? `꿈 해석: ${symbolsStr}` : '꿈 해석',
            content: JSON.stringify({
              dreamText: dreamText.slice(0, 500),
              symbols,
              emotions,
              themes,
              context: contextArray,
              koreanTypes,
              koreanLucky,
            }),
          },
        })
      } catch (saveErr) {
        logger.warn('[Dream API] Failed to save reading:', saveErr)
      }
    }

    // Proxy the SSE stream from backend to client
    return createSSEStreamProxy({
      source: streamResult.response,
      route: 'DreamStream',
    })
  } catch (err: unknown) {
    logger.error('Dream stream error:', err)
    return NextResponse.json({ error: 'Server error' }, { status: HTTP_STATUS.SERVER_ERROR })
  }
}

// src/app/api/feedback/route.ts

import { NextRequest, NextResponse } from 'next/server'
import {
  withApiMiddleware,
  createPublicStreamGuard,
  createSimpleGuard,
  apiSuccess,
  apiError,
  ErrorCodes,
  type ApiContext,
} from '@/lib/api/middleware'
import { prisma } from '@/lib/db/prisma'
import { guardText, cleanText } from '@/lib/textGuards'
import { apiClient } from '@/lib/api'
import { logger } from '@/lib/logger'
import { sectionFeedbackRequestSchema, feedbackGetQuerySchema } from '@/lib/api/zodValidation'

import { parseRequestBody } from '@/lib/api/requestParser'
type RlhfResponse = {
  feedback_id?: string
  new_badges?: string[]
}
type SectionGroup = {
  sectionId: string
  _count: { _all: number }
}

function trimValue(value: unknown, max = 120) {
  return String(value ?? '')
    .trim()
    .slice(0, max)
}

export const POST = withApiMiddleware(
  async (req: NextRequest, _context: ApiContext) => {
    const rawBody = await parseRequestBody(req, { context: 'Feedback' })

    if (!rawBody || typeof rawBody !== 'object') {
      return apiError(ErrorCodes.BAD_REQUEST, 'Invalid request body')
    }

    const validationResult = sectionFeedbackRequestSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[Feedback POST] validation failed', { errors: validationResult.error.issues })
      return apiError(
        ErrorCodes.VALIDATION_ERROR,
        `Validation failed: ${validationResult.error.issues.map((e) => e.message).join(', ')}`
      )
    }

    const body = validationResult.data
    const safeLocale = body.locale || 'ko'
    const safeUserHash = body.userHash || null
    const safeRecordId = body.recordId || null
    const safeFeedbackText = body.feedbackText ? guardText(body.feedbackText, 600) : null
    const safeUserQuestion = body.userQuestion ? guardText(body.userQuestion, 600) : null
    const safeConsultationSummary = body.consultationSummary
      ? guardText(body.consultationSummary, 600)
      : null
    const safeContextUsed = body.contextUsed ? guardText(body.contextUsed, 600) : null
    const safeDayMaster = body.dayMaster || null
    const safeSunSign = body.sunSign || null
    const normalizedRating = body.rating || null

    // Save to local database
    const feedback = await prisma.sectionFeedback.create({
      data: {
        service: body.service,
        theme: body.theme,
        sectionId: body.sectionId,
        helpful: body.helpful,
        dayMaster: safeDayMaster,
        sunSign: safeSunSign,
        locale: safeLocale,
        userHash: safeUserHash,
      },
    })

    // Also send to backend RLHF system for AI improvement
    let rlhfResult: RlhfResponse | null = null
    try {
      const rlhfRating = normalizedRating ?? (body.helpful ? 5 : 1) // Convert boolean to rating if not provided
      const rlhfPayload = {
        consultation_data: {
          record_id: safeRecordId || feedback.id,
          theme: body.theme,
          locale: safeLocale,
          user_prompt: safeUserQuestion || '',
          consultation_summary: safeConsultationSummary || body.sectionId,
          context_used: safeContextUsed || '',
        },
        rating: rlhfRating,
        feedback: cleanText(safeFeedbackText || (body.helpful ? 'Helpful' : 'Not helpful'), 500),
        user_id: safeUserHash || 'anonymous',
      }

      const result = await apiClient.post<RlhfResponse>('/rlhf/feedback', rlhfPayload, {
        timeout: 8000,
      })

      if (result.ok && result.data) {
        rlhfResult = result.data
        logger.warn('[Feedback] RLHF recorded:', rlhfResult.feedback_id)
      }
    } catch (rlhfErr) {
      // RLHF is optional - don't fail the whole request
      logger.warn('[Feedback] RLHF backend not available:', rlhfErr)
    }

    const res = NextResponse.json({
      success: true,
      id: feedback.id,
      rlhfId: rlhfResult?.feedback_id,
      badges: rlhfResult?.new_badges || [],
    })
    res.headers.set('Cache-Control', 'no-store')
    return res
  },
  createPublicStreamGuard({
    route: '/api/feedback',
    limit: 20,
    windowSeconds: 60,
  })
)

// GET: Fetch feedback stats (for admin/analytics)
export const GET = withApiMiddleware(
  async (req: NextRequest, _context: ApiContext) => {
    const { searchParams } = new URL(req.url)
    const queryValidation = feedbackGetQuerySchema.safeParse({
      service: searchParams.get('service') || undefined,
      theme: searchParams.get('theme') || undefined,
    })
    if (!queryValidation.success) {
      logger.warn('[Feedback GET] query validation failed', {
        errors: queryValidation.error.issues,
      })
      return apiError(
        ErrorCodes.VALIDATION_ERROR,
        `Validation failed: ${queryValidation.error.issues.map((e) => e.message).join(', ')}`
      )
    }
    const { service, theme } = queryValidation.data

    const where: { service?: string; theme?: string } = {}
    if (service) {
      where.service = service
    }
    if (theme) {
      where.theme = theme
    }

    // Get aggregated stats
    const [total, positive, bySectionTotals, bySectionPositives] = (await Promise.all([
      prisma.sectionFeedback.count({ where }),
      prisma.sectionFeedback.count({ where: { ...where, helpful: true } }),
      prisma.sectionFeedback.groupBy({
        by: ['sectionId'],
        where,
        _count: { _all: true },
      }),
      prisma.sectionFeedback.groupBy({
        by: ['sectionId'],
        where: { ...where, helpful: true },
        _count: { _all: true },
      }),
    ])) as [number, number, SectionGroup[], SectionGroup[]]

    const satisfactionRate = total > 0 ? Math.round((positive / total) * 100) : 0

    // Format section stats
    const positiveMap = new Map<string, number>()
    bySectionPositives.forEach((s) => {
      positiveMap.set(s.sectionId, s._count._all || 0)
    })

    const sectionStats = bySectionTotals.map((s) => {
      const pos = positiveMap.get(s.sectionId) || 0
      const totalCount = s._count._all
      return {
        sectionId: s.sectionId,
        total: totalCount,
        positive: pos,
        rate: totalCount > 0 ? Math.round((pos / totalCount) * 100) : 0,
      }
    })

    return NextResponse.json({
      total,
      positive,
      negative: total - positive,
      satisfactionRate,
      bySection: sectionStats,
    })
  },
  createSimpleGuard({
    route: '/api/feedback',
    limit: 60,
    windowSeconds: 60,
  })
)

