import { NextRequest } from 'next/server'
import { getServerSession } from 'next-auth/next'
import { authOptions } from '@/lib/auth/authOptions'
import { registerClient, unregisterClient, drainQueuedNotifications } from '@/lib/notifications/sse'
import { HTTP_STATUS } from '@/lib/constants/http'
import { rateLimit } from '@/lib/rateLimit'
import { getClientIp } from '@/lib/request-ip'
import { logger } from '@/lib/logger'

export const dynamic = 'force-dynamic'
export const runtime = 'nodejs'

/**
 * Server-Sent Events (SSE) endpoint for real-time notifications
 * GET /api/notifications/stream?userId=user@example.com
 */
export async function GET(_request: NextRequest) {
  const session = await getServerSession(authOptions)

  if (!session?.user?.email) {
    return new Response('Unauthorized', { status: HTTP_STATUS.UNAUTHORIZED })
  }

  const ip = getClientIp(_request.headers)
  const limit = await rateLimit(`notif-stream:${ip}`, { limit: 30, windowSeconds: 60 })
  if (!limit.allowed) {
    return new Response('Too many requests', {
      status: HTTP_STATUS.RATE_LIMITED,
      headers: Object.fromEntries(limit.headers.entries()),
    })
  }

  const userId = session.user.email

  // Create a readable stream for SSE
  const stream = new ReadableStream({
    start(controller) {
      // Store the controller for this user
      registerClient(userId, controller)

      // Send initial connection message
      const data = JSON.stringify({
        id: Date.now().toString(),
        type: 'system',
        title: 'Connected',
        message: 'Notification system connected',
        createdAt: Date.now(),
        read: true,
      })
      controller.enqueue(`data: ${data}\n\n`)

      // Keep connection alive with heartbeat
      const heartbeat = setInterval(() => {
        controller.enqueue(`: heartbeat\n\n`)
      }, 30000) // Every 30 seconds

      // Drain queued notifications from Redis (cross-instance)
      let draining = false
      const drainQueue = async () => {
        if (draining) {
          return
        }
        draining = true
        try {
          const queued = await drainQueuedNotifications(userId, 20)
          for (const item of queued) {
            try {
              controller.enqueue(`data: ${JSON.stringify(item)}\n\n`)
            } catch (enqueueErr) {
              logger.warn('[notifications/stream] Enqueue failed, stream may be closed', { userId })
            }
          }
        } finally {
          draining = false
        }
      }

      // Initial drain + periodic polling
      void drainQueue()
      const queuePoller = setInterval(() => {
        void drainQueue()
      }, 5000)

      // Cleanup on connection close
      _request.signal.addEventListener('abort', () => {
        clearInterval(heartbeat)
        clearInterval(queuePoller)
        unregisterClient(userId)
        try {
          controller.close()
        } catch {
          // Controller already closed
        }
      })
    },
  })

  return new Response(stream, {
    headers: {
      'Content-Type': 'text/event-stream',
      'Cache-Control': 'no-cache, no-transform',
      Connection: 'keep-alive',
      'X-Accel-Buffering': 'no', // Disable Nginx buffering
    },
  })
}

// src/app/api/past-life/save/route.ts
/**
 * Past Life Result Save API
 * 전생 리딩 결과 저장 API
 */

import { NextRequest, NextResponse } from 'next/server'
import { withApiMiddleware, createAuthenticatedGuard, type ApiContext } from '@/lib/api/middleware'
import { prisma } from '@/lib/db/prisma'
import { logger } from '@/lib/logger'
import { pastLifeSaveRequestSchema } from '@/lib/api/zodValidation'
import { HTTP_STATUS } from '@/lib/constants/http'

interface SavePastLifeBody {
  birthDate: string
  birthTime?: string
  latitude?: number
  longitude?: number
  timezone?: string
  karmaScore: number
  analysisData: {
    soulPattern: unknown
    pastLife: unknown
    soulJourney: unknown
    karmicDebts: unknown[]
    thisLifeMission: unknown
    talentsCarried: string[]
    saturnLesson: unknown
  }
  locale?: string
}

/**
 * POST /api/past-life/save
 * 전생 리딩 결과 저장 (로그인 필요)
 */
export const POST = withApiMiddleware(
  async (req: NextRequest, context: ApiContext) => {
    const rawBody = await req.json().catch(() => null)
    if (!rawBody) {
      return NextResponse.json(
        { error: 'Invalid request body' },
        { status: HTTP_STATUS.BAD_REQUEST }
      )
    }

    // Validate with Zod
    const validationResult = pastLifeSaveRequestSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[Past Life save] validation failed', { errors: validationResult.error.issues })
      return NextResponse.json(
        {
          error: 'validation_failed',
          details: validationResult.error.issues.map((e) => ({
            path: e.path.join('.'),
            message: e.message,
          })),
        },
        { status: HTTP_STATUS.BAD_REQUEST }
      )
    }

    const {
      birthDate,
      birthTime,
      latitude,
      longitude,
      timezone,
      karmaScore,
      analysisData,
      locale = 'ko',
    } = validationResult.data

    try {
      // Save to database
      const result = await prisma.pastLifeResult.create({
        data: {
          userId: context.userId!,
          birthDate,
          birthTime: birthTime || null,
          latitude: latitude || null,
          longitude: longitude || null,
          timezone: timezone || null,
          karmaScore,
          analysisData: analysisData as never,
          locale,
        },
      })

      logger.info('[PastLife Save] Saved successfully', {
        userId: context.userId,
        resultId: result.id,
        karmaScore,
      })

      return NextResponse.json({
        success: true,
        id: result.id,
        message: 'Past life result saved successfully',
      })
    } catch (error) {
      logger.error('[PastLife Save] Failed to save:', error)
      return NextResponse.json({ error: 'Failed to save past life result' }, { status: 500 })
    }
  },
  createAuthenticatedGuard({
    route: '/api/past-life/save',
    limit: 30,
    windowSeconds: 60,
  })
)

/**
 * GET /api/past-life/save
 * 최근 저장된 전생 리딩 결과 조회
 */
export const GET = withApiMiddleware(
  async (_req: NextRequest, context: ApiContext) => {
    try {
      const latestResult = await prisma.pastLifeResult.findFirst({
        where: { userId: context.userId! },
        orderBy: { createdAt: 'desc' },
        select: {
          id: true,
          birthDate: true,
          birthTime: true,
          karmaScore: true,
          analysisData: true,
          locale: true,
          createdAt: true,
        },
      })

      if (!latestResult) {
        return NextResponse.json({ saved: false })
      }

      return NextResponse.json({
        saved: true,
        result: latestResult,
      })
    } catch (error) {
      logger.error('[PastLife Save] Failed to fetch:', error)
      return NextResponse.json({ saved: false, error: 'Failed to fetch result' }, { status: 500 })
    }
  },
  createAuthenticatedGuard({
    route: '/api/past-life/save',
    limit: 60,
    windowSeconds: 60,
  })
)

import { NextRequest, NextResponse } from 'next/server'
import {
  withApiMiddleware,
  createAuthenticatedGuard,
  apiSuccess,
  apiError,
  ErrorCodes,
  type ApiContext,
  type ApiHandlerResult,
} from '@/lib/api/middleware'
import { prisma, Prisma } from '@/lib/db/prisma'
import type { PersonaMemory } from '@prisma/client'
import { logger } from '@/lib/logger'
import { personaMemoryPostSchema, personaMemoryPatchSchema } from '@/lib/api/zodValidation'

export const dynamic = 'force-dynamic'

// GET: 페르소나 기억 조회 (로그인 필요)
export const GET = withApiMiddleware<{ data: PersonaMemory | null; isNewUser: boolean }>(
  async (_req: NextRequest, context: ApiContext) => {
    try {
      const memory = await prisma.personaMemory.findUnique({
        where: { userId: context.userId! },
      })

      return apiSuccess({
        data: memory ?? null,
        isNewUser: !memory,
      })
    } catch (err) {
      logger.error('[PersonaMemory GET] Database error', { error: err })
      return apiError(ErrorCodes.DATABASE_ERROR, 'Failed to fetch persona memory')
    }
  },
  createAuthenticatedGuard({
    route: '/api/persona-memory',
    limit: 60,
    windowSeconds: 60,
  })
)

// POST: 페르소나 기억 생성/업데이트
export const POST = withApiMiddleware<{ data: PersonaMemory; action: string }>(
  async (request: NextRequest, context: ApiContext) => {
    const rawBody = await request.json()

    const validationResult = personaMemoryPostSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[PersonaMemory POST] validation failed', {
        errors: validationResult.error.issues,
      })
      return apiError(
        ErrorCodes.VALIDATION_ERROR,
        `Validation failed: ${validationResult.error.issues.map((e) => e.message).join(', ')}`
      )
    }

    const {
      dominantThemes,
      keyInsights,
      emotionalTone,
      growthAreas,
      lastTopics,
      recurringIssues,
      birthChart,
      sajuProfile,
    } = validationResult.data

    try {
      const existing = await prisma.personaMemory.findUnique({
        where: { userId: context.userId! },
      })

      if (existing) {
        const updated = await prisma.personaMemory.update({
          where: { userId: context.userId! },
          data: {
            dominantThemes: (dominantThemes ?? existing.dominantThemes) as Prisma.InputJsonValue,
            keyInsights: (keyInsights ?? existing.keyInsights) as Prisma.InputJsonValue,
            emotionalTone: emotionalTone ?? existing.emotionalTone,
            growthAreas: (growthAreas ?? existing.growthAreas) as Prisma.InputJsonValue,
            lastTopics: (lastTopics ?? existing.lastTopics) as Prisma.InputJsonValue,
            recurringIssues: (recurringIssues ?? existing.recurringIssues) as Prisma.InputJsonValue,
            birthChart: (birthChart ?? existing.birthChart) as Prisma.InputJsonValue,
            sajuProfile: (sajuProfile ?? existing.sajuProfile) as Prisma.InputJsonValue,
          },
        })

        return apiSuccess({ data: updated, action: 'updated' })
      } else {
        const created = await prisma.personaMemory.create({
          data: {
            userId: context.userId!,
            dominantThemes,
            keyInsights,
            emotionalTone,
            growthAreas,
            lastTopics,
            recurringIssues,
            sessionCount: 0,
            birthChart: birthChart as Prisma.InputJsonValue,
            sajuProfile: sajuProfile as Prisma.InputJsonValue,
          },
        })

        return apiSuccess({ data: created, action: 'created' })
      }
    } catch (err) {
      logger.error('[PersonaMemory POST] Database error', { error: err })
      return apiError(ErrorCodes.DATABASE_ERROR, 'Failed to save persona memory')
    }
  },
  createAuthenticatedGuard({
    route: '/api/persona-memory',
    limit: 30,
    windowSeconds: 60,
  })
)

// PATCH: 페르소나 기억 부분 업데이트 (통찰 추가 등)
export const PATCH = withApiMiddleware<{
  data: PersonaMemory
  action: string
  changed?: boolean
}>(
  async (request: NextRequest, context: ApiContext) => {
    const rawBody = await request.json()

    const validationResult = personaMemoryPatchSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[PersonaMemory PATCH] validation failed', {
        errors: validationResult.error.issues,
      })
      return apiError(
        ErrorCodes.VALIDATION_ERROR,
        `Validation failed: ${validationResult.error.issues.map((e) => e.message).join(', ')}`
      )
    }

    const { action, data = {} } = validationResult.data

    try {
      const existing = await prisma.personaMemory.findUnique({
        where: { userId: context.userId! },
      })

      if (!existing) {
        return apiError(ErrorCodes.NOT_FOUND, '페르소나 기억이 없습니다. 먼저 생성해주세요.')
      }

      const updateData: Record<string, unknown> = {}

      switch (action) {
        case 'add_insight': {
          const currentInsights = (existing.keyInsights as string[]) || []
          const insight = data.insight as string | undefined
          if (insight && !currentInsights.includes(insight)) {
            updateData.keyInsights = [...currentInsights, insight]
          }
          break
        }
        case 'add_growth_area': {
          const currentGrowth = (existing.growthAreas as string[]) || []
          const area = data.area as string | undefined
          if (area && !currentGrowth.includes(area)) {
            updateData.growthAreas = [...currentGrowth, area]
          }
          break
        }
        case 'add_recurring_issue': {
          const currentIssues = (existing.recurringIssues as string[]) || []
          const issue = data.issue as string | undefined
          if (issue && !currentIssues.includes(issue)) {
            updateData.recurringIssues = [...currentIssues, issue]
          }
          break
        }
        case 'update_emotional_tone':
          if (data.tone) {
            updateData.emotionalTone = data.tone
          }
          break

        case 'increment_session':
          updateData.sessionCount = existing.sessionCount + 1
          break

        case 'update_birth_chart':
          if (data.birthChart) {
            updateData.birthChart = data.birthChart
          }
          break

        case 'update_saju_profile':
          if (data.sajuProfile) {
            updateData.sajuProfile = data.sajuProfile
          }
          break

        default:
          return apiError(ErrorCodes.BAD_REQUEST, '지원하지 않는 액션입니다.')
      }

      if (Object.keys(updateData).length === 0) {
        return apiSuccess({ data: existing, action, changed: false })
      }

      const updated = await prisma.personaMemory.update({
        where: { userId: context.userId! },
        data: updateData,
      })

      return apiSuccess({ data: updated, action, changed: true })
    } catch (err) {
      logger.error('[PersonaMemory PATCH] Database error', { error: err })
      return apiError(ErrorCodes.DATABASE_ERROR, 'Failed to update persona memory')
    }
  },
  createAuthenticatedGuard({
    route: '/api/persona-memory',
    limit: 30,
    windowSeconds: 60,
  })
)

import { NextRequest } from 'next/server'
import { prisma } from '@/lib/db/prisma'
import {
  withApiMiddleware,
  createAuthenticatedGuard,
  parseJsonBody,
  validateRequired,
  apiError,
  apiSuccess,
  ErrorCodes,
  type ApiContext,
} from '@/lib/api/middleware'
import { readingsSaveSchema, readingsGetQuerySchema } from '@/lib/api/zodValidation'
import { logger } from '@/lib/logger'

// POST: Create a new reading
export const POST = withApiMiddleware(
  async (req: NextRequest, context: ApiContext) => {
    const rawBody = await parseJsonBody<{ type: string; title?: string; content: string }>(req)

    // Validate with Zod
    const validationResult = readingsSaveSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[Readings] validation failed', { errors: validationResult.error.issues })
      return apiError(
        ErrorCodes.VALIDATION_ERROR,
        `Validation failed: ${validationResult.error.issues.map((e) => e.message).join(', ')}`
      )
    }

    const { type, title, content } = validationResult.data

    const reading = await prisma.reading.create({
      data: {
        userId: context.userId!,
        type,
        title: title || null,
        content,
      },
    })

    return apiSuccess({ success: true, id: reading.id })
  },
  createAuthenticatedGuard({ route: 'readings/create', limit: 20 })
)

// GET: Fetch user's readings
export const GET = withApiMiddleware(
  async (req: NextRequest, context: ApiContext) => {
    const { searchParams } = new URL(req.url)
    const queryValidation = readingsGetQuerySchema.safeParse({
      type: searchParams.get('type') || undefined,
      limit: searchParams.get('limit') || undefined,
    })
    if (!queryValidation.success) {
      logger.warn('[Readings GET] query validation failed', {
        errors: queryValidation.error.issues,
      })
      return apiError(
        ErrorCodes.VALIDATION_ERROR,
        `Validation failed: ${queryValidation.error.issues.map((e) => e.message).join(', ')}`
      )
    }
    const { type, limit } = queryValidation.data

    const readings = await prisma.reading.findMany({
      where: {
        userId: context.userId!,
        ...(type ? { type } : {}),
      },
      orderBy: { createdAt: 'desc' },
      take: limit,
    })

    return apiSuccess({ readings })
  },
  createAuthenticatedGuard({ route: 'readings/list', limit: 30 })
)

// src/app/api/saju/route.ts

import { NextRequest } from 'next/server'
import { toDate } from 'date-fns-tz'
import { prisma } from '@/lib/db/prisma'
import { calculateSajuData } from '@/lib/Saju/saju'
import { getCreditBalance } from '@/lib/credits/creditService'
import { apiClient } from '@/lib/api/ApiClient'
import { getNowInTimezone } from '@/lib/datetime'
import {
  getDaeunCycles,
  getAnnualCycles,
  getMonthlyCycles,
  getIljinCalendar,
} from '@/lib/Saju/unse'
import type { SajuPillars } from '@/lib/Saju/types'
import {
  getShinsalHits,
  getTwelveStagesForPillars,
  getTwelveShinsalSingleByPillar,
} from '@/lib/Saju/shinsal'
import { analyzeRelations, toAnalyzeInputFromSaju } from '@/lib/Saju/relations'
import { logger } from '@/lib/logger'
import { cacheGet, cacheSet, CACHE_TTL } from '@/lib/cache/redis-cache'

// Middleware imports
import {
  withApiMiddleware,
  createSajuGuard,
  parseJsonBody,
  validateRequired,
  apiError,
  apiSuccess,
  ErrorCodes,
  type ApiContext,
} from '@/lib/api/middleware'
import type { SajuRequestBody } from '@/lib/api/types'
import { sajuCalculationRequestSchema } from '@/lib/api/zodValidation'

// Services
import {
  checkPremiumStatus,
  withYY,
  toBranch,
  pickLucky,
  formatSajuForGPT,
  coerceJijanggan,
  enrichSibsin,
  buildJijangganRaw,
  performAdvancedAnalysis,
} from './services'

/* -----------------------------
   Handler
------------------------------*/
export const POST = withApiMiddleware(async (req: NextRequest, context: ApiContext) => {
  // 1. Parse and validate request body
  const rawBody = await parseJsonBody<SajuRequestBody>(req)

  // Validate with Zod
  const validationResult = sajuCalculationRequestSchema.safeParse(rawBody)
  if (!validationResult.success) {
    logger.warn('[Saju] validation failed', { errors: validationResult.error.issues })
    return apiError(
      ErrorCodes.VALIDATION_ERROR,
      `Validation failed: ${validationResult.error.issues.map((e) => e.message).join(', ')}`
    )
  }

  const {
    birthDate: birthDateString,
    birthTime: birthTimeRaw,
    gender,
    calendarType,
    timezone,
    userTimezone,
  } = validationResult.data

  // 2. Check premium status (credit-based)
  let isPremium = false
  let creditBalance = null

  if (context.userId) {
    // 초기 사주 분석은 무료 (크레딧 차감 없음)
    creditBalance = await getCreditBalance(context.userId)
    isPremium = creditBalance.plan !== 'free'
  } else if (context.session?.user?.email) {
    // 비로그인 사용자: 기존 Stripe 체크 (호환성)
    isPremium = await checkPremiumStatus(context.session.user.email, context.ip)
  }

  // 3. Calculate Saju
  const effectiveUserTz = userTimezone || timezone
  const birthDate = toDate(`${birthDateString}T${birthTimeRaw}:00`, { timeZone: timezone })
  const adjustedBirthTime = String(birthTimeRaw)

  const sajuGender = gender.toLowerCase() === 'female' ? ('female' as const) : ('male' as const)
  const sajuResult = calculateSajuData(
    birthDateString,
    adjustedBirthTime,
    sajuGender,
    calendarType,
    timezone,
    false
  )

  // 4. Build Saju pillars
  const sajuPillars: SajuPillars = {
    year: {
      heavenlyStem: withYY(sajuResult.yearPillar.heavenlyStem),
      earthlyBranch: toBranch(sajuResult.yearPillar.earthlyBranch),
      jijanggan: sajuResult.yearPillar.jijanggan,
    },
    month: {
      heavenlyStem: withYY(sajuResult.monthPillar.heavenlyStem),
      earthlyBranch: toBranch(sajuResult.monthPillar.earthlyBranch),
      jijanggan: sajuResult.monthPillar.jijanggan,
    },
    day: {
      heavenlyStem: withYY(sajuResult.dayPillar.heavenlyStem),
      earthlyBranch: toBranch(sajuResult.dayPillar.earthlyBranch),
      jijanggan: sajuResult.dayPillar.jijanggan,
    },
    time: {
      heavenlyStem: withYY(sajuResult.timePillar.heavenlyStem),
      earthlyBranch: toBranch(sajuResult.timePillar.earthlyBranch),
      jijanggan: sajuResult.timePillar.jijanggan,
    },
  }

  // 5. Calculate fortune cycles (운세)
  const daeunInfo = getDaeunCycles(
    birthDate,
    sajuGender,
    sajuPillars,
    sajuResult.dayMaster,
    'Asia/Seoul'
  )
  const userNow = getNowInTimezone(effectiveUserTz)
  const yeonun = getAnnualCycles(userNow.year, 10, sajuResult.dayMaster)
  const wolun = getMonthlyCycles(userNow.year, sajuResult.dayMaster)
  const iljin = getIljinCalendar(userNow.year, userNow.month, sajuResult.dayMaster)

  // 6. Calculate 12운성/신살/길성
  const twelveStages = getTwelveStagesForPillars(sajuPillars)

  const twelveShinsalSingle = getTwelveShinsalSingleByPillar(sajuPillars, {
    includeTwelveAll: true,
    useMonthCompletion: false,
    ruleSet: 'your',
  })

  const rawShinsal = getShinsalHits(sajuPillars, {
    includeLucky: true,
    includeUnlucky: true,
    includeTwelveAll: true,
    useMonthCompletion: false,
    includeGeneralShinsal: true,
    includeLuckyDetails: true,
    ruleSet: 'your',
  })

  // 7. Build display data by pillar
  const dayMasterStem = sajuResult.dayPillar.heavenlyStem.name

  const buildPillar = (pillar: 'time' | 'day' | 'month' | 'year') => {
    const p = sajuResult[`${pillar}Pillar` as const]
    const jgRaw = buildJijangganRaw(p.jijanggan)
    const jgObj = enrichSibsin(coerceJijanggan(p.jijanggan), dayMasterStem)
    const display = (['chogi', 'junggi', 'jeonggi'] as const)
      .map((k) => {
        const it = jgObj[k]
        if (!it?.name) return ''
        const han = it.name
        const sib = it.sibsin ? `(${it.sibsin})` : ''
        return `${han}${sib}`
      })
      .filter(Boolean)
      .join(' · ')
    const shinsalKinds = rawShinsal.filter((h) => h.pillars.includes(pillar)).map((h) => h.kind)

    return {
      stem: p.heavenlyStem.name,
      branch: p.earthlyBranch.name,
      jijanggan: {
        raw: jgRaw.raw,
        list: jgRaw.list,
        display,
        object: jgObj,
      },
      twelveStage: twelveStages[pillar],
      twelveShinsal: Array.isArray(twelveShinsalSingle[pillar])
        ? twelveShinsalSingle[pillar]
        : [twelveShinsalSingle[pillar]].filter(Boolean),
      lucky: pickLucky(rawShinsal, pillar),
      shinsal: shinsalKinds,
      shinsalSummary: pickLucky(rawShinsal, pillar).join('·'),
    }
  }

  const byPillar = {
    time: buildPillar('time'),
    day: buildPillar('day'),
    month: buildPillar('month'),
    year: buildPillar('year'),
  }

  // 8. Analyze relations
  const relationsInput = toAnalyzeInputFromSaju(
    {
      year: sajuResult.yearPillar,
      month: sajuResult.monthPillar,
      day: sajuResult.dayPillar,
      time: sajuResult.timePillar,
    },
    sajuResult.dayPillar.heavenlyStem.name
  )
  const relations = analyzeRelations(relationsInput)

  // 9. Advanced analysis
  const timePillarSimple = {
    stem: sajuResult.timePillar.heavenlyStem.name,
    branch: sajuResult.timePillar.earthlyBranch.name,
  }
  const simplePillars = {
    year: {
      stem: sajuResult.yearPillar.heavenlyStem.name,
      branch: sajuResult.yearPillar.earthlyBranch.name,
    },
    month: {
      stem: sajuResult.monthPillar.heavenlyStem.name,
      branch: sajuResult.monthPillar.earthlyBranch.name,
    },
    day: {
      stem: sajuResult.dayPillar.heavenlyStem.name,
      branch: sajuResult.dayPillar.earthlyBranch.name,
    },
    time: timePillarSimple,
    hour: timePillarSimple,
  }
  const pillarsWithHour = {
    year: simplePillars.year,
    month: simplePillars.month,
    day: simplePillars.day,
    hour: simplePillars.time,
  }

  const advancedResult = performAdvancedAnalysis(
    simplePillars,
    pillarsWithHour,
    sajuPillars,
    dayMasterStem,
    sajuResult.monthPillar.earthlyBranch.name,
    twelveStages,
    sajuResult.fiveElements
  )

  const gptPrompt = formatSajuForGPT({
    ...sajuResult,
    birthDate: birthDateString,
    daeun: daeunInfo,
  })

  const analysisDate = `${userNow.year}-${String(userNow.month).padStart(2, '0')}-${String(userNow.day).padStart(2, '0')}`
  const fullAdvancedAnalysis = advancedResult

  // 10. AI backend call (GPT) with caching
  let aiInterpretation = ''
  let aiModelUsed = ''

  // Cache key: birthDate + birthTime + gender + calendar + locale + premium status
  const aiCacheKey = `saju:ai:v1:${birthDateString}:${adjustedBirthTime}:${gender}:${calendarType}:${context.locale}:${isPremium ? 'premium' : 'free'}`

  const cachedAI = await cacheGet<{ interpretation: string; model: string }>(aiCacheKey)

  if (cachedAI) {
    aiInterpretation = cachedAI.interpretation
    aiModelUsed = cachedAI.model
  } else {
    try {
      const response = await apiClient.post(
        '/ask',
        {
          theme: 'saju',
          prompt: gptPrompt,
          saju: {
            dayMaster: sajuResult.dayMaster,
            fiveElements: sajuResult.fiveElements,
            pillars: simplePillars,
            daeun: daeunInfo,
            yeonun,
            wolun,
          },
          locale: context.locale,
          advancedSaju: isPremium ? fullAdvancedAnalysis : null,
        },
        { timeout: 90000 }
      )

      if (response.ok) {
        const data = (response.data as Record<string, unknown>)?.data as
          | Record<string, unknown>
          | undefined
        aiInterpretation = (data?.fusion_layer || data?.report || '') as string
        aiModelUsed = (data?.model || 'gpt-4o') as string

        // Cache the AI interpretation for 7 days
        await cacheSet(
          aiCacheKey,
          { interpretation: aiInterpretation, model: aiModelUsed },
          CACHE_TTL.SAJU_RESULT
        )
      }
    } catch (aiErr) {
      logger.warn('[Saju API] AI backend call failed:', aiErr)
      const dayMasterName = sajuResult.dayMaster?.name || ''
      aiInterpretation =
        context.locale === 'ko'
          ? `${dayMasterName} 일간으로 태어나셨습니다. 현재 AI 해석 서비스가 일시적으로 이용 불가합니다. 기본 사주 분석 결과를 확인해주세요.`
          : `You were born with ${dayMasterName} as your day master. AI interpretation is temporarily unavailable. Please check the basic Saju analysis results.`
      aiModelUsed = 'fallback'
    }
  }

  // 11. Save reading record (logged-in users only)
  if (context.userId) {
    try {
      await prisma.reading.create({
        data: {
          userId: context.userId,
          type: 'saju',
          title: `${sajuResult.dayMaster?.name || ''} 일간 사주 분석`,
          content: JSON.stringify({
            birthDate: birthDateString,
            birthTime: adjustedBirthTime,
            gender,
            timezone,
            dayMaster: sajuResult.dayMaster,
            fiveElements: sajuResult.fiveElements,
            pillars: {
              year: sajuResult.yearPillar,
              month: sajuResult.monthPillar,
              day: sajuResult.dayPillar,
              time: sajuResult.timePillar,
            },
          }),
        },
      })
    } catch (saveErr) {
      logger.warn('[Saju API] Failed to save reading:', saveErr)
    }
  }

  // 12. Return success response
  return apiSuccess({
    isPremium,
    isLoggedIn: context.isAuthenticated,
    birthYear: new Date(birthDateString).getFullYear(),
    birthDate: birthDateString,
    analysisDate,
    userTimezone: effectiveUserTz,
    yearPillar: sajuResult.yearPillar,
    monthPillar: sajuResult.monthPillar,
    dayPillar: sajuResult.dayPillar,
    timePillar: sajuResult.timePillar,
    fiveElements: sajuResult.fiveElements,
    dayMaster: sajuResult.dayMaster,
    daeun: daeunInfo,
    yeonun,
    wolun,
    iljin,
    table: { byPillar },
    shinsal: (rawShinsal || [])
      .map((h) => {
        const first = h.pillars?.[0]
        const scope =
          first === 'year'
            ? '연주'
            : first === 'month'
              ? '월주'
              : first === 'day'
                ? '일주'
                : first === 'time'
                  ? '시주'
                  : ''
        return {
          name: h.kind,
          scope,
          from: h.target ?? '',
          to: '',
          note: h.detail ?? '',
        }
      })
      .filter((x) => x.name && x.scope),
    shinsalRaw: rawShinsal,
    relations,
    gptPrompt,
    aiInterpretation,
    aiModelUsed,
    advancedAnalysis: fullAdvancedAnalysis,
  })
}, createSajuGuard())

// src/app/api/tarot/chat/stream/route.ts
// Streaming Tarot Chat API - Real-time SSE proxy to backend

import { NextRequest, NextResponse } from 'next/server'
import { initializeApiContext, createPublicStreamGuard } from '@/lib/api/middleware'
import { createSSEStreamProxy, createFallbackSSEStream } from '@/lib/streaming'
import { apiClient } from '@/lib/api/ApiClient'
import { enforceBodySize } from '@/lib/http'
import { getServerSession } from 'next-auth'
import { authOptions } from '@/lib/auth/authOptions'
import { prisma } from '@/lib/db/prisma'
import {
  cleanStringArray,
  normalizeMessages as normalizeMessagesBase,
  type ChatMessage,
} from '@/lib/api'
import { logger } from '@/lib/logger'

import { parseRequestBody } from '@/lib/api/requestParser'
import { tarotChatStreamRequestSchema } from '@/lib/api/zodValidation'
interface CardContext {
  position: string
  name: string
  is_reversed: boolean
  meaning: string
  keywords?: string[]
}

interface TarotContext {
  spread_title: string
  category: string
  cards: CardContext[]
  overall_message: string
  guidance: string
}

import {
  ALLOWED_LOCALES,
  MESSAGE_LIMITS,
  TEXT_LIMITS,
  LIST_LIMITS,
} from '@/lib/constants/api-limits'
import { HTTP_STATUS } from '@/lib/constants/http'
const ALLOWED_TAROT_LANG = ALLOWED_LOCALES
const MAX_MESSAGES = MESSAGE_LIMITS.MAX_MESSAGES
const MAX_MESSAGE_LENGTH = MESSAGE_LIMITS.MAX_MESSAGE_LENGTH
const MAX_CARD_COUNT = LIST_LIMITS.MAX_CARDS
const MAX_CARD_TEXT = TEXT_LIMITS.MAX_CARD_TEXT
const MAX_TITLE_TEXT = TEXT_LIMITS.MAX_TITLE
const MAX_GUIDANCE_TEXT = TEXT_LIMITS.MAX_GUIDANCE

// Use shared normalizeMessages with local config
function normalizeMessages(raw: unknown): ChatMessage[] {
  return normalizeMessagesBase(raw, {
    maxMessages: MAX_MESSAGES,
    maxLength: MAX_MESSAGE_LENGTH,
  })
}

function sanitizeCards(raw: unknown): CardContext[] {
  if (!Array.isArray(raw)) {
    return []
  }
  const cards: CardContext[] = []
  for (const card of raw.slice(0, MAX_CARD_COUNT)) {
    if (!card || typeof card !== 'object') {
      continue
    }
    const record = card as Record<string, unknown>
    const position =
      typeof record.position === 'string' ? record.position.trim().slice(0, MAX_TITLE_TEXT) : ''
    const name = typeof record.name === 'string' ? record.name.trim().slice(0, MAX_TITLE_TEXT) : ''
    const meaning =
      typeof record.meaning === 'string' ? record.meaning.trim().slice(0, MAX_CARD_TEXT) : ''
    if (!position || !name || !meaning) {
      continue
    }
    const isReversed = Boolean(record.is_reversed ?? record.isReversed)
    const keywords = cleanStringArray(record.keywords)
    cards.push({
      position,
      name,
      is_reversed: isReversed,
      meaning,
      keywords,
    })
  }
  return cards
}

function sanitizeContext(raw: unknown): TarotContext | null {
  if (!raw || typeof raw !== 'object') {
    return null
  }
  const record = raw as Record<string, unknown>
  const spread_title =
    typeof record.spread_title === 'string'
      ? record.spread_title.trim().slice(0, MAX_TITLE_TEXT)
      : ''
  const category =
    typeof record.category === 'string' ? record.category.trim().slice(0, MAX_TITLE_TEXT) : ''
  const cards = sanitizeCards(record.cards)
  const overall_message =
    typeof record.overall_message === 'string'
      ? record.overall_message.trim().slice(0, MAX_GUIDANCE_TEXT)
      : ''
  const guidance =
    typeof record.guidance === 'string' ? record.guidance.trim().slice(0, MAX_GUIDANCE_TEXT) : ''

  if (!spread_title || !category || cards.length === 0) {
    return null
  }

  return { spread_title, category, cards, overall_message, guidance }
}

interface PersonalityData {
  typeCode: string
  personaName: string
  energyScore: number
  cognitionScore: number
  decisionScore: number
  rhythmScore: number
  analysisData: {
    summary?: string
    keyMotivations?: string[]
    strengths?: string[]
    challenges?: string[]
  }
}

function getPersonalityLabel(score: number, trueLabel: string, falseLabel: string): string {
  return score >= 50 ? trueLabel : falseLabel
}

function buildPersonalityContext(personality: PersonalityData, language: 'ko' | 'en'): string {
  const {
    typeCode,
    personaName,
    energyScore,
    cognitionScore,
    decisionScore,
    rhythmScore,
    analysisData,
  } = personality

  if (language === 'ko') {
    return [
      '\n\n사용자 성격 정보 (Nova Persona):',
      `- 페르소나: ${personaName} (${typeCode})`,
      `- 에너지: ${getPersonalityLabel(energyScore, 'Radiant(외향적)', 'Grounded(내향적)')} ${energyScore}`,
      `- 인지: ${getPersonalityLabel(cognitionScore, 'Visionary(직관적)', 'Structured(체계적)')} ${cognitionScore}`,
      `- 결정: ${getPersonalityLabel(decisionScore, 'Logic(논리적)', 'Empathic(공감적)')} ${decisionScore}`,
      `- 리듬: ${getPersonalityLabel(rhythmScore, 'Flow(유동적)', 'Anchor(안정적)')} ${rhythmScore}`,
      analysisData.keyMotivations
        ? `- 핵심 동기: ${analysisData.keyMotivations.slice(0, 2).join(', ')}`
        : '',
      analysisData.strengths ? `- 강점: ${analysisData.strengths.slice(0, 2).join(', ')}` : '',
      '\n이 성격 정보를 바탕으로 사용자의 성향에 맞춰 조언을 개인화해줘.',
      `예: ${getPersonalityLabel(energyScore, '외향적이므로 다른 사람과 함께하는 행동 제안', '내향적이므로 혼자 성찰하는 시간 제안')}`,
      `${getPersonalityLabel(cognitionScore, '비전과 가능성 중심으로', '구체적이고 단계별로')} 설명해줘.`,
    ]
      .filter(Boolean)
      .join('\n')
  } else {
    return [
      '\n\nUser Personality (Nova Persona):',
      `- Persona: ${personaName} (${typeCode})`,
      `- Energy: ${getPersonalityLabel(energyScore, 'Radiant(extroverted)', 'Grounded(introverted)')} ${energyScore}`,
      `- Cognition: ${getPersonalityLabel(cognitionScore, 'Visionary(intuitive)', 'Structured(systematic)')} ${cognitionScore}`,
      `- Decision: ${getPersonalityLabel(decisionScore, 'Logic-focused', 'Empathy-focused')} ${decisionScore}`,
      `- Rhythm: ${getPersonalityLabel(rhythmScore, 'Flow(flexible)', 'Anchor(stable)')} ${rhythmScore}`,
      analysisData.keyMotivations
        ? `- Key motivations: ${analysisData.keyMotivations.slice(0, 2).join(', ')}`
        : '',
      analysisData.strengths ? `- Strengths: ${analysisData.strengths.slice(0, 2).join(', ')}` : '',
      '\nPersonalize advice based on this personality.',
      `E.g., ${getPersonalityLabel(energyScore, 'suggest social actions', 'suggest introspective time')},`,
      `${getPersonalityLabel(cognitionScore, 'focus on vision and possibilities', 'provide concrete step-by-step guidance')}.`,
    ]
      .filter(Boolean)
      .join('\n')
  }
}

function formatCardLines(cards: CardContext[], language: 'ko' | 'en'): string {
  return cards
    .map((c, idx) => {
      const pos = c.position || `Card ${idx + 1}`
      const orient = c.is_reversed
        ? language === 'ko'
          ? '역위'
          : 'reversed'
        : language === 'ko'
          ? '정위'
          : 'upright'
      return `- ${pos}: ${c.name} (${orient})`
    })
    .join('\n')
}

