  return language === 'ko'
    ? buildKoreanInstruction(context, cardLines, personalityContext)
    : buildEnglishInstruction(context, cardLines, personalityContext)
}

function generateFallbackMessage(
  tarotContext: TarotContext,
  lastUserMessage: string,
  language: 'ko' | 'en'
): string {
  const cardSummary = tarotContext.cards.map((c) => `${c.position}: ${c.name}`).join(', ')

  if (language === 'ko') {
    return `${tarotContext.spread_title} 리딩에서 ${cardSummary} 카드가 나왔네요. "${lastUserMessage}"에 대해 말씀드리면, 카드들이 전하는 메시지는 ${tarotContext.overall_message || '내면의 지혜를 믿으라는 것'}입니다. ${tarotContext.guidance || '카드의 조언에 귀 기울여보세요.'}\n\n다음으로 물어볼 것: 특정 카드에 대해 더 자세히 알고 싶으신가요?`
  } else {
    return `In your ${tarotContext.spread_title} reading with ${cardSummary}, regarding "${lastUserMessage}", the cards suggest: ${tarotContext.overall_message || 'trust your inner wisdom'}. ${tarotContext.guidance || 'Listen to the guidance of the cards.'}\n\nNext question: Would you like to explore any specific card in more detail?`
  }
}

async function fetchUserPersonality(userId: string): Promise<PersonalityData | null> {
  try {
    const personalityResult = await prisma.personalityResult.findUnique({
      where: { userId },
      select: {
        typeCode: true,
        personaName: true,
        energyScore: true,
        cognitionScore: true,
        decisionScore: true,
        rhythmScore: true,
        analysisData: true,
      },
    })

    if (!personalityResult || !personalityResult.analysisData) {
      return null
    }

    const analysisData = personalityResult.analysisData as Record<string, unknown>
    return {
      typeCode: personalityResult.typeCode,
      personaName: personalityResult.personaName,
      energyScore: personalityResult.energyScore,
      cognitionScore: personalityResult.cognitionScore,
      decisionScore: personalityResult.decisionScore,
      rhythmScore: personalityResult.rhythmScore,
      analysisData: {
        summary: typeof analysisData.summary === 'string' ? analysisData.summary : undefined,
        keyMotivations: Array.isArray(analysisData.keyMotivations)
          ? analysisData.keyMotivations.filter((x): x is string => typeof x === 'string')
          : undefined,
        strengths: Array.isArray(analysisData.strengths)
          ? analysisData.strengths.filter((x): x is string => typeof x === 'string')
          : undefined,
        challenges: Array.isArray(analysisData.challenges)
          ? analysisData.challenges.filter((x): x is string => typeof x === 'string')
          : undefined,
      },
    }
  } catch (err) {
    logger.error('[TarotChatStream] Failed to fetch personality:', err)
    return null
  }
}

export async function POST(req: NextRequest) {
  try {
    // Apply middleware: rate limiting + public token auth + credit consumption
    const guardOptions = createPublicStreamGuard({
      route: 'tarot-chat-stream',
      limit: 30,
      windowSeconds: 60,
      requireCredits: true,
      creditType: 'followUp', // 타로 후속 질문은 followUp 타입 사용
      creditAmount: 1,
    })

    const { context, error } = await initializeApiContext(req, guardOptions)
    if (error) {
      return error
    }

    const oversized = enforceBodySize(req, 256 * 1024)
    if (oversized) {
      return oversized
    }

    const rawBody = await parseRequestBody<Record<string, unknown>>(req, {
      context: 'Tarot Chat Stream',
    })
    if (!rawBody || typeof rawBody !== 'object') {
      return NextResponse.json({ error: 'invalid_body' }, { status: HTTP_STATUS.BAD_REQUEST })
    }

    // Validate with Zod
    const validationResult = tarotChatStreamRequestSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[Tarot chat-stream] validation failed', {
        errors: validationResult.error.issues,
      })
      return NextResponse.json(
        {
          error: 'validation_failed',
          details: validationResult.error.issues.map((e) => ({
            path: e.path.join('.'),
            message: e.message,
          })),
        },
        { status: HTTP_STATUS.BAD_REQUEST }
      )
    }

    const validatedData = validationResult.data
    const language = (validatedData.language || context.locale) as 'ko' | 'en'
    const messages = normalizeMessages(validatedData.messages)
    const tarotContext = sanitizeContext(validatedData.context)
    const counselorId = validatedData.counselor_id
    const counselorStyle = validatedData.counselor_style

    if (!messages || messages.length === 0) {
      return NextResponse.json({ error: 'Missing messages' }, { status: HTTP_STATUS.BAD_REQUEST })
    }
    if (!tarotContext) {
      return NextResponse.json(
        { error: 'Invalid tarot context' },
        { status: HTTP_STATUS.BAD_REQUEST }
      )
    }

    // Credits already consumed by middleware

    // Fetch user's personality result (if authenticated)
    let personalityData: PersonalityData | null = null
    const session = await getServerSession(authOptions)
    if (session?.user?.id) {
      personalityData = await fetchUserPersonality(session.user.id)
    }

    // Inject system guardrails for consistent, card-grounded answers with personality
    const systemInstruction = buildSystemInstruction(tarotContext, language, personalityData)
    const messagesWithSystem: ChatMessage[] = [
      { role: 'system', content: systemInstruction },
      ...messages,
    ]

    // Call backend streaming endpoint using apiClient
    const streamResult = await apiClient.postSSEStream('/api/tarot/chat-stream', {
      messages: messagesWithSystem,
      context: tarotContext,
      language,
      counselor_id: counselorId,
      counselor_style: counselorStyle,
    })

    if (!streamResult.ok) {
      logger.error('[TarotChatStream] Backend error:', {
        status: streamResult.status,
        error: streamResult.error,
      })

      // Generate fallback response based on context
      const lastUserMessage = messages.filter((m) => m.role === 'user').pop()?.content || ''
      const fallbackMessage = generateFallbackMessage(tarotContext, lastUserMessage, language)

      return createFallbackSSEStream({
        content: fallbackMessage,
        done: true,
        'X-Fallback': '1',
      })
    }

    // Proxy the SSE stream from backend to client
    return createSSEStreamProxy({
      source: streamResult.response,
      route: 'TarotChatStream',
      additionalHeaders: {
        'X-Fallback': '0',
      },
    })
  } catch (err: unknown) {
    logger.error('Tarot chat stream error:', err)
    return NextResponse.json({ error: 'Server error' }, { status: HTTP_STATUS.SERVER_ERROR })
  }
}

// src/app/api/tarot/interpret-stream/route.ts
// Direct OpenAI Streaming Tarot Interpretation API

import { NextRequest, NextResponse } from 'next/server'
import { initializeApiContext, createPublicStreamGuard } from '@/lib/api/middleware'
import { createSSEEvent, createSSEDoneEvent } from '@/lib/streaming'
import { apiClient } from '@/lib/api/ApiClient'
import { enforceBodySize } from '@/lib/http'
import { sanitizeString } from '@/lib/api/sanitizers'
import { logger } from '@/lib/logger'
import { HTTP_STATUS } from '@/lib/constants/http'
import { recordExternalCall } from '@/lib/metrics/index'
import { tarotInterpretStreamSchema } from '@/lib/api/zodValidation'

interface CardInput {
  name: string
  nameKo?: string
  isReversed: boolean
  position: string
  positionKo?: string
  keywords?: string[]
  keywordsKo?: string[]
}

interface StreamInterpretRequest {
  categoryId: string
  spreadId: string
  spreadTitle: string
  cards: CardInput[]
  userQuestion?: string
  language?: 'ko' | 'en'
  // 개인화 정보
  birthdate?: string // YYYY-MM-DD
  zodiacSign?: string // 별자리
  previousReadings?: string[] // 이전 상담 요약
  questionMood?: 'worried' | 'curious' | 'hopeful' | 'urgent' | 'neutral' // 질문 감정
}

const MAX_TITLE = 120
const MAX_QUESTION = 600
const MAX_CARDS = 15
const BACKEND_TIMEOUT_MS = 20000
const OPENAI_TIMEOUT_MS = 30000

// Use centralized sanitizeString from @/lib/api/sanitizers

function buildFallbackPayload(
  cards: CardInput[],
  language: 'ko' | 'en'
): { overall: string; cards: { position: string; interpretation: string }[]; advice: string } {
  const isKorean = language === 'ko'
  const overall = isKorean
    ? '\uCE74\uB4DC\uC5D0\uC11C \uC804\uD574\uC9C0\uB294 \uD575\uC2EC \uBA54\uC2DC\uC9C0\uB97C \uC815\uB9AC\uD588\uC2B5\uB2C8\uB2E4.'
    : 'Here is the core message the cards are pointing to.'
  const advice = isKorean
    ? '\uC624\uB298 \uD560 \uC218 \uC788\uB294 \uC791\uC740 \uD589\uB3D9\uBD80\uD130 \uC2DC\uC791\uD574 \uBCF4\uC138\uC694.'
    : 'Start with one small, concrete step you can take today.'

  const cardsPayload = cards.map((card, index) => {
    const position =
      (isKorean && card.positionKo ? card.positionKo : card.position) || `Card ${index + 1}`
    const name = (isKorean && card.nameKo ? card.nameKo : card.name) || `Card ${index + 1}`
    const orientation = card.isReversed
      ? isKorean
        ? '\uC5ED\uBC29\uD5A5'
        : 'reversed'
      : isKorean
        ? '\uC815\uBC29\uD5A5'
        : 'upright'
    const interpretation = isKorean
      ? `${name} (${orientation}) \uCE74\uB4DC\uB294 \uD604\uC7AC \uC0C1\uD669\uC5D0\uC11C \uC911\uC694\uD55C \uD3EC\uC778\uD2B8\uB97C \uC9DA\uC5B4 \uC90D\uB2C8\uB2E4.`
      : `${name} (${orientation}) highlights a key point in your current situation.`
    return { position, interpretation }
  })

  return { overall, cards: cardsPayload, advice }
}

function normalizeAdvice(advice: unknown): string {
  if (typeof advice === 'string') {
    return advice
  }
  if (Array.isArray(advice)) {
    const lines = advice
      .map((entry) => {
        if (!entry || typeof entry !== 'object') {
          return ''
        }
        const record = entry as Record<string, unknown>
        const title = typeof record.title === 'string' ? record.title.trim() : ''
        const detail = typeof record.detail === 'string' ? record.detail.trim() : ''
        if (title && detail) {
          return `${title}: ${detail}`
        }
        return title || detail
      })
      .filter(Boolean)
    return lines.join('\n')
  }
  return ''
}

function normalizeBackendPayload(
  data: unknown,
  fallback: {
    overall: string
    cards: { position: string; interpretation: string }[]
    advice: string
  }
): {
  overall: string
  cards: { position: string; interpretation: string }[]
  advice: string
} | null {
  if (!data || typeof data !== 'object') {
    return null
  }
  const record = data as Record<string, unknown>
  const overall =
    typeof record.overall_message === 'string' && record.overall_message.trim()
      ? record.overall_message
      : fallback.overall
  const advice = normalizeAdvice(record.guidance) || fallback.advice

  const insights = Array.isArray(record.card_insights) ? record.card_insights : []
  const cards =
    insights.length > 0
      ? insights.map((entry, index) => {
          const cardRecord = entry as Record<string, unknown>
          const position =
            typeof cardRecord.position === 'string' && cardRecord.position.trim()
              ? cardRecord.position
              : fallback.cards[index]?.position || `Card ${index + 1}`
          const interpretation =
            typeof cardRecord.interpretation === 'string' && cardRecord.interpretation.trim()
              ? cardRecord.interpretation
              : fallback.cards[index]?.interpretation || ''
          return { position, interpretation }
        })
      : fallback.cards

  return { overall, cards, advice }
}

function streamJsonPayload(
  payload: {
    overall: string
    cards: { position: string; interpretation: string }[]
    advice: string
  },
  extraHeaders?: Record<string, string>
): Response {
  const encoder = new TextEncoder()
  const jsonText = JSON.stringify(payload)
  const stream = new ReadableStream({
    start(controller) {
      controller.enqueue(encoder.encode(createSSEEvent({ content: jsonText })))
      controller.enqueue(encoder.encode(createSSEDoneEvent()))
      controller.close()
    },
  })

  return new Response(stream, {
    headers: {
      'Content-Type': 'text/event-stream',
      'Cache-Control': 'no-cache',
      Connection: 'keep-alive',
      ...(extraHeaders || {}),
    },
  })
}

async function fetchBackendFallback(payload: {
  categoryId: string
  spreadId: string
  spreadTitle: string
  cards: CardInput[]
  userQuestion: string
  language: 'ko' | 'en'
  birthdate: string
}): Promise<unknown | null> {
  try {
    const result = await apiClient.post(
      '/api/tarot/interpret',
      {
        category: payload.categoryId,
        spread_id: payload.spreadId,
        spread_title: payload.spreadTitle,
        cards: payload.cards.map((card) => ({
          name: card.name,
          is_reversed: card.isReversed,
          position: card.position,
        })),
        user_question: payload.userQuestion,
        language: payload.language,
        birthdate: payload.birthdate || undefined,
      },
      { timeout: BACKEND_TIMEOUT_MS }
    )

    if (!result.ok) {
      logger.error('Tarot stream backend fallback error:', {
        status: result.status,
        error: result.error,
      })
      return null
    }

    return result.data
  } catch (error) {
    logger.error('Tarot stream backend fallback exception:', { error })
    return null
  }
}

// 별자리 계산 함수
function getZodiacSign(
  birthdate: string
): { sign: string; signKo: string; element: string } | null {
  const date = new Date(birthdate)
  if (isNaN(date.getTime())) {
    return null
  }

  const month = date.getMonth() + 1
  const day = date.getDate()

  const zodiacData = [
    { sign: 'Capricorn', signKo: '염소자리', element: '흙', start: [12, 22], end: [1, 19] },
    { sign: 'Aquarius', signKo: '물병자리', element: '공기', start: [1, 20], end: [2, 18] },
    { sign: 'Pisces', signKo: '물고기자리', element: '물', start: [2, 19], end: [3, 20] },
    { sign: 'Aries', signKo: '양자리', element: '불', start: [3, 21], end: [4, 19] },
    { sign: 'Taurus', signKo: '황소자리', element: '흙', start: [4, 20], end: [5, 20] },
    { sign: 'Gemini', signKo: '쌍둥이자리', element: '공기', start: [5, 21], end: [6, 20] },
    { sign: 'Cancer', signKo: '게자리', element: '물', start: [6, 21], end: [7, 22] },
    { sign: 'Leo', signKo: '사자자리', element: '불', start: [7, 23], end: [8, 22] },
    { sign: 'Virgo', signKo: '처녀자리', element: '흙', start: [8, 23], end: [9, 22] },
    { sign: 'Libra', signKo: '천칭자리', element: '공기', start: [9, 23], end: [10, 22] },
    { sign: 'Scorpio', signKo: '전갈자리', element: '물', start: [10, 23], end: [11, 21] },
    { sign: 'Sagittarius', signKo: '사수자리', element: '불', start: [11, 22], end: [12, 21] },
  ]

  for (const z of zodiacData) {
    const [startM, startD] = z.start
    const [endM, endD] = z.end

    if (startM > endM) {
      // 염소자리 같이 연도를 걸치는 경우
      if ((month === startM && day >= startD) || (month === endM && day <= endD)) {
        return { sign: z.sign, signKo: z.signKo, element: z.element }
      }
    } else {
      if (
        (month === startM && day >= startD) ||
        (month === endM && day <= endD) ||
        (month > startM && month < endM)
      ) {
        return { sign: z.sign, signKo: z.signKo, element: z.element }
      }
    }
  }
  return null
}

// 감정 분석 함수
function analyzeQuestionMood(
  question: string
): 'worried' | 'curious' | 'hopeful' | 'urgent' | 'neutral' {
  const lowerQ = question.toLowerCase()
  const koreanQ = question

  // 걱정/불안 패턴
  if (
    /걱정|불안|두렵|힘들|무서|어떡|망하|실패|잃|끝|포기/i.test(koreanQ) ||
    /worried|anxious|scared|afraid|fail|lose|end/i.test(lowerQ)
  ) {
    return 'worried'
  }

  // 긴급 패턴
  if (
    /급해|빨리|당장|지금|바로|언제|오늘/i.test(koreanQ) ||
    /urgent|asap|now|immediately|today|when/i.test(lowerQ)
  ) {
    return 'urgent'
  }

  // 희망/긍정 패턴
  if (
    /잘될|좋아질|성공|행복|사랑|만날|이룰|희망/i.test(koreanQ) ||
    /hope|success|love|happy|better|achieve/i.test(lowerQ)
  ) {
    return 'hopeful'
  }

  // 호기심 패턴
  if (
    /어떨까|궁금|알고싶|보여줘|뭘까|왜/i.test(koreanQ) ||
    /what|how|why|curious|wonder/i.test(lowerQ)
  ) {
    return 'curious'
  }

  return 'neutral'
}

export async function POST(req: NextRequest) {
  try {
    // Apply middleware: rate limiting + public token auth + credit consumption
    const guardOptions = createPublicStreamGuard({
      route: 'tarot-interpret-stream',
      limit: 10,
      windowSeconds: 60,
      requireCredits: true,
      creditType: 'reading',
      creditAmount: 1,
    })

    const { context, error } = await initializeApiContext(req, guardOptions)
    if (error) {
      return error
    }

    logger.info('Tarot stream request', { ip: context.ip })

    const oversized = enforceBodySize(req, 256 * 1024)
    if (oversized) {
      return oversized
    }

    const rawBody = await req.json()

    // Validate with Zod
    const validationResult = tarotInterpretStreamSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[Tarot interpret-stream] validation failed', {
        errors: validationResult.error.issues,
      })
      return NextResponse.json(
        {
          error: 'validation_failed',
          details: validationResult.error.issues.map((e) => ({
            path: e.path.join('.'),
            message: e.message,
          })),
        },
        { status: HTTP_STATUS.BAD_REQUEST }
      )
    }

    const body = validationResult.data
    const categoryId = body.categoryId
    const spreadId = body.spreadId || ''
    const spreadTitle = body.spreadTitle || ''
    const language = body.language === 'en' ? 'en' : (context.locale as 'ko' | 'en')
    const rawCards = body.cards
    const userQuestion = body.userQuestion || ''
    const birthdate = body.birthdate || ''
    const previousReadings = body.previousReadings || []

    logger.info('Tarot stream payload', {
      categoryId,
      spreadId,
      language,
      cards: rawCards.length,
      hasQuestion: Boolean(userQuestion),
      hasBirthdate: Boolean(birthdate),
      previousReadings: previousReadings.length,
    })

    // Credits already consumed by middleware

    const isKorean = language === 'ko'
    const cardListText = rawCards
      .map((c, i) => {
        const name = isKorean && c.nameKo ? c.nameKo : c.name
        const pos = isKorean && c.positionKo ? c.positionKo : c.position
        const keywords = (isKorean && c.keywordsKo ? c.keywordsKo : c.keywords) || []
        return `${i + 1}. [${pos}] ${name}${c.isReversed ? '(역방향)' : ''} - ${keywords.slice(0, 3).join(', ')}`
      })
      .join('\n')

    const q = userQuestion || (isKorean ? '일반 운세' : 'general reading')

    // 개인화 정보 구성
    const zodiac = birthdate ? getZodiacSign(birthdate) : null
    const mood = analyzeQuestionMood(q)

    // 개인화 컨텍스트 생성
    let personalizationContext = ''
    if (isKorean) {
      if (zodiac) {
        personalizationContext += `\n## 질문자 정보\n- 별자리: ${zodiac.signKo} (${zodiac.element} 원소)\n`
      }
      if (previousReadings.length > 0) {
        personalizationContext += `\n## 이전 상담 요약 (맥락 참고용)\n${previousReadings.map((r, i) => `${i + 1}. ${r}`).join('\n')}\n`
      }
      const moodGuide: Record<typeof mood, string> = {
        worried: '질문자가 걱정하고 있어요. 안심시키면서도 현실적인 조언을 해주세요.',
        urgent: '질문자가 급한 상황이에요. 핵심을 먼저 말하고 구체적인 행동 지침을 주세요.',
        hopeful: '질문자가 희망적이에요. 긍정적인 에너지를 유지하면서 균형 잡힌 조언을 해주세요.',
        curious: '질문자가 호기심이 많아요. 흥미롭게 설명하면서 깊이 있는 통찰을 주세요.',
        neutral: '',
      }
      if (moodGuide[mood]) {
        personalizationContext += `\n## 말투 힌트\n${moodGuide[mood]}\n`
      }
    } else {
      if (zodiac) {
        personalizationContext += `\n## Querent Info\n- Zodiac: ${zodiac.sign} (${zodiac.element} element)\n`
      }
      if (previousReadings.length > 0) {
        personalizationContext += `\n## Previous Readings Summary (for context)\n${previousReadings.map((r, i) => `${i + 1}. ${r}`).join('\n')}\n`
      }
    }

    // 카드 수에 맞는 JSON 예시 생성
    const cardExamplesKo = rawCards
      .map((c, i) => {
        const pos = isKorean && c.positionKo ? c.positionKo : c.position
        return `    {"position": "${pos || `카드 ${i + 1}`}", "interpretation": "이 위치에서 이 카드가 의미하는 바를 구체적으로 설명 (300-500자)"}`
      })
      .join(',\n')
    const cardExamplesEn = rawCards
      .map((c, i) => {
        return `    {"position": "${c.position || `Card ${i + 1}`}", "interpretation": "What this card means in this position specifically (180-280 words)"}`
      })
      .join(',\n')

    // 시스템 프롬프트 분리 (OpenAI 자동 프롬프트 캐싱 활용 - 동일 프리픽스 재사용 시 비용 절감)
    const systemPrompt = isKorean
      ? `당신은 친근한 타로 리더예요. 따뜻하고 공감하는 말투로 해석해주세요.

## 중요: 반드시 모든 ${rawCards.length}개 카드에 대해 각각 해석을 작성하세요!
cards 배열에 정확히 ${rawCards.length}개의 항목이 있어야 합니다. 하나도 빠뜨리지 마세요.

## 출력 형식 (JSON)
{
  "overall": "전체 메시지 (400-600자). 친근한 말투로 카드들이 전하는 큰 그림을 설명해요.",
  "cards": [
${cardExamplesKo}
  ],
  "advice": "실용적인 조언 (100-150자). 구체적인 행동 지침."
}

## 말투 규칙
✅ 사용: "~해요", "~네요", "~거든요", "~죠"
❌ 금지: "~것입니다", "~하겠습니다" (딱딱한 말투)`
      : `You are a warm, intuitive tarot reader. Give interpretations in a friendly, conversational tone.

## IMPORTANT: You MUST provide interpretation for ALL ${rawCards.length} cards!
The "cards" array must contain exactly ${rawCards.length} entries. Do not skip any card.

## Output Format (JSON)
{
  "overall": "Overall message (250-350 words). Explain the big picture from these cards warmly.",
  "cards": [
${cardExamplesEn}
  ],
  "advice": "Practical advice (60-90 words). Specific action steps."
}`

    // 유저 프롬프트 (요청마다 달라지는 동적 부분)
    const userPrompt = isKorean
      ? `## 스프레드: ${spreadTitle}
## 질문: "${q}"
${personalizationContext}
## 뽑힌 카드
${cardListText}
${zodiac ? `\n질문자 별자리(${zodiac.signKo}, ${zodiac.element} 원소)를 자연스럽게 연결해서 해석해주세요.` : ''}${previousReadings.length > 0 ? '\n이전 상담 내용과의 연결점이 있다면 언급해주세요.' : ''}`
      : `## Spread: ${spreadTitle}
## Question: "${q}"
${personalizationContext}
## Cards Drawn
${cardListText}
${zodiac ? `\nNaturally incorporate ${zodiac.sign}'s ${zodiac.element} element traits.` : ''}${previousReadings.length > 0 ? '\nReference connections to previous readings if relevant.' : ''}`

    // OpenAI Streaming
    if (!process.env.OPENAI_API_KEY) {
      logger.warn('Tarot stream missing OPENAI_API_KEY, using fallback')
      const fallback = buildFallbackPayload(rawCards, language)
      return streamJsonPayload(fallback, { 'X-Fallback': '1' })
    }

    const openaiController = new AbortController()
    const openaiTimeoutId = setTimeout(() => openaiController.abort(), OPENAI_TIMEOUT_MS)
    const openaiStartTime = Date.now()
    let openaiResponse: Response
    try {
      logger.info('Tarot stream OpenAI request', {
        model: 'gpt-4o-mini',
        systemLen: systemPrompt.length,
        userLen: userPrompt.length,
      })
      openaiResponse = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${process.env.OPENAI_API_KEY}`,
        },
        body: JSON.stringify({
          model: 'gpt-4o-mini',
          messages: [
            { role: 'system', content: systemPrompt },
            { role: 'user', content: userPrompt },
          ],
          max_tokens: 8000,
          temperature: 0.75,
          stream: true,
          response_format: { type: 'json_object' },
        }),
        signal: openaiController.signal,
      })
    } catch (error) {
      clearTimeout(openaiTimeoutId)
      recordExternalCall('openai', 'gpt-4o-mini', 'error', Date.now() - openaiStartTime)
      logger.error('OpenAI stream fetch error:', { error })
      logger.warn('Tarot stream fallback to backend')
      const fallbackBase = buildFallbackPayload(rawCards, language)
      const backendFallback = await fetchBackendFallback({
        categoryId,
        spreadId,
        spreadTitle,
        cards: rawCards,
        userQuestion: userQuestion,
        language,
        birthdate,
      })
      const fallback = normalizeBackendPayload(backendFallback, fallbackBase) || fallbackBase
      return streamJsonPayload(fallback, { 'X-Fallback': '1' })
    }
    clearTimeout(openaiTimeoutId)

    if (!openaiResponse.ok) {
      recordExternalCall('openai', 'gpt-4o-mini', 'error', Date.now() - openaiStartTime)
      const errorText = await openaiResponse.text()
      logger.error('OpenAI stream error:', { status: openaiResponse.status, error: errorText })
      logger.warn('Tarot stream fallback to backend')
      const fallbackBase = buildFallbackPayload(rawCards, language)
      const backendFallback = await fetchBackendFallback({
        categoryId,
        spreadId,
        spreadTitle,
        cards: rawCards,
        userQuestion: userQuestion,
        language,
        birthdate,
      })
      const fallback = normalizeBackendPayload(backendFallback, fallbackBase) || fallbackBase
      return streamJsonPayload(fallback, { 'X-Fallback': '1' })
    }

    // SSE - 성공 메트릭스 기록 (스트리밍 시작 시점)
    recordExternalCall('openai', 'gpt-4o-mini', 'success', Date.now() - openaiStartTime)

    const encoder = new TextEncoder()
    const decoder = new TextDecoder()

    const stream = new ReadableStream({
      async start(controller) {
        const reader = openaiResponse.body?.getReader()
        logger.info('Tarot stream SSE start', { hasReader: Boolean(reader) })
        if (!reader) {
          const fallbackBase = buildFallbackPayload(rawCards, language)
          const backendFallback = await fetchBackendFallback({
            categoryId,
            spreadId,
            spreadTitle,
            cards: rawCards,
            userQuestion: userQuestion,
            language,
            birthdate,
          })
          const fallback = normalizeBackendPayload(backendFallback, fallbackBase) || fallbackBase
          controller.enqueue(encoder.encode(createSSEEvent({ content: JSON.stringify(fallback) })))
          controller.enqueue(encoder.encode(createSSEDoneEvent()))
          controller.close()
          return
        }

        let buffer = ''

        const handleLine = (line: string) => {
          if (!line.startsWith('data: ')) {
            return
          }
          const data = line.slice(6)
          if (data === '[DONE]') {
            controller.enqueue(encoder.encode(createSSEDoneEvent()))
            return
          }
          try {
            const parsed = JSON.parse(data)
            const content = parsed.choices?.[0]?.delta?.content
            if (content) {
              controller.enqueue(encoder.encode(createSSEEvent({ content })))
            }
          } catch (parseErr) {
            logger.warn('[Tarot stream] Invalid JSON chunk skipped', {
              data: data.substring(0, 100),
            })
          }
        }

        let sawContent = false
        try {
          while (true) {
            const { done, value } = await reader.read()
            if (done) {
              break
            }

            buffer += decoder.decode(value, { stream: true })
            const lines = buffer.split('\n')
            buffer = lines.pop() || ''

            for (const line of lines) {
              if (!sawContent && line.startsWith('data: ')) {
                sawContent = true
                logger.info('Tarot stream first chunk')
              }
              handleLine(line)
            }
          }

          if (buffer.trim()) {
            handleLine(buffer)
          }
        } catch (error) {
          logger.error('Stream error:', { error: error })
          try {
            controller.enqueue(encoder.encode(createSSEEvent({ error: 'Stream interrupted' })))
          } catch {
            /* stream may already be closed */
          }
        } finally {
          logger.info('Tarot stream SSE finished', { sawContent })
          try {
            controller.close()
          } catch {
            /* already closed */
          }
        }
      },
    })

    return new Response(stream, {
      headers: {
        'Content-Type': 'text/event-stream',
        'Cache-Control': 'no-cache',
        Connection: 'keep-alive',
      },
    })
  } catch (err) {
    logger.error('Tarot stream error:', { error: err })
    return NextResponse.json({ error: 'Server error' }, { status: HTTP_STATUS.SERVER_ERROR })
  }
}

// src/app/api/tarot/route.ts
import { NextRequest, NextResponse } from 'next/server'
import { withApiMiddleware, createPublicStreamGuard, type ApiContext } from '@/lib/api/middleware'
import { tarotThemes } from '@/lib/Tarot/tarot-spreads-data'
import { Card, DrawnCard } from '@/lib/Tarot/tarot.types'
import { tarotDeck } from '@/lib/Tarot/tarot-data'
import { checkCreditsOnly, creditErrorResponse } from '@/lib/credits/withCredits'

import { parseRequestBody } from '@/lib/api/requestParser'
import { LIMITS } from '@/lib/validation/patterns'
import { HTTP_STATUS } from '@/lib/constants/http'
import { recordApiRequest } from '@/lib/metrics/index'
import { tarotDrawSchema } from '@/lib/api/zodValidation'
import { logger } from '@/lib/logger'
const MAX_ID_LEN = LIMITS.ID
type TarotBody = {
  categoryId?: string
  spreadId?: string
}

function drawCards(count: number): DrawnCard[] {
  const deck = [...tarotDeck]
  // Fisher-Yates 셔플 - 완벽한 랜덤 분포
  for (let i = deck.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1))
    ;[deck[i], deck[j]] = [deck[j], deck[i]]
  }
  return deck.slice(0, count).map((card: Card) => ({
    card,
    isReversed: Math.random() < 0.5,
  }))
}

export const POST = withApiMiddleware(
  async (req: NextRequest, _context: ApiContext) => {
    const startTime = Date.now()
    try {
      const rawBody = await parseRequestBody<TarotBody>(req, { context: 'Tarot' })
      if (!rawBody || typeof rawBody !== 'object') {
        recordApiRequest('tarot', 'generate', 'validation_error')
        return NextResponse.json({ error: 'invalid_body' }, { status: HTTP_STATUS.BAD_REQUEST })
      }

      // Validate with Zod
      const validationResult = tarotDrawSchema.safeParse(rawBody)
      if (!validationResult.success) {
        logger.warn('[Tarot] validation failed', { errors: validationResult.error.issues })
        recordApiRequest('tarot', 'generate', 'validation_error')
        return NextResponse.json(
          {
            error: 'validation_failed',
            details: validationResult.error.issues.map((e) => ({
              path: e.path.join('.'),
              message: e.message,
            })),
          },
          { status: HTTP_STATUS.BAD_REQUEST }
        )
      }

      const { categoryId, spreadId } = validationResult.data

      const creditResult = await checkCreditsOnly('reading', 1)
      if (!creditResult.allowed) {
        recordApiRequest('tarot', 'generate', 'error')
        return creditErrorResponse(creditResult)
      }

      const theme = tarotThemes.find((t) => t.id === categoryId)
      if (!theme) {
        recordApiRequest('tarot', 'generate', 'error')
        return NextResponse.json({ error: 'Invalid category' }, { status: HTTP_STATUS.NOT_FOUND })
      }

      const spread = theme.spreads.find((s) => s.id === spreadId)
      if (!spread) {
        recordApiRequest('tarot', 'generate', 'error')
        return NextResponse.json({ error: 'Invalid spread' }, { status: HTTP_STATUS.NOT_FOUND })
      }

      const drawnCards = drawCards(spread.cardCount)

      recordApiRequest('tarot', 'generate', 'success', Date.now() - startTime)
      return NextResponse.json({
        category: theme.category,
        spread,
        drawnCards,
      })
    } catch (error) {
      recordApiRequest('tarot', 'generate', 'error', Date.now() - startTime)
      throw error
    }
  },
  createPublicStreamGuard({
    route: '/api/tarot',
    limit: 40,
    windowSeconds: 60,
  })
)

import { NextRequest, NextResponse } from 'next/server'
import {
  withApiMiddleware,
  createAuthenticatedGuard,
  type ApiContext,
  apiSuccess,
  apiError,
  ErrorCodes,
} from '@/lib/api/middleware'
import { prisma } from '@/lib/db/prisma'
import { logger } from '@/lib/logger'
import { tarotSaveRequestSchema, tarotQuerySchema } from '@/lib/api/zodValidation'

export const dynamic = 'force-dynamic'

interface SaveTarotRequest {
  question: string
  theme?: string
  spreadId: string
  spreadTitle: string
  cards: Array<{
    cardId: string
    name: string
    image: string
    isReversed: boolean
    position: string
  }>
  overallMessage?: string
  cardInsights?: Array<{
    position: string
    card_name: string
    is_reversed: boolean
    interpretation: string
  }>
  guidance?: string
  affirmation?: string
  source?: 'standalone' | 'counselor'
  counselorSessionId?: string
  locale?: string
}

export const POST = withApiMiddleware(
  async (req: NextRequest, context: ApiContext) => {
    const rawBody = await req.json()

    // Validate request body with Zod
    const validationResult = tarotSaveRequestSchema.safeParse(rawBody)
    if (!validationResult.success) {
      logger.warn('[TarotSave] validation failed', { errors: validationResult.error.issues })
      return apiError(ErrorCodes.VALIDATION_ERROR, 'validation_failed', {
        details: validationResult.error.issues.map((e) => ({
          path: e.path.join('.'),
          message: e.message,
        })),
      })
    }

    const body = validationResult.data
    const {
      question,
      theme,
      spreadId,
      spreadTitle,
      cards,
      overallMessage,
      cardInsights,
      guidance,
      affirmation,
      source = 'standalone',
      counselorSessionId,
      locale = 'ko',
    } = body

    const tarotReading = await prisma.tarotReading.create({
      data: {
        userId: context.userId!,
        question,
        theme,
        spreadId,
        spreadTitle,
        cards,
        overallMessage,
        cardInsights,
        guidance,
        affirmation,
        source,
        counselorSessionId,
        locale,
      },
    })

    return apiSuccess({
      success: true,
      readingId: tarotReading.id,
    })
  },
  createAuthenticatedGuard({
    route: '/api/tarot/save',
    limit: 60,
    windowSeconds: 60,
  })
)

export const GET = withApiMiddleware(
  async (req: NextRequest, context: ApiContext) => {
    const { searchParams } = new URL(req.url)

    // Validate query parameters with Zod
    const queryValidation = tarotQuerySchema.safeParse({
      limit: searchParams.get('limit') || '10',
      offset: searchParams.get('offset') || '0',
      theme: searchParams.get('theme'),
    })

    if (!queryValidation.success) {
      logger.warn('[TarotSave] Invalid query parameters', { errors: queryValidation.error.issues })
      return apiError(ErrorCodes.VALIDATION_ERROR, 'invalid_query_parameters')
    }

    const { limit = 10, offset = 0, theme } = queryValidation.data

    const where = {
      userId: context.userId!,
      ...(theme && { theme }),
    }

    const [readings, total] = await Promise.all([
      prisma.tarotReading.findMany({
        where,
        orderBy: { createdAt: 'desc' },
        take: limit,
        skip: offset,
        select: {
          id: true,
          createdAt: true,
          question: true,
          theme: true,
          spreadTitle: true,
          cards: true,
          overallMessage: true,
          source: true,
        },
      }),
      prisma.tarotReading.count({ where }),
    ])

    return apiSuccess({
      success: true,
      readings,
      total,
      hasMore: offset + readings.length < total,
    })
  },
  createAuthenticatedGuard({
    route: '/api/tarot/save',
    limit: 60,
    windowSeconds: 60,
  })
)

