import { useState, useRef, useEffect, useCallback } from 'react'
import type { Session } from 'next-auth'
import type { AppRouterInstance } from 'next/dist/shared/lib/app-router-context.shared-runtime'
import { logger } from '@/lib/logger'
import type { UserProfile, ViewMode, Filters } from './types'
import { convertToUserProfile } from './convertProfile'

export interface UseDiscoveryParams {
  session: Session | null
  status: 'loading' | 'authenticated' | 'unauthenticated'
  router: AppRouterInstance
  signInUrl: string
}

export interface UseDiscoveryReturn {
  // View state
  viewMode: ViewMode
  setViewMode: (mode: ViewMode) => void

  // Profile state
  profiles: UserProfile[]
  currentIndex: number
  likedProfiles: string[]
  selectedProfile: UserProfile | null
  setSelectedProfile: (profile: UserProfile | null) => void

  // Filter state
  showFilters: boolean
  setShowFilters: (show: boolean) => void
  filters: Filters
  setFilters: (filters: Filters) => void

  // Loading/error state
  isLoading: boolean
  error: string | null
  needsSetup: boolean
  hasMore: boolean

  // Drag state
  cardRef: React.RefObject<HTMLDivElement | null>
  dragOffset: { x: number; y: number }
  isDragging: boolean

  // Computed
  currentProfile: UserProfile | undefined
  hasMoreProfiles: boolean
  rotation: number
  opacity: number

  // Undo
  canUndo: boolean
  handleUndo: () => Promise<void>

  // Callbacks
  loadProfiles: () => Promise<void>
  handleDragStart: (clientX: number, clientY: number) => void
  handleDragMove: (clientX: number, clientY: number) => void
  handleDragEnd: () => void
  handleLike: () => Promise<void>
  handlePass: () => Promise<void>
  handleSuperLike: () => Promise<void>
}

export function useDiscovery({
  session,
  status,
  router,
  signInUrl,
}: UseDiscoveryParams): UseDiscoveryReturn {
  const [viewMode, setViewMode] = useState<ViewMode>('swipe')
  const [profiles, setProfiles] = useState<UserProfile[]>([])
  const [currentIndex, setCurrentIndex] = useState(0)
  const [likedProfiles, setLikedProfiles] = useState<string[]>([])
  const [_passedProfiles, setPassedProfiles] = useState<string[]>([])
  const [selectedProfile, setSelectedProfile] = useState<UserProfile | null>(null)
  const [showFilters, setShowFilters] = useState(false)
  const [filters, setFilters] = useState<Filters>({
    zodiacSign: 'all',
    sajuElement: 'all',
    minAge: 18,
    maxAge: 99,
    maxDistance: 50,
  })

  // 로딩/에러 상태
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [needsSetup, setNeedsSetup] = useState(false)
  const [hasMore, setHasMore] = useState(true)

  // Undo 상태
  const [lastSwipeId, setLastSwipeId] = useState<string | null>(null)
  const [lastSwipeTime, setLastSwipeTime] = useState<number>(0)
  const canUndo = !!lastSwipeId && Date.now() - lastSwipeTime < 5 * 60 * 1000

  const cardRef = useRef<HTMLDivElement>(null)
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 })
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 })
  const [isDragging, setIsDragging] = useState(false)

  const currentProfile = profiles[currentIndex]
  const hasMoreProfiles = currentIndex < profiles.length

  // 프로필 로딩 함수
  const loadProfiles = useCallback(async () => {
    if (!session?.user) {
      return
    }

    setIsLoading(true)
    setError(null)

    try {
      const params = new URLSearchParams()
      params.set('limit', '20')
      if (filters.zodiacSign !== 'all') {
        params.set('zodiac', filters.zodiacSign)
      }
      if (filters.sajuElement !== 'all') {
        params.set('element', filters.sajuElement)
      }

      const res = await fetch(`/api/destiny-match/discover?${params.toString()}`)
      const data = await res.json()

      if (!res.ok) {
        if (res.status === 400 && typeof data.error === 'string' && data.error.includes('프로필')) {
          setNeedsSetup(true)
          return
        }
        const errorMsg =
          typeof data.error === 'string' ? data.error : '프로필을 불러오는데 실패했습니다'
        throw new Error(errorMsg)
      }

      const convertedProfiles = (data.profiles || []).map(convertToUserProfile)
      setProfiles(convertedProfiles)
      setHasMore(data.hasMore)
      setCurrentIndex(0)
    } catch (err) {
      const message =
        err instanceof Error ? err.message : typeof err === 'string' ? err : '오류가 발생했습니다'
      setError(message)
    } finally {
      setIsLoading(false)
    }
  }, [session?.user, filters.zodiacSign, filters.sajuElement])

  // 세션 변경 시 프로필 로딩
  useEffect(() => {
    if (status === 'authenticated' && session?.user) {
      loadProfiles()
    }
  }, [status, session?.user, loadProfiles])

  // 스와이프 API 호출
  const handleSwipeApi = async (
    profileId: string,
    action: 'like' | 'pass' | 'super_like',
    compatibilityScore?: number
  ) => {
    try {
      const res = await fetch('/api/destiny-match/swipe', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          targetProfileId: profileId,
          action,
          compatibilityScore,
        }),
      })

      const data = await res.json()

      if (!res.ok) {
        logger.error('Swipe failed:', data.error)
        return null
      }

      // Undo를 위해 swipeId 저장 (매칭되지 않은 경우만)
      if (!data.isMatch && data.swipeId) {
        setLastSwipeId(data.swipeId)
        setLastSwipeTime(Date.now())
      } else {
        setLastSwipeId(null)
      }

      // 매치 성사 시 알림
      if (data.isMatch) {
        alert('💕 매치 성사! 상대방도 당신을 좋아합니다!')
      }

      return data
    } catch (err) {
      logger.error('Swipe error:', err)
      return null
    }
  }

  // Undo 처리
  const handleUndo = async () => {
    if (!lastSwipeId || !canUndo) return

    try {
      const res = await fetch('/api/destiny-match/swipe', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ swipeId: lastSwipeId }),
      })

      if (!res.ok) {
        const data = await res.json()
        logger.error('Undo failed:', data.error)
        return
      }

      // 이전 프로필로 되돌리기
      setCurrentIndex((prev) => Math.max(0, prev - 1))
      setLikedProfiles((prev) => prev.slice(0, -1))
      setPassedProfiles((prev) => prev.slice(0, -1))
      setLastSwipeId(null)
    } catch (err) {
      logger.error('Undo error:', err)
    }
  }

  // Swipe handlers
  const handleDragStart = (clientX: number, clientY: number) => {
    if (!session) {
      router.push(signInUrl)
      return
    }
    setIsDragging(true)
    setDragStart({ x: clientX, y: clientY })
  }

  const handleDragMove = (clientX: number, clientY: number) => {
    if (!isDragging) {
      return
    }
    const offsetX = clientX - dragStart.x
    const offsetY = clientY - dragStart.y
    setDragOffset({ x: offsetX, y: offsetY })
  }

  const handleDragEnd = () => {
    if (!isDragging) {
      return
    }
    setIsDragging(false)

    const threshold = 100
    if (Math.abs(dragOffset.x) > threshold) {
      if (dragOffset.x > 0) {
        handleLike()
      } else {
        handlePass()
      }
    }
    setDragOffset({ x: 0, y: 0 })
  }

  const handleLike = async () => {
    if (!session) {
      router.push(signInUrl)
      return
    }
    if (currentProfile) {
      // API 호출
      await handleSwipeApi(currentProfile.id, 'like', currentProfile.compatibility)
      setLikedProfiles((prev) => [...prev, currentProfile.id])
      setCurrentIndex((prev) => prev + 1)
    }
  }

  const handlePass = async () => {
    if (!session) {
      router.push(signInUrl)
      return
    }
    if (currentProfile) {
      // API 호출
      await handleSwipeApi(currentProfile.id, 'pass')
      setPassedProfiles((prev) => [...prev, currentProfile.id])
      setCurrentIndex((prev) => prev + 1)
    }
  }

  const handleSuperLike = async () => {
    if (!session) {
      router.push(signInUrl)
      return
    }
    if (currentProfile) {
      // API 호출
      await handleSwipeApi(currentProfile.id, 'super_like', currentProfile.compatibility)
      setLikedProfiles((prev) => [...prev, currentProfile.id])
      setCurrentIndex((prev) => prev + 1)
    }
  }

  const rotation = dragOffset.x * 0.1
  const opacity = 1 - Math.abs(dragOffset.x) / 300

  // Keyboard shortcuts
  useEffect(() => {
    if (viewMode !== 'swipe' || !currentProfile || selectedProfile) return

    const handleKeyDown = (e: KeyboardEvent) => {
      // Ignore if user is typing in an input/textarea
      if (e.target instanceof HTMLInputElement || e.target instanceof HTMLTextAreaElement) {
        return
      }

      switch (e.key) {
        case 'ArrowLeft':
          e.preventDefault()
          handlePass()
          break
        case 'ArrowRight':
          e.preventDefault()
          handleLike()
          break
        case 'ArrowUp':
          e.preventDefault()
          handleSuperLike()
          break
        case 'z':
        case 'Z':
          if (e.ctrlKey || e.metaKey) {
            e.preventDefault()
            if (canUndo) handleUndo()
          }
          break
        case 'i':
        case 'I':
          e.preventDefault()
          if (currentProfile) setSelectedProfile(currentProfile)
          break
      }
    }

    window.addEventListener('keydown', handleKeyDown)
    return () => window.removeEventListener('keydown', handleKeyDown)
  }, [
    viewMode,
    currentProfile,
    selectedProfile,
    canUndo,
    handlePass,
    handleLike,
    handleSuperLike,
    handleUndo,
  ])

  return {
    viewMode,
    setViewMode,
    profiles,
    currentIndex,
    likedProfiles,
    selectedProfile,
    setSelectedProfile,
    showFilters,
    setShowFilters,
    filters,
    setFilters,
    isLoading,
    error,
    needsSetup,
    hasMore,
    cardRef,
    dragOffset,
    isDragging,
    currentProfile,
    hasMoreProfiles,
    rotation,
    opacity,
    canUndo,
    handleUndo,
    loadProfiles,
    handleDragStart,
    handleDragMove,
    handleDragEnd,
    handleLike,
    handlePass,
    handleSuperLike,
  }
}

'use client'

import React, { Suspense, useEffect, useCallback, useRef, useState } from 'react'
import { useParams, useRouter, useSearchParams } from 'next/navigation'
import { useSession } from 'next-auth/react'
import { useI18n } from '@/i18n/I18nProvider'
import AuthGate from '@/components/auth/AuthGate'
import { buildSignInUrl } from '@/lib/auth/signInUrl'
import { useTarotGame, useTarotInterpretation } from './hooks'
import { smoothScrollTo } from './utils'
import { PageContent } from './components/PageContent'
import LoginPrompt from './components/LoginPrompt'
import styles from './tarot-reading.module.css'

export default function TarotReadingPageWrapper() {
  return (
    <Suspense
      fallback={
        <div className={styles.loading}>
          <div className={styles.loadingOrb}></div>
          <p>✨ Loading...</p>
        </div>
      }
    >
      <TarotReadingPage />
    </Suspense>
  )
}

function TarotReadingPage() {
  const router = useRouter()
  const params = useParams()
  const searchParams = useSearchParams()
  const { status } = useSession()
  const { translate, language } = useI18n()

  // Setup URLs
  const categoryName = params?.categoryName as string | undefined
  const spreadId = params?.spreadId as string | undefined
  const search = searchParams?.toString()
  const basePath = categoryName && spreadId ? `/tarot/${categoryName}/${spreadId}` : '/tarot'
  const callbackUrl = search ? `${basePath}?${search}` : basePath
  const signInUrl = buildSignInUrl(callbackUrl)

  // Local state
  const detailedSectionRef = useRef<HTMLDivElement | null>(null)
  const [expandedCard, setExpandedCard] = useState<number | null>(null)
  const [isSaving, setIsSaving] = useState(false)

  // Custom hooks
  const gameHook = useTarotGame()
  const interpretationHook = useTarotInterpretation({
    categoryName,
    spreadId,
    userTopic: gameHook.userTopic,
    selectedDeckStyle: gameHook.selectedDeckStyle,
  })

  // Fetch interpretation when reading result is available
  useEffect(() => {
    if (gameHook.readingResult && gameHook.interpretation?.fallback) {
      interpretationHook.fetchInterpretation(gameHook.readingResult).then((result) => {
        if (result) {
          gameHook.setInterpretation(result)
        }
      })
    }
  }, [
    gameHook.readingResult,
    gameHook.interpretation?.fallback,
    interpretationHook.fetchInterpretation,
    gameHook.setInterpretation,
  ])

  // Card reveal with auto-scroll
  const handleCardReveal = useCallback(
    (index: number) => {
      gameHook.handleCardReveal(index)
      // Auto-scroll after last card
      if (
        index === (gameHook.readingResult?.drawnCards.length || 0) - 1 &&
        detailedSectionRef.current
      ) {
        setTimeout(() => {
          smoothScrollTo(detailedSectionRef.current!, 1200)
        }, 800)
      }
    },
    [gameHook.handleCardReveal, gameHook.readingResult?.drawnCards.length]
  )

  const scrollToDetails = useCallback(() => {
    if (detailedSectionRef.current) {
      smoothScrollTo(detailedSectionRef.current, 800)
    }
  }, [])

  const handleSaveReading = useCallback(async () => {
    if (isSaving || interpretationHook.isSaved) return
    setIsSaving(true)
    try {
      await interpretationHook.handleSaveReading(
        gameHook.readingResult,
        gameHook.spreadInfo,
        gameHook.interpretation
      )
    } finally {
      setIsSaving(false)
    }
  }, [
    interpretationHook.handleSaveReading,
    gameHook.readingResult,
    gameHook.spreadInfo,
    gameHook.interpretation,
    isSaving,
    interpretationHook.isSaved,
  ])

  const handleReset = () => router.push('/tarot')
  const toggleCardExpand = (index: number) => setExpandedCard(expandedCard === index ? null : index)

  // Session loading state
  if (status === 'loading') {
    return (
      <div className={styles.loading}>
        <div className={styles.loadingOrb}></div>
        <p>✨ {translate('common.loading', 'Loading...')}</p>
      </div>
    )
  }

  // Login fallback
  const loginFallback = <LoginPrompt signInUrl={signInUrl} language={language} />

  return (
    <AuthGate statusOverride={status} callbackUrl={callbackUrl} fallback={loginFallback}>
      <PageContent
        {...gameHook}
        {...interpretationHook}
        detailedSectionRef={detailedSectionRef}
        expandedCard={expandedCard}
        isSaving={isSaving}
        handleCardReveal={handleCardReveal}
        scrollToDetails={scrollToDetails}
        handleSaveReading={handleSaveReading}
        handleReset={handleReset}
        toggleCardExpand={toggleCardExpand}
        language={language}
        translate={translate}
      />
    </AuthGate>
  )
}

