"use client";

// src/components/calendar/CalendarMainView.tsx
import React, { memo, useCallback, useMemo } from 'react';
import { useI18n } from '@/i18n/I18nProvider';
import styles from './DestinyCalendar.module.css';
import { CATEGORY_EMOJI, WEEKDAYS_KO, WEEKDAYS_EN } from './constants';
import { getGradeEmoji, getCategoryLabel, getScoreClass } from './utils';
import SelectedDatePanel from './SelectedDatePanel';
import MonthHighlights from './MonthHighlights';
import type { CalendarData, ImportantDate, EventCategory, BirthInfo } from './types';

interface CalendarMainViewProps {
  data: CalendarData;
  birthInfo: BirthInfo;
  currentDate: Date;
  selectedDay: Date | null;
  selectedDate: ImportantDate | null;
  activeCategory: EventCategory | "all";
  isDarkTheme: boolean;
  slideDirection: 'left' | 'right' | null;
  savedDates: Set<string>;
  saving: boolean;
  saveMsg: string | null;
  onCategoryChange: (category: EventCategory | "all") => void;
  onDayClick: (date: Date | null) => void;
  onPrevMonth: () => void;
  onNextMonth: () => void;
  onGoToToday: () => void;
  onToggleTheme: () => void;
  onEditBirthInfo: () => void;
  onSaveDate: () => void;
  onUnsaveDate: () => void;
}

const MONTHS_KO = ["1월", "2월", "3월", "4월", "5월", "6월", "7월", "8월", "9월", "10월", "11월", "12월"];
const MONTHS_EN = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
const CATEGORIES: EventCategory[] = ["wealth", "career", "love", "health", "travel", "study"];

const CalendarMainView = memo(function CalendarMainView({
  data,
  currentDate,
  selectedDay,
  selectedDate,
  activeCategory,
  isDarkTheme,
  slideDirection,
  savedDates,
  saving,
  saveMsg,
  onCategoryChange,
  onDayClick,
  onPrevMonth,
  onNextMonth,
  onGoToToday,
  onEditBirthInfo,
  onSaveDate,
  onUnsaveDate,
}: CalendarMainViewProps) {
  const { locale } = useI18n();

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();
  const today = useMemo(() => new Date(), []);

  const WEEKDAYS = locale === "ko" ? WEEKDAYS_KO : WEEKDAYS_EN;
  const MONTHS = locale === "ko" ? MONTHS_KO : MONTHS_EN;

  // Year summary calculation
  const yearSummary = useMemo(() => {
    if (!data?.allDates) {return null;}

    const result = { total: 0, grade0: 0, grade1: 0, grade2: 0, grade3: 0, grade4: 0, grade5: 0 };
    for (const d of data.allDates) {
      const dateYear = new Date(d.date).getFullYear();
      if (dateYear === year) {
        result.total++;
        if (d.grade === 0) {result.grade0++;}
        else if (d.grade === 1) {result.grade1++;}
        else if (d.grade === 2) {result.grade2++;}
        else if (d.grade === 3) {result.grade3++;}
        else if (d.grade === 4) {result.grade4++;}
        else if (d.grade === 5) {result.grade5++;}
      }
    }
    return result.total > 0 ? result : null;
  }, [data?.allDates, year]);

  // Monthly fortune data for graph
  const fortuneData = useMemo(() => {
    if (!data?.allDates) {return [];}
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const monthData: { day: number; grade: number; score: number }[] = [];

    for (let day = 1; day <= daysInMonth; day++) {
      const dateStr = `${year}-${String(month + 1).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
      const dateInfo = data.allDates.find(d => d.date === dateStr);
      monthData.push({
        day,
        grade: dateInfo?.grade ?? 3,
        score: dateInfo?.score ?? 50
      });
    }
    return monthData;
  }, [data?.allDates, year, month]);

  // Generate month days array
  const getMonthDays = useCallback(() => {
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const startDayOfWeek = firstDay.getDay();
    const daysInMonth = lastDay.getDate();

    const days: (Date | null)[] = [];

    // Empty cells for previous month
    for (let i = 0; i < startDayOfWeek; i++) {
      days.push(null);
    }

    // Current month days
    for (let i = 1; i <= daysInMonth; i++) {
      days.push(new Date(year, month, i));
    }

    return days;
  }, [year, month]);

  const getDateInfo = useCallback((date: Date): ImportantDate | undefined => {
    if (!data?.allDates) {return undefined;}
    const y = date.getFullYear();
    const m = String(date.getMonth() + 1).padStart(2, '0');
    const d = String(date.getDate()).padStart(2, '0');
    const dateStr = `${y}-${m}-${d}`;
    return data.allDates.find(item => item.date === dateStr);
  }, [data?.allDates]);

  const getDayClassName = useCallback((date: Date | null): string => {
    if (!date) {return styles.emptyDay;}

    const isToday =
      date.getDate() === today.getDate() &&
      date.getMonth() === today.getMonth() &&
      date.getFullYear() === today.getFullYear();

    const isSelected = selectedDay &&
      date.getDate() === selectedDay.getDate() &&
      date.getMonth() === selectedDay.getMonth() &&
      date.getFullYear() === selectedDay.getFullYear();

    const dateInfo = getDateInfo(date);

    let className = styles.day;
    if (isToday) {className += ` ${styles.today}`;}
    if (isSelected) {className += ` ${styles.selected}`;}
    if (dateInfo) {
      className += ` ${styles[`dayGrade${dateInfo.grade}`]}`;
    }
    if (date.getDay() === 0) {className += ` ${styles.sunday}`;}
    if (date.getDay() === 6) {className += ` ${styles.saturday}`;}

    return className;
  }, [today, selectedDay, getDateInfo]);

  const handleDayClick = useCallback((date: Date | null) => {
    if (!date) {return;}
    onDayClick(date);
  }, [onDayClick]);

  const handleDateSelect = useCallback((date: Date, _info: ImportantDate) => {
    onDayClick(date);
  }, [onDayClick]);

  const days = getMonthDays();

  const getGradeLabel = (grade: number) => {
    const labels = {
      0: locale === "ko" ? "최고의 날" : "Best Day",
      1: locale === "ko" ? "아주 좋은 날" : "Very Good Day",
      2: locale === "ko" ? "좋은 날" : "Good Day",
      3: locale === "ko" ? "보통 날" : "Normal Day",
      4: locale === "ko" ? "안좋은 날" : "Bad Day",
      5: locale === "ko" ? "최악의 날" : "Worst Day",
    };
    return labels[grade as keyof typeof labels] || labels[3];
  };

  return (
    <div className={`${styles.container} ${!isDarkTheme ? styles.lightTheme : ''}`}>
      {/* Header */}
      <div className={styles.calendarHeader}>
        <div className={styles.headerTop}>
          <div className={styles.headerTitleSection}>
            <div className={styles.calendarIconWrapper}>
              <span className={styles.calendarIcon}>📅</span>
            </div>
            <div className={styles.titleGroup}>
              <h1 className={styles.calendarTitle}>
                {locale === "ko" ? "운명 캘린더" : "Destiny Calendar"}
              </h1>
              <p className={styles.calendarSubtitle}>
                {locale === "ko" ? `${year}년 당신만의 특별한 날들` : `Your special days in ${year}`}
              </p>
            </div>
          </div>
          <div className={styles.headerActions}>
            <button className={styles.editBirthBtn} onClick={onEditBirthInfo}>
              <span>✏️</span>
              <span>{locale === "ko" ? "수정" : "Edit"}</span>
            </button>
          </div>
        </div>

        {/* Year Summary Badges */}
        {yearSummary && (
          <div className={styles.summaryBadges}>
            <span className={styles.summaryBadge} title={locale === "ko" ? "최고의 날 (0등급)" : "Best Days (grade 0)"}>
              <span className={styles.badgeEmoji}>🌟</span>
              <span className={styles.badgeLabel}>{locale === "ko" ? "최고" : "Best"}</span>
              <span className={styles.badgeCount}>
                {locale === "ko" ? `${yearSummary.grade0}일` : `${yearSummary.grade0} days`}
              </span>
            </span>
            <span className={styles.summaryBadge} title={locale === "ko" ? "좋은 날 (1~2등급)" : "Good Days (grade 1-2)"}>
              <span className={styles.badgeEmoji}>✨</span>
              <span className={styles.badgeLabel}>{locale === "ko" ? "좋음" : "Good"}</span>
              <span className={styles.badgeCount}>
                {locale === "ko"
                  ? `${yearSummary.grade1 + yearSummary.grade2}일`
                  : `${yearSummary.grade1 + yearSummary.grade2} days`}
              </span>
            </span>
            <span className={styles.summaryBadge} title={locale === "ko" ? "보통 날 (3등급)" : "Normal Days (grade 3)"}>
              <span className={styles.badgeEmoji}>◆</span>
              <span className={styles.badgeLabel}>{locale === "ko" ? "보통" : "Normal"}</span>
              <span className={styles.badgeCount}>
                {locale === "ko" ? `${yearSummary.grade3}일` : `${yearSummary.grade3} days`}
              </span>
            </span>
            <span className={`${styles.summaryBadge} ${styles.cautionBadge}`} title={locale === "ko" ? "안좋은 날 (4등급)" : "Bad Days (grade 4)"}>
              <span className={styles.badgeEmoji}>⚠️</span>
              <span className={styles.badgeLabel}>{locale === "ko" ? "안좋음" : "Bad"}</span>
              <span className={styles.badgeCount}>
                {locale === "ko" ? `${yearSummary.grade4}일` : `${yearSummary.grade4} days`}
              </span>
            </span>
            <span className={`${styles.summaryBadge} ${styles.worstBadge}`} title={locale === "ko" ? "최악의 날 (5등급)" : "Worst Days (grade 5)"}>
              <span className={styles.badgeEmoji}>☠️</span>
              <span className={styles.badgeLabel}>{locale === "ko" ? "최악" : "Worst"}</span>
              <span className={styles.badgeCount}>
                {locale === "ko" ? `${yearSummary.grade5}일` : `${yearSummary.grade5} days`}
              </span>
            </span>
          </div>
        )}
      </div>

      {/* Month Navigation */}
      <div className={styles.monthNav}>
        <button className={styles.navBtn} onClick={onPrevMonth} aria-label={locale === "ko" ? "이전 달" : "Previous month"}>◀</button>
        <div className={styles.monthDisplay}>
          <span className={styles.monthYear}>{year}</span>
          <span className={styles.monthName}>{MONTHS[month]}</span>
        </div>
        <button className={styles.navBtn} onClick={onNextMonth} aria-label={locale === "ko" ? "다음 달" : "Next month"}>▶</button>
        <button className={styles.todayBtn} onClick={onGoToToday}>
          {locale === "ko" ? "오늘" : "Today"}
        </button>
      </div>

      {/* Category Filter */}
      <div className={styles.filters}>
        <button
          className={`${styles.filterBtn} ${activeCategory === "all" ? styles.active : ""}`}
          onClick={() => onCategoryChange("all")}
        >
          {locale === "ko" ? "전체" : "All"}
        </button>
        {CATEGORIES.map(cat => (
          <button
            key={cat}
            className={`${styles.filterBtn} ${activeCategory === cat ? styles.active : ""}`}
            onClick={() => onCategoryChange(cat)}
          >
            {CATEGORY_EMOJI[cat]} {getCategoryLabel(cat, locale)}
          </button>
        ))}
      </div>

      {/* Calendar Grid */}
      <div className={styles.calendarWrapper}>
        {/* Weekday Header */}
        <div className={styles.weekdaysHeader}>
          {WEEKDAYS.map((day, idx) => (
            <div
              key={day}
              className={`${styles.weekdayCell} ${idx === 0 ? styles.sunday : ""} ${idx === 6 ? styles.saturday : ""}`}
            >
              {day}
            </div>
          ))}
        </div>

        {/* Days Grid */}
        <div
          className={`${styles.daysGrid} ${slideDirection === 'left' ? styles.slideLeft : ''} ${slideDirection === 'right' ? styles.slideRight : ''}`}
          role="grid"
          aria-label={locale === "ko" ? `${year}년 ${month + 1}월 캘린더` : `Calendar for ${MONTHS[month]} ${year}`}
        >
          {days.map((date, idx) => {
            const dateInfo = date ? getDateInfo(date) : undefined;
            const isToday = date &&
              date.getDate() === today.getDate() &&
              date.getMonth() === today.getMonth() &&
              date.getFullYear() === today.getFullYear();

            return (
              <div
                key={idx}
                className={getDayClassName(date)}
                onClick={() => handleDayClick(date)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' || e.key === ' ') {
                    e.preventDefault();
                    handleDayClick(date);
                  }
                }}
                role="gridcell"
                tabIndex={date ? 0 : -1}
                aria-label={date ? `${date.getDate()}${locale === "ko" ? "일" : ""}, ${dateInfo ? getGradeLabel(dateInfo.grade) : locale === "ko" ? "정보 없음" : "No info"}${isToday ? (locale === "ko" ? ", 오늘" : ", Today") : ""}` : undefined}
                aria-selected={!!(selectedDay && date &&
                  date.getDate() === selectedDay.getDate() &&
                  date.getMonth() === selectedDay.getMonth() &&
                  date.getFullYear() === selectedDay.getFullYear())}
                aria-current={isToday ? "date" : undefined}
              >
                {date && (
                  <>
                    <span className={styles.dayNumber}>{date.getDate()}</span>
                    {dateInfo && (
                      <div className={styles.dayIndicators} aria-hidden="true">
                        {dateInfo.categories.slice(0, 2).map((cat, i) => (
                          <span key={i} className={styles.dayEmoji}>{CATEGORY_EMOJI[cat]}</span>
                        ))}
                        <span className={styles.gradeIndicator}>
                          {getGradeEmoji(dateInfo.grade)}
                        </span>
                      </div>
                    )}
                  </>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {/* Legend */}
      <div className={styles.legend} role="list" aria-label={locale === "ko" ? "등급 범례" : "Grade Legend"}>
        <div className={styles.legendItem} role="listitem">
          <span className={`${styles.legendDot} ${styles.grade0Dot}`} aria-hidden="true">
            <span className={styles.legendPattern}>★</span>
          </span>
          <span>{locale === "ko" ? "최고 (72+)" : "Best (72+)"}</span>
        </div>
        <div className={styles.legendItem} role="listitem">
          <span className={`${styles.legendDot} ${styles.grade1Dot}`} aria-hidden="true">
            <span className={styles.legendPattern}>●</span>
          </span>
          <span>{locale === "ko" ? "좋음 (65-71)" : "Good (65-71)"}</span>
        </div>
        <div className={styles.legendItem} role="listitem">
          <span className={`${styles.legendDot} ${styles.grade2Dot}`} aria-hidden="true">
            <span className={styles.legendPattern}>◆</span>
          </span>
          <span>{locale === "ko" ? "보통 (45-64)" : "Normal (45-64)"}</span>
        </div>
        <div className={styles.legendItem} role="listitem">
          <span className={`${styles.legendDot} ${styles.grade3Dot}`} aria-hidden="true">
            <span className={styles.legendPattern}>▲</span>
          </span>
          <span>{locale === "ko" ? "안좋음 (30-44)" : "Bad (30-44)"}</span>
        </div>
        <div className={styles.legendItem} role="listitem">
          <span className={`${styles.legendDot} ${styles.grade4Dot}`} aria-hidden="true">
            <span className={styles.legendPattern}>✕</span>
          </span>
          <span>{locale === "ko" ? "최악 (<30)" : "Worst (<30)"}</span>
        </div>
      </div>

      {/* Monthly Fortune Graph */}
      {fortuneData.length > 0 && (
        <div className={styles.fortuneGraph}>
          <div className={styles.graphHeader}>
            <span className={styles.graphTitle}>
              📊 {locale === "ko" ? "월간 운세 흐름" : "Monthly Fortune Flow"}
            </span>
          </div>
          <div className={styles.sparkline}>
            {fortuneData.map((d, idx) => {
              const isSelected = selectedDay && selectedDay.getDate() === d.day && selectedDay.getMonth() === month;
              const height = Math.max(10, (100 - d.grade * 20)) + '%';
              return (
                <div
                  key={idx}
                  className={`${styles.sparkBar} ${styles[`grade${d.grade}`]} ${isSelected ? styles.active : ''}`}
                  style={{ height }}
                  onClick={() => {
                    const clickedDate = new Date(year, month, d.day);
                    handleDayClick(clickedDate);
                  }}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault();
                      handleDayClick(new Date(year, month, d.day));
                    }
                  }}
                  role="button"
                  tabIndex={0}
                  aria-label={`${d.day}${locale === "ko" ? "일" : ""}: ${locale === "ko" ? "점수" : "score"} ${d.score}`}
                  title={`${d.day}${locale === "ko" ? "일" : ""}: ${d.score}${locale === "ko" ? "점" : ""}`}
                />
              );
            })}
          </div>
        </div>
      )}

      {/* Selected Date Panel */}
      <SelectedDatePanel
        selectedDay={selectedDay}
        selectedDate={selectedDate}
        savedDates={savedDates}
        saving={saving}
        saveMsg={saveMsg}
        onSave={onSaveDate}
        onUnsave={onUnsaveDate}
        getGradeEmoji={getGradeEmoji}
        getScoreClass={getScoreClass}
      />

      {/* Month Highlights */}
      {data?.allDates && data.allDates.length > 0 && (
        <MonthHighlights
          allDates={data.allDates}
          year={year}
          month={month}
          onDateSelect={handleDateSelect}
        />
      )}
    </div>
  );
});

export default CalendarMainView;

"use client";

// src/components/calendar/SelectedDatePanel.tsx
import React, { useCallback, memo } from 'react';
import { useSession } from 'next-auth/react';
import { useI18n } from '@/i18n/I18nProvider';
import styles from './DestinyCalendar.module.css';

type EventCategory = "wealth" | "career" | "love" | "health" | "travel" | "study" | "general";
type ImportanceGrade = 0 | 1 | 2 | 3 | 4;

interface ImportantDate {
  date: string;
  grade: ImportanceGrade;
  score: number;
  categories: EventCategory[];
  title: string;
  description: string;
  summary?: string;
  bestTimes?: string[];
  sajuFactors: string[];
  astroFactors: string[];
  recommendations: string[];
  warnings: string[];
  ganzhi?: string;
  transitSunSign?: string;
  crossVerified?: boolean;
}

interface SelectedDatePanelProps {
  selectedDay: Date | null;
  selectedDate: ImportantDate | null;
  savedDates: Set<string>;
  saving: boolean;
  saveMsg: string | null;
  onSave: () => void;
  onUnsave: () => void;
  getGradeEmoji: (grade: number) => string;
  getScoreClass: (score: number) => string;
}

const CATEGORY_EMOJI: Record<EventCategory, string> = {
  wealth: "💰",
  career: "💼",
  love: "💕",
  health: "💪",
  travel: "✈️",
  study: "📚",
  general: "⭐",
};

// 등급별 스타일 클래스
const GRADE_STYLES: Record<number, { bg: string; border: string; text: string }> = {
  0: { bg: 'bg-gradient-to-r from-yellow-100 to-amber-100', border: 'border-yellow-400', text: 'text-yellow-800' },
  1: { bg: 'bg-gradient-to-r from-green-50 to-emerald-50', border: 'border-green-400', text: 'text-green-800' },
  2: { bg: 'bg-gray-50', border: 'border-gray-300', text: 'text-gray-700' },
  3: { bg: 'bg-gradient-to-r from-orange-50 to-amber-50', border: 'border-orange-400', text: 'text-orange-800' },
  4: { bg: 'bg-gradient-to-r from-red-50 to-rose-50', border: 'border-red-400', text: 'text-red-800' },
};

const WEEKDAYS_KO = ["일", "월", "화", "수", "목", "금", "토"];
const WEEKDAYS_EN = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

const SelectedDatePanel = memo(function SelectedDatePanel({
  selectedDay,
  selectedDate,
  savedDates,
  saving,
  saveMsg,
  onSave,
  onUnsave,
  getGradeEmoji,
  getScoreClass,
}: SelectedDatePanelProps) {
  const { locale } = useI18n();
  const { status } = useSession();
  const WEEKDAYS = locale === "ko" ? WEEKDAYS_KO : WEEKDAYS_EN;

  if (!selectedDay) {return null;}

  const getCategoryLabel = (cat: EventCategory) => {
    const labels: Record<EventCategory, { ko: string; en: string }> = {
      wealth: { ko: "재물운", en: "Wealth" },
      career: { ko: "커리어", en: "Career" },
      love: { ko: "연애운", en: "Love" },
      health: { ko: "건강운", en: "Health" },
      travel: { ko: "여행운", en: "Travel" },
      study: { ko: "학업운", en: "Study" },
      general: { ko: "전체운", en: "General" },
    };
    return locale === "ko" ? labels[cat].ko : labels[cat].en;
  };

  const isSaved = selectedDate ? savedDates.has(selectedDate.date) : false;

  const handleAddToCalendar = useCallback(() => {
    if (!selectedDate || !selectedDay) return;

    const dateStr = selectedDate.date.replace(/-/g, '');
    // All-day event DTEND must be the NEXT day (exclusive end per RFC 5545)
    const nextDay = new Date(selectedDay);
    nextDay.setDate(nextDay.getDate() + 1);
    const endStr = `${nextDay.getFullYear()}${String(nextDay.getMonth() + 1).padStart(2, '0')}${String(nextDay.getDate()).padStart(2, '0')}`;

    const title = selectedDate.title;
    const catLabels: Record<EventCategory, string> = {
      wealth: locale === 'ko' ? '재물운' : 'Wealth',
      career: locale === 'ko' ? '커리어' : 'Career',
      love: locale === 'ko' ? '연애운' : 'Love',
      health: locale === 'ko' ? '건강운' : 'Health',
      travel: locale === 'ko' ? '여행운' : 'Travel',
      study: locale === 'ko' ? '학업운' : 'Study',
      general: locale === 'ko' ? '전체운' : 'General',
    };
    const categories = selectedDate.categories
      .map(cat => catLabels[cat])
      .join(', ');
    const descParts = [
      selectedDate.description,
      categories ? `${locale === 'ko' ? '카테고리' : 'Categories'}: ${categories}` : '',
      `${locale === 'ko' ? '점수' : 'Score'}: ${selectedDate.score}/100`,
    ];
    if (selectedDate.recommendations.length > 0) {
      descParts.push(`${locale === 'ko' ? '추천' : 'Recommendations'}:`);
      selectedDate.recommendations.forEach(r => descParts.push(`- ${r}`));
    }
    if (selectedDate.warnings.length > 0) {
      descParts.push(`${locale === 'ko' ? '주의' : 'Warnings'}:`);
      selectedDate.warnings.forEach(w => descParts.push(`- ${w}`));
    }
    const description = descParts.filter(Boolean).join('\n');

    const now = new Date();
    const pad = (n: number) => String(n).padStart(2, '0');
    const stamp = `${now.getFullYear()}${pad(now.getMonth() + 1)}${pad(now.getDate())}T${pad(now.getHours())}${pad(now.getMinutes())}${pad(now.getSeconds())}`;

    const escapeICS = (text: string) =>
      text.replace(/\\/g, '\\\\').replace(/;/g, '\\;').replace(/,/g, '\\,').replace(/\n/g, '\\n');

    const icsContent = [
      'BEGIN:VCALENDAR',
      'VERSION:2.0',
      'PRODID:-//SajuAstroChat//DestinyCalendar//EN',
      'CALSCALE:GREGORIAN',
      'METHOD:PUBLISH',
      'BEGIN:VEVENT',
      `DTSTART;VALUE=DATE:${dateStr}`,
      `DTEND;VALUE=DATE:${endStr}`,
      `DTSTAMP:${stamp}`,
      `UID:destiny-${dateStr}@sajuastrochat`,
      `SUMMARY:${escapeICS(title)}`,
      `DESCRIPTION:${escapeICS(description)}`,
      'END:VEVENT',
      'END:VCALENDAR',
    ].join('\r\n');

    // iOS Safari doesn't support Blob URL + <a download>.
    // Use data URI which works across iOS Safari, Android Chrome, and desktop.
    const dataUri = 'data:text/calendar;charset=utf-8,' + encodeURIComponent(icsContent);
    window.open(dataUri, '_blank');
  }, [selectedDate, selectedDay, locale]);

  return (
    <div className={styles.selectedDayInfo}>
      <div className={styles.selectedDayHeader}>
        <span className={styles.selectedDayDate}>
          {selectedDay.getMonth() + 1}/{selectedDay.getDate()}
          {locale === "ko" && ` (${WEEKDAYS[selectedDay.getDay()]})`}
        </span>
        <div className={styles.headerActions}>
          {selectedDate && (
            <span className={styles.selectedGrade}>{getGradeEmoji(selectedDate.grade)}</span>
          )}
          {/* Save button - authenticated users only */}
          {status === 'authenticated' && selectedDate && (
            <button
              className={`${styles.saveBtn} ${isSaved ? styles.saved : ''}`}
              onClick={isSaved ? onUnsave : onSave}
              disabled={saving}
              aria-label={isSaved
                ? (locale === 'ko' ? '저장됨 (클릭하여 삭제)' : 'Saved (click to remove)')
                : (locale === 'ko' ? '이 날짜 저장하기' : 'Save this date')}
              title={isSaved
                ? (locale === 'ko' ? '저장됨 (클릭하여 삭제)' : 'Saved (click to remove)')
                : (locale === 'ko' ? '이 날짜 저장하기' : 'Save this date')}
            >
              {saving ? '...' : isSaved ? '★' : '☆'}
            </button>
          )}
        </div>
      </div>

      {/* Save message */}
      {saveMsg && (
        <div className={styles.saveMsg}>{saveMsg}</div>
      )}

      {selectedDate ? (
        <div className={styles.selectedDayContent}>
          <h3 className={styles.selectedTitle}>{selectedDate.title}</h3>

          {/* Grade 3, 4 (나쁜 날): 경고를 상단에 강조 표시 */}
          {selectedDate.grade >= 3 && selectedDate.warnings.length > 0 && (
            <div className={`${styles.urgentWarningBox} ${selectedDate.grade === 4 ? styles.worstDay : ''}`}>
              <div className={styles.urgentWarningHeader}>
                <span className={styles.urgentWarningIcon}>
                  {selectedDate.grade === 4 ? '🚨' : '⚠️'}
                </span>
                <span className={styles.urgentWarningTitle}>
                  {locale === "ko"
                    ? (selectedDate.grade === 4 ? "오늘 주의해야 할 점!" : "오늘의 주의사항")
                    : (selectedDate.grade === 4 ? "Critical Warnings!" : "Today's Cautions")}
                </span>
              </div>
              <ul className={styles.urgentWarningList}>
                {selectedDate.warnings.slice(0, 3).map((w, i) => (
                  <li key={i} className={styles.urgentWarningItem}>
                    <span className={styles.urgentWarningDot}>•</span>
                    {w}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Cross-verified badge - 좋은 날에만 표시 */}
          {selectedDate.crossVerified && selectedDate.grade <= 1 && (
            <div className={styles.crossVerifiedBadge}>
              <span className={styles.crossVerifiedIcon}>🔮</span>
              <span className={styles.crossVerifiedText}>
                {locale === "ko" ? "사주 + 점성술 교차 검증 완료" : "Saju + Astrology Cross-verified"}
              </span>
            </div>
          )}

          {/* Summary */}
          {selectedDate.summary && (
            <div className={`${styles.summaryBox} ${selectedDate.grade >= 3 ? styles.summaryWarning : ''}`}>
              <p className={styles.summaryText}>{selectedDate.summary}</p>
            </div>
          )}

          <p className={styles.selectedDesc}>{selectedDate.description}</p>

          {/* Ganzhi info */}
          {selectedDate.ganzhi && (
            <div className={styles.ganzhiBox}>
              <span className={styles.ganzhiLabel}>
                {locale === "ko" ? "일주" : "Day Pillar"}
              </span>
              <span className={styles.ganzhiValue}>{selectedDate.ganzhi}</span>
              {selectedDate.transitSunSign && (
                <>
                  <span className={styles.ganzhiDivider}>|</span>
                  <span className={styles.ganzhiLabel}>
                    {locale === "ko" ? "태양" : "Sun"}
                  </span>
                  <span className={styles.ganzhiValue}>{selectedDate.transitSunSign}</span>
                </>
              )}
            </div>
          )}

          {/* Best times */}
          {selectedDate.bestTimes && selectedDate.bestTimes.length > 0 && (
            <div className={styles.bestTimesBox}>
              <h4 className={styles.bestTimesTitle}>
                <span className={styles.bestTimesIcon}>⏰</span>
                {locale === "ko" ? "오늘의 좋은 시간" : "Best Times Today"}
              </h4>
              <div className={styles.bestTimesList}>
                {selectedDate.bestTimes.map((time, i) => (
                  <span key={i} className={styles.bestTimeItem}>
                    <span className={styles.bestTimeNumber}>{i + 1}</span>
                    {time}
                  </span>
                ))}
              </div>
            </div>
          )}

          {/* Categories */}
          <div className={styles.selectedCategories}>
            {selectedDate.categories.map(cat => (
              <span key={cat} className={`${styles.categoryTag} ${styles[cat]}`}>
                {CATEGORY_EMOJI[cat]} {getCategoryLabel(cat)}
              </span>
            ))}
          </div>

          {/* Score bar */}
          <div className={styles.scoreWrapper}>
            <div className={styles.scoreBar}>
              <div
                className={`${styles.scoreFill} ${getScoreClass(selectedDate.score)}`}
                style={{ width: `${selectedDate.score}%` }}
              />
            </div>
            <span className={styles.scoreText}>
              {locale === "ko" ? "점수" : "Score"}: {selectedDate.score}/100
            </span>
          </div>

          {/* Saju analysis section */}
          {selectedDate.sajuFactors && selectedDate.sajuFactors.length > 0 && (
            <div className={styles.analysisSection}>
              <h4 className={styles.analysisTitle}>
                <span className={styles.analysisBadge}>☯️</span>
                {locale === "ko" ? "사주 분석" : "Saju Analysis"}
              </h4>
              <ul className={styles.analysisList}>
                {selectedDate.sajuFactors.slice(0, 4).map((factor, i) => (
                  <li key={i} className={styles.analysisItem}>
                    <span className={styles.analysisDotSaju}></span>
                    {factor}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Astrology analysis section */}
          {selectedDate.astroFactors && selectedDate.astroFactors.length > 0 && (
            <div className={styles.analysisSection}>
              <h4 className={styles.analysisTitle}>
                <span className={styles.analysisBadge}>🌟</span>
                {locale === "ko" ? "점성술 분석" : "Astrology Analysis"}
              </h4>
              <ul className={styles.analysisList}>
                {selectedDate.astroFactors.slice(0, 4).map((factor, i) => (
                  <li key={i} className={styles.analysisItem}>
                    <span className={styles.analysisDotAstro}></span>
                    {factor}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Recommendations */}
          {selectedDate.recommendations.length > 0 && (
            <div className={styles.recommendationsSection}>
              <h4 className={styles.recommendationsTitle}>
                <span className={styles.recommendationsIcon}>✨</span>
                {locale === "ko" ? "오늘의 행운 키" : "Lucky Keys"}
              </h4>
              <div className={styles.recommendationsGrid}>
                {selectedDate.recommendations.slice(0, 4).map((r, i) => (
                  <div key={i} className={styles.recommendationCard}>
                    <span className={styles.recommendationNumber}>{i + 1}</span>
                    <span className={styles.recommendationText}>{r}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Warnings - Grade 3 이상은 상단에서 이미 표시했으므로 생략 */}
          {selectedDate.warnings.length > 0 && selectedDate.grade < 3 && (
            <div className={styles.warningsSection}>
              <h4 className={styles.warningsTitle}>
                <span className={styles.warningsIcon}>⚡</span>
                {locale === "ko" ? "오늘의 주의보" : "Today's Alert"}
              </h4>
              <ul className={styles.warningsList}>
                {selectedDate.warnings.slice(0, 3).map((w, i) => (
                  <li key={i} className={styles.warningItem}>
                    <span className={styles.warningDot}></span>
                    {w}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Large save button - authenticated users only */}
          {status === 'authenticated' && (
            <button
              className={`${styles.saveBtnLarge} ${isSaved ? styles.saved : ''}`}
              onClick={isSaved ? onUnsave : onSave}
              disabled={saving}
            >
              {saving ? (
                <span>{locale === 'ko' ? '저장 중...' : 'Saving...'}</span>
              ) : isSaved ? (
                <>
                  <span>★</span>
                  <span>{locale === 'ko' ? '저장됨 (클릭하여 삭제)' : 'Saved (click to remove)'}</span>
                </>
              ) : (
                <>
                  <span>☆</span>
                  <span>{locale === 'ko' ? '이 날짜 저장하기' : 'Save this date'}</span>
                </>
              )}
            </button>
          )}

          {/* Add to phone calendar button */}
          <button
            className={styles.calendarSyncBtn}
            onClick={handleAddToCalendar}
            aria-label={locale === 'ko' ? '휴대폰 캘린더에 추가' : 'Add to phone calendar'}
          >
            <span>📲</span>
            <span>{locale === 'ko' ? '캘린더에 추가' : 'Add to Calendar'}</span>
          </button>
        </div>
      ) : (
        <div className={styles.noInfo}>
          <p>{locale === "ko" ? "이 날짜에 대한 정보가 없습니다" : "No info for this date"}</p>
        </div>
      )}
    </div>
  );
});

export default SelectedDatePanel;

"use client";

import type { TabProps } from '../types';
import { InsightCard, InsightContent, Badge } from '../InsightCard';

export default function FutureTab({ data, isKo }: TabProps) {
  const { persons, sajuAnalysis, compositeChart, synastry } = data;

  const person1Name = persons[0]?.name || (isKo ? '사람 1' : 'Person 1');
  const person2Name = persons[1]?.name || (isKo ? '사람 2' : 'Person 2');

  // Generate future outlook based on analysis
  const longevityScore = compositeChart?.longevityPotential || 70;
  const overallScore = sajuAnalysis?.overallScore || 65;
  const averageScore = Math.round((longevityScore + overallScore) / 2);

  // Future phases based on scores
  const futurePhases = [
    {
      period: isKo ? '초기 (1-2년)' : 'Early (1-2 years)',
      emoji: '🌱',
      description: isKo
        ? '새로운 관계의 시작, 서로를 알아가는 시간입니다. 차이점을 발견하고 조율하는 시기입니다.'
        : 'Beginning of a new relationship, time to know each other. Period of discovering differences and adjusting.',
      advice: isKo
        ? '서로의 차이를 인정하고 열린 대화를 유지하세요.'
        : 'Accept differences and maintain open communication.',
      color: 'emerald',
    },
    {
      period: isKo ? '중기 (3-5년)' : 'Middle (3-5 years)',
      emoji: '🌳',
      description: isKo
        ? '관계가 깊어지고 안정화되는 시기입니다. 공동의 목표와 계획을 세울 수 있습니다.'
        : 'Relationship deepens and stabilizes. Time to set shared goals and plans.',
      advice: isKo
        ? '함께하는 목표를 설정하고 성취감을 나누세요.'
        : 'Set goals together and share achievements.',
      color: 'blue',
    },
    {
      period: isKo ? '장기 (5년 이상)' : 'Long-term (5+ years)',
      emoji: '🌲',
      description: isKo
        ? '성숙한 관계로 발전할 수 있습니다. 서로의 성장을 지지하며 동반자로 함께합니다.'
        : 'Can develop into a mature relationship. Supporting each other\'s growth as companions.',
      advice: isKo
        ? '개인의 성장과 관계의 발전을 균형있게 유지하세요.'
        : 'Balance individual growth with relationship development.',
      color: 'purple',
    },
  ];

  // Potential challenges by phase
  const potentialChallenges = sajuAnalysis?.conflicts?.totalConflicts || 0;
  const strengthCount = (synastry?.strengths?.length || 0) + (sajuAnalysis?.detailedInsights?.length || 0);

  // Generate continuous flowing analysis text
  const analysisLines: string[] = [];
  if (isKo) {
    analysisLines.push(`${person1Name}님과 ${person2Name}님의 미래 전망을 분석합니다.`);
    analysisLines.push(`장기 잠재력 점수는 ${averageScore}%로, ${averageScore >= 80 ? '매우 밝은 미래가 기대됩니다.' : averageScore >= 60 ? '꾸준한 노력으로 좋은 관계를 유지할 수 있습니다.' : '도전이 있지만 함께 극복하면 더 강해질 수 있습니다.'}`);
    analysisLines.push(`현재 궁합 점수 ${overallScore}%를 기반으로, 서로의 이해와 노력을 통해 최대 ${Math.min(overallScore + 20, 100)}%까지 성장할 수 있습니다.`);
    analysisLines.push(`관계의 강점이 ${strengthCount}개, 도전 요소가 ${potentialChallenges}개 발견되었습니다. ${strengthCount > potentialChallenges ? '강점이 도전을 넘어서므로 긍정적인 관계 발전이 기대됩니다.' : '도전 요소를 함께 극복하면서 성장하는 관계입니다.'}`);
    analysisLines.push('초기 1-2년은 서로를 알아가는 시간으로, 차이점을 발견하고 조율하는 중요한 시기입니다.');
    analysisLines.push('중기 3-5년에는 관계가 깊어지고 안정화되며, 공동의 목표를 세울 수 있습니다.');
    analysisLines.push('장기적으로는 서로의 성장을 지지하는 성숙한 동반자 관계로 발전할 수 있습니다.');
    if (sajuAnalysis?.detailedInsights && sajuAnalysis.detailedInsights.length > 0) {
      analysisLines.push(`핵심 인사이트: ${sajuAnalysis.detailedInsights[0]}`);
    }
    if (synastry?.strengths && synastry.strengths.length > 0) {
      analysisLines.push(`미래를 밝히는 강점: ${synastry.strengths.slice(0, 2).join(', ')}`);
    }
    analysisLines.push('서로의 차이를 장점으로 활용하고, 정기적인 소통과 감정 공유를 통해 관계를 더욱 발전시켜 나가세요.');
  } else {
    analysisLines.push(`Future outlook for ${person1Name} and ${person2Name}.`);
    analysisLines.push(`Long-term potential: ${averageScore}%. ${averageScore >= 80 ? 'A very bright future ahead.' : averageScore >= 60 ? 'Steady effort will maintain a good relationship.' : 'Challenges ahead but overcoming them together strengthens the bond.'}`);
    analysisLines.push(`Current compatibility of ${overallScore}% can grow to ${Math.min(overallScore + 20, 100)}% through mutual understanding.`);
    analysisLines.push(`${strengthCount} strengths vs ${potentialChallenges} challenges. ${strengthCount > potentialChallenges ? 'Strengths outweigh challenges — positive growth expected.' : 'Together you can turn challenges into growth.'}`);
    analysisLines.push('Early phase (1-2 years): discovering differences and building understanding.');
    analysisLines.push('Middle phase (3-5 years): deepening bonds and setting shared goals.');
    analysisLines.push('Long-term (5+ years): developing into mature companions supporting each other\'s growth.');
    if (sajuAnalysis?.detailedInsights && sajuAnalysis.detailedInsights.length > 0) {
      analysisLines.push(`Key insight: ${sajuAnalysis.detailedInsights[0]}`);
    }
  }

  return (
    <div className="space-y-6">
      {/* Future Outlook Hero */}
      <div className="relative overflow-hidden rounded-3xl bg-gradient-to-br from-slate-900 via-indigo-900/30 to-slate-900 border border-indigo-500/30 p-6">
        <div className="absolute top-0 right-0 w-48 h-48 bg-indigo-500/10 rounded-full blur-3xl" />
        <div className="absolute bottom-0 left-0 w-40 h-40 bg-purple-500/10 rounded-full blur-3xl" />

        <div className="relative text-center">
          <span className="text-5xl mb-4 block">🔮</span>
          <h2 className="text-xl md:text-2xl font-bold text-gray-100 mb-2">
            {isKo ? '미래 전망' : 'Future Outlook'}
          </h2>
          <p className="text-indigo-300 mb-4">
