      : `${currentMonth} is the month of ${monthEnergyDesc[monthElement].en}.\n\n` +
        (monthYongsinMatch
          ? `🎯 Perfect month for you! Your ${yongsinElement ? elementTraits[yongsinElement]?.en : ""} energy is strong. Take action boldly.`
          : monthYongsinClash
          ? `⚡ Be careful this month. Focus on boosting ${yongsinElement ? elementTraits[yongsinElement]?.en : ""} energy and don't overextend.`
          : monthElement === "wood" ? "Good time to start new projects." :
            monthElement === "fire" ? "Act and express yourself actively." :
            monthElement === "earth" ? "Review and organize what you've built." :
            monthElement === "metal" ? "Good time for important decisions." :
            "Don't overdo it, recharge your energy.")
  };

  // 3) 현재 인생 단계 - 대운 기반 + 용신 활용법
  const stageDescriptions: Record<number, { ko: string; en: string; emoji: string }> = {
    0: { ko: "씨앗을 심는 시기", en: "Time to plant seeds", emoji: "🌱" },
    1: { ko: "기반을 다지는 시기", en: "Time to build foundation", emoji: "🏗️" },
    2: { ko: "성장하는 시기", en: "Time of growth", emoji: "🌿" },
    3: { ko: "꽃을 피우는 시기", en: "Time to bloom", emoji: "🌸" },
    4: { ko: "열매를 맺는 시기", en: "Time to bear fruit", emoji: "🍎" },
    5: { ko: "수확하는 시기", en: "Time to harvest", emoji: "🌾" },
    6: { ko: "정리하는 시기", en: "Time to organize", emoji: "📦" },
    7: { ko: "지혜를 나누는 시기", en: "Time to share wisdom", emoji: "🦉" },
    8: { ko: "재정비하는 시기", en: "Time to reorganize", emoji: "🔄" },
    9: { ko: "다음을 준비하는 시기", en: "Time to prepare for next", emoji: "🎯" }
  };

  const currentStageInfo = stageDescriptions[daeunProgress] || stageDescriptions[0];

  // 단계별 용신 활용 조언
  const stageYongsinAdvice: Record<string, Record<string, { ko: string; en: string }>> = {
    early: { // 0-2년차
      wood: { ko: "배우고 성장하는데 집중하세요", en: "Focus on learning and growth" },
      fire: { ko: "열정적으로 관계를 넓히세요", en: "Expand relationships passionately" },
      earth: { ko: "튼튼한 기반을 만드세요", en: "Build a solid foundation" },
      metal: { ko: "체계를 갖추고 정리하세요", en: "Get organized and systematic" },
      water: { ko: "깊이 생각하고 준비하세요", en: "Think deeply and prepare" }
    },
    mid: { // 3-6년차
      wood: { ko: "성장의 기회를 적극 잡으세요", en: "Seize growth opportunities" },
      fire: { ko: "에너지를 발산하고 도전하세요", en: "Express energy and take challenges" },
      earth: { ko: "안정적으로 결실을 만드세요", en: "Create results steadily" },
      metal: { ko: "명확한 결단을 내리세요", en: "Make clear decisions" },
      water: { ko: "지혜롭게 흐름을 타세요", en: "Ride the flow wisely" }
    },
    late: { // 7-9년차
      wood: { ko: "경험을 나누고 전수하세요", en: "Share and pass on experience" },
      fire: { ko: "후배들에게 영감을 주세요", en: "Inspire juniors" },
      earth: { ko: "쌓아온 것을 정리하세요", en: "Organize what you've built" },
      metal: { ko: "완성도를 높이고 마무리하세요", en: "Perfect and complete" },
      water: { ko: "다음 단계를 차분히 준비하세요", en: "Calmly prepare next stage" }
    }
  };

  const stagePhase = daeunProgress < 3 ? "early" : daeunProgress < 7 ? "mid" : "late";
  const yongsinAdvice = yongsinElement ? stageYongsinAdvice[stagePhase][yongsinElement] : null;

  const currentStage = {
    emoji: "🦋",
    title: isKo ? "지금 인생 단계" : "Life Stage Now",
    subtitle: currentStageInfo[isKo ? "ko" : "en"],
    message: isKo
      ? `${age}세, ${currentStageInfo.ko}입니다.\n\n` +
        (yongsinAdvice
          ? `💡 이 단계에서는: ${yongsinAdvice.ko}\n${yongsinElement ? elementTraits[yongsinElement]?.ko : ""} 에너지를 채우면서 나아가면 더 순조롭게 흐름을 탈 수 있어요.`
          : daeunProgress < 3 ? "지금은 준비하고 배우는 시기예요. 조급해하지 마세요." :
            daeunProgress < 7 ? "가장 활발하게 활동하는 시기예요. 적극적으로 도전하세요." :
            "경험을 정리하고 다음 단계를 준비하세요.")
      : `Age ${age}, ${currentStageInfo.en}.\n\n` +
        (yongsinAdvice
          ? `💡 At this stage: ${yongsinAdvice.en}\nEnhancing your ${yongsinElement ? elementTraits[yongsinElement]?.en : ""} energy will help you flow more smoothly.`
          : daeunProgress < 3 ? "Time to prepare and learn. Don't rush." :
            daeunProgress < 7 ? "Most active period. Challenge yourself." :
            "Organize experiences and prepare for next stage.")
  };

  return {
    today,
    thisMonth,
    currentStage
  };
}

import type { SajuData, UnseItem } from '../types';

export function getCurrentFlowAnalysis(saju: SajuData | undefined, lang: string): { title: string; flow: string; advice: string; emoji: string } | null {
  const isKo = lang === "ko";
  const unse = saju?.unse;

  if (!unse) {return null;}

  // 현재 대운 찾기 - age 기준으로 현재 대운 계산
  const daeunList = Array.isArray(unse.daeun) ? unse.daeun : [];
  const currentYear = new Date().getFullYear();

  // birthYear 계산 - 여러 경로 시도
  let birthYear: number | null = null;
  if (saju?.facts?.birthDate) {
    birthYear = new Date(saju.facts.birthDate).getFullYear();
  } else if (saju?.pillars?.year?.heavenlyStem) {
    // pillars.year에서 간지로 연도 역산 (간단히 대운 첫 번째 항목에서 추정)
    // 더 정확한 방법: 대운 age를 이용
    if (daeunList.length > 0) {
      const firstDaeun = daeunList[0];
      birthYear = currentYear - 31; // 임시: 현재 나이 31세 가정 (나중에 개선)
    }
  }

  // 만 나이와 한국 나이 둘 다 계산
  const koreanAge = birthYear ? currentYear - birthYear + 1 : null;
  const westernAge = birthYear ? currentYear - birthYear : null;

  // 대운 찾기 - 만 나이와 한국 나이 둘 다 시도
  let currentDaeun = null;
  if (koreanAge) {
    // 한국 나이로 먼저 시도 (age부터 age+9까지, 총 10년)
    currentDaeun = daeunList.find((d: UnseItem) => {
      const startAge = d.age || 0;
      const endAge = startAge + 9;
      return koreanAge >= startAge && koreanAge <= endAge;
    });

    // 못 찾으면 만 나이로 시도
    if (!currentDaeun && westernAge) {
      currentDaeun = daeunList.find((d: UnseItem) => {
        const startAge = d.age || 0;
        const endAge = startAge + 9;
        return westernAge >= startAge && westernAge <= endAge;
      });
    }
  }

  // 올해 연운 찾기
  const annualList = Array.isArray(unse.annual) ? unse.annual : [];
  const thisYear = annualList.find((a: UnseItem) => a.year === currentYear);

  if (!currentDaeun && !thisYear) {return null;}

  const daeunGanji = currentDaeun ? `${currentDaeun.heavenlyStem}${currentDaeun.earthlyBranch}` : "";
  const daeunAge = currentDaeun?.age != null ? `${currentDaeun.age}-${currentDaeun.age + 9}세` : "";
  const yearGanji = thisYear ? `${thisYear.heavenlyStem}${thisYear.earthlyBranch}` : "";
  const yearNum = thisYear?.year || currentYear;

  // 십신 정보 추출 - sibsin이 string일 수도 있고 object일 수도 있음
  const daeunSibsinRaw = currentDaeun?.sibsin;
  const daeunSibsin = typeof daeunSibsinRaw === 'object' && daeunSibsinRaw?.cheon ? daeunSibsinRaw.cheon : (typeof daeunSibsinRaw === 'string' ? daeunSibsinRaw : "");
  const yearSibsinRaw = thisYear?.sibsin;
  const yearSibsin = typeof yearSibsinRaw === 'object' && yearSibsinRaw?.cheon ? yearSibsinRaw.cheon : (typeof yearSibsinRaw === 'string' ? yearSibsinRaw : "");

  // 십신별 간단한 설명
  const sibsinFlow: Record<string, { ko: string; en: string }> = {
    "비견": { ko: "독립·경쟁", en: "Independence" },
    "겁재": { ko: "변화·도전", en: "Challenge" },
    "식신": { ko: "창작·여유", en: "Creativity" },
    "상관": { ko: "표현·혁신", en: "Expression" },
    "편재": { ko: "사업·활동", en: "Business" },
    "정재": { ko: "안정·재물", en: "Stability" },
    "편관": { ko: "변화·추진", en: "Drive" },
    "정관": { ko: "명예·질서", en: "Honor" },
    "편인": { ko: "탐구·자유", en: "Exploration" },
    "정인": { ko: "학습·성장", en: "Learning" }
  };

  const daeunFlow = sibsinFlow[daeunSibsin]?.[isKo ? "ko" : "en"] || "";
  const yearFlow = sibsinFlow[yearSibsin]?.[isKo ? "ko" : "en"] || "";

  // 대운 표시: 간지 + 연령대 + 십신
  const daeunDisplay = daeunGanji
    ? `${daeunGanji}(${daeunAge})${daeunFlow ? ` - ${daeunFlow}` : ""}`
    : isKo ? "정보 없음" : "N/A";

  // 세운 표시: 연도 + 간지 + 십신
  const yearDisplay = yearGanji
    ? `${yearNum}년 ${yearGanji}${yearFlow ? ` - ${yearFlow}` : ""}`
    : isKo ? "정보 없음" : "N/A";

  const flow = isKo
    ? `대운(10년 주기): ${daeunDisplay}\n세운(올해): ${yearDisplay}`
    : `Daeun (10-year): ${daeunDisplay}\nAnnual Luck: ${yearDisplay}`;

  const advice = isKo
    ? daeunSibsin && yearSibsin
      ? `${daeunFlow} 에너지 속에서 올해는 ${yearFlow}에 집중하세요.`
      : "현재 흐름에 맞춰 차근차근 나아가세요."
    : daeunSibsin && yearSibsin
      ? `Focus on ${yearFlow} within ${daeunFlow} energy this year.`
      : "Move forward steadily with the current flow.";

  return {
    title: isKo ? "지금 내 흐름" : "Current Flow",
    flow,
    advice,
    emoji: "🌊"
  };
}

import { extractSajuProfile, extractAstroProfile, calculateMonthlyImportantDates, type ImportantDate } from '@/lib/destiny-map/destinyCalendar';
import { elementTraits, elementKeyMap, elementRelations, monthElements } from '../data';
import type { SajuData, AstroData } from '../types';

interface RecommendedDateItem {
  date: string;
  type: string;
  reason: string;
  score: number;
  grade?: number;
  categories?: string[];
}

export function getRecommendedDates(saju: SajuData | undefined, astro: AstroData | undefined, lang: string): RecommendedDateItem[] {
  const isKo = lang === "ko";
  const currentYear = new Date().getFullYear();

  try {
    const sajuProfile = extractSajuProfile(saju);
    const astroProfile = extractAstroProfile(astro);

    // 생년월일에서 출생년도 추출 (과거 15년 ~ 미래 15년)
    const birthYear = parseInt(saju?.birthDate?.split("-")[0] || "") || 1990;
    const startYear = currentYear - 15; // 과거 15년
    const endYear = currentYear + 15; // 미래 15년

    // 연도별 평균 점수 계산
    const yearScores: Record<number, { totalScore: number; count: number; bestGrade: number; dates: ImportantDate[] }> = {};

    // 과거 15년부터 미래 15년까지 스캔
    for (let year = startYear; year <= endYear; year++) {
      yearScores[year] = { totalScore: 0, count: 0, bestGrade: 0, dates: [] };

      // 각 년도의 모든 월을 체크
      for (let month = 0; month < 12; month++) {
        const monthData = calculateMonthlyImportantDates(year, month, sajuProfile, astroProfile);

        // 모든 날짜 수집 (좋은 날과 나쁜 날 모두)
        for (const d of monthData.dates) {
          yearScores[year].totalScore += d.score;
          yearScores[year].count += 1;
          yearScores[year].bestGrade = Math.max(yearScores[year].bestGrade, d.grade || 0);
          yearScores[year].dates.push(d);
        }
      }
    }

    // 연도별 평균 점수 계산하여 배열로 변환
    const yearRankings = Object.entries(yearScores)
      .map(([year, data]) => ({
        year: parseInt(year),
        avgScore: data.count > 0 ? data.totalScore / data.count : 0,
        bestGrade: data.bestGrade,
        totalScore: data.totalScore,
        count: data.count
      }))
      .filter(y => y.count > 0); // 데이터가 있는 년도만

    // 점수 순으로 정렬
    yearRankings.sort((a, b) => b.avgScore - a.avgScore);

    // 상위 4개 (좋은 해)와 하위 4개 (나쁜 해) 선택
    const bestYears = yearRankings.slice(0, 4);
    const worstYears = yearRankings.slice(-4).reverse(); // 최악부터 순서

    // 결과 배열 생성
    const result: { date: string; type: string; reason: string; score: number; grade?: number }[] = [];

    // 좋은 해 4개 추가
    for (const yearData of bestYears) {
      const yearInfo = yearScores[yearData.year];
      const age = yearData.year - birthYear;

      // 해당 년도의 카테고리 분석
      const categories: Record<string, number> = {};
      for (const d of yearInfo.dates) {
        for (const cat of d.categories || []) {
          categories[cat] = (categories[cat] || 0) + 1;
        }
      }
      const topCategories = Object.entries(categories)
        .sort((a, b) => b[1] - a[1])
        .slice(0, 2)
        .map(([cat]) => cat);

      const categoryText = isKo
        ? topCategories.map(c => {
            if (c === 'career') {return '커리어';}
            if (c === 'wealth') {return '재물';}
            if (c === 'love') {return '연애';}
            if (c === 'health') {return '건강';}
            if (c === 'study') {return '학업';}
            if (c === 'travel') {return '이동';}
            return '전반';
          }).join(', ')
        : topCategories.join(', ');

      result.push({
        date: isKo ? `${yearData.year}년` : `${yearData.year}`,
        type: isKo ? "✨ 좋은 해" : "✨ Good Year",
        reason: isKo
          ? `${age}세 - 운세 점수 ${Math.round(yearData.avgScore)}점. ${categoryText} 운이 특히 강한 해입니다. 중요한 결정과 새로운 시작에 유리한 시기입니다.`
          : `Age ${age} - Fortune score ${Math.round(yearData.avgScore)}. Strong ${categoryText} luck. Favorable for important decisions and new beginnings.`,
        score: yearData.avgScore,
        grade: yearData.bestGrade
      });
    }

    // 나쁜 해 4개 추가
    for (const yearData of worstYears) {
      const yearInfo = yearScores[yearData.year];
      const age = yearData.year - birthYear;

      result.push({
        date: isKo ? `${yearData.year}년` : `${yearData.year}`,
        type: isKo ? "⚠️ 조심할 해" : "⚠️ Cautious Year",
        reason: isKo
          ? `${age}세 - 운세 점수 ${Math.round(yearData.avgScore)}점. 신중함이 필요한 해입니다. 큰 결정은 피하고 현상 유지와 준비에 집중하세요.`
          : `Age ${age} - Fortune score ${Math.round(yearData.avgScore)}. Year requiring caution. Avoid major decisions, focus on maintaining status quo and preparation.`,
        score: yearData.avgScore,
        grade: yearData.bestGrade
      });
    }

    return result;
  } catch (error) {
    // 폴백: 기존 단순 계산
    return getSimpleRecommendedDates(saju, lang);
  }
}

export function getSimpleRecommendedDates(saju: SajuData | undefined, lang: string): { date: string; type: string; reason: string; score: number }[] {
  const dates: { date: string; type: string; reason: string; score: number }[] = [];
  const isKo = lang === "ko";
  const currentYear = new Date().getFullYear();
  const currentMonth = new Date().getMonth() + 1;

  const dayElement = saju?.dayMaster?.element ? elementKeyMap[saju.dayMaster.element] : null;
  if (!dayElement) {return dates;}

  for (let m = 1; m <= 12; m++) {
    const monthEl = monthElements[m];
    let score = 50;
    let reason = "";

    if (elementRelations.supportedBy[dayElement] === monthEl) {
      score = 85;
      reason = isKo ? `${elementTraits[monthEl]?.ko}이 나를 생(生)해주는 달` : `${elementTraits[monthEl]?.en} generates your energy`;
    } else if (monthEl === dayElement) {
      score = 75;
      reason = isKo ? "같은 오행으로 힘이 강해지는 달" : "Same element strengthens you";
    }

    if (score >= 75) {
      const isUpcoming = m >= currentMonth;
      const year = isUpcoming ? currentYear : currentYear + 1;
      dates.push({
        date: isKo ? `${year}년 ${m}월` : `${year}/${m}`,
        type: score >= 80 ? (isKo ? "🌟 대길월" : "🌟 Excellent") : (isKo ? "⭐ 길월" : "⭐ Good"),
        reason,
        score
      });
    }
  }

  return dates.sort((a, b) => b.score - a.score).slice(0, 4);
}

// 개인화된 어드바이스 생성기 - 사주 요소 조합 + 조건부 변형
import type { SajuData, AstroData } from '../types';

interface PersonalizedAdvice {
  emoji: string;
  title: { ko: string; en: string };
  summary: { ko: string; en: string };
  detail: { ko: string; en: string };
  source?: string; // 어떤 요소에서 도출되었는지
}

// 십신 분포 추출
function getSibsinDistribution(saju: SajuData | undefined): Record<string, number> {
  return saju?.advancedAnalysis?.sibsin?.sibsinDistribution || {};
}

// 가장 강한 십신 찾기
function getDominantSibsin(saju: SajuData | undefined): string | null {
  const dist = getSibsinDistribution(saju);
  const entries = Object.entries(dist);
  if (entries.length === 0) {return null;}
  const sorted = entries.sort(([, a], [, b]) => b - a);
  return sorted[0]?.[0] || null;
}

// 오행 비율 추출
function getElementRatios(saju: SajuData | undefined): { element: string; ratio: number }[] {
  const elements = saju?.fiveElements || {};
  const total = Object.values(elements).reduce((a, b) => (a as number) + (b as number), 0) as number;
  if (total === 0) {return [];}

  return Object.entries(elements)
    .map(([el, val]) => ({ element: el, ratio: Math.round(((val as number) / total) * 100) }))
    .sort((a, b) => b.ratio - a.ratio);
}

// 신살 추출
function getSinsalList(saju: SajuData | undefined): string[] {
  const sinsal = saju?.sinsal;
  const shinsal = saju?.shinsal;
  const result: string[] = [];

  if (sinsal?.luckyList) {result.push(...sinsal.luckyList.map(s => s.name));}
  if (sinsal?.unluckyList) {result.push(...sinsal.unluckyList.map(s => s.name));}
  if (Array.isArray(shinsal)) {result.push(...shinsal.map(s => s.name || '').filter(Boolean));}

  return result;
}

// 12운성 추출
function getTwelveStage(saju: SajuData | undefined): string | null {
  return saju?.twelveStages?.day || saju?.twelveStage || null;
}

// 일간 추출
function getDayMaster(saju: SajuData | undefined): string | null {
  return saju?.dayMaster?.name || saju?.dayMaster?.heavenlyStem || null;
}

