/**
 * Aspect finding and analysis utilities
 */

import type { StandardPlanetName, AspectType } from '../types/core';
import type { AstroData, AspectData } from '../types';
import { extractAspectPlanetName, normalizeAspectType } from './planets';

// ========== Aspect Info Types ==========

export interface AspectInfo {
  planet1: StandardPlanetName;
  planet2: StandardPlanetName;
  type: AspectType;
  orb?: number;
}

export interface DetailedAspectInfo extends AspectInfo {
  isHarmonious: boolean;
  isChallenging: boolean;
  strength: 'exact' | 'close' | 'wide';
}

// ========== Aspect Classification ==========

const HARMONIOUS_ASPECTS: AspectType[] = ['trine', 'sextile'];
const CHALLENGING_ASPECTS: AspectType[] = ['square', 'opposition'];
const CONJUNCTION_NEUTRAL: AspectType[] = ['conjunction'];

/**
 * Checks if an aspect type is harmonious (trine, sextile)
 */
export function isHarmoniousAspect(type: AspectType): boolean {
  return HARMONIOUS_ASPECTS.includes(type);
}

/**
 * Checks if an aspect type is challenging (square, opposition)
 */
export function isChallengingAspect(type: AspectType): boolean {
  return CHALLENGING_ASPECTS.includes(type);
}

/**
 * Get aspect strength based on orb
 */
export function getAspectStrength(orb: number | undefined): 'exact' | 'close' | 'wide' {
  if (orb === undefined) {return 'close';}
  if (orb <= 1) {return 'exact';}
  if (orb <= 3) {return 'close';}
  return 'wide';
}

// ========== Aspect Finding ==========

/**
 * Find aspect between two specific planets
 *
 * @param astro - Astro data containing aspects
 * @param planet1 - First planet name (standardized)
 * @param planet2 - Second planet name (standardized)
 * @returns Aspect info if found, null otherwise
 */
export function findAspect(
  astro: AstroData | undefined,
  planet1: StandardPlanetName,
  planet2: StandardPlanetName
): AspectInfo | null {
  if (!astro?.aspects || !Array.isArray(astro.aspects)) {return null;}

  for (const aspect of astro.aspects) {
    const from = extractAspectPlanetName(aspect.from);
    const to = extractAspectPlanetName(aspect.to);
    const type = normalizeAspectType(aspect.type);

    if (!from || !to || !type) {continue;}

    // Check both directions
    if ((from === planet1 && to === planet2) || (from === planet2 && to === planet1)) {
      return {
        planet1: from,
        planet2: to,
        type,
        orb: aspect.orb
      };
    }
  }

  return null;
}

/**
 * Find aspect with detailed info
 */
export function findAspectDetailed(
  astro: AstroData | undefined,
  planet1: StandardPlanetName,
  planet2: StandardPlanetName
): DetailedAspectInfo | null {
  const basic = findAspect(astro, planet1, planet2);
  if (!basic) {return null;}

  return {
    ...basic,
    isHarmonious: isHarmoniousAspect(basic.type),
    isChallenging: isChallengingAspect(basic.type),
    strength: getAspectStrength(basic.orb)
  };
}

/**
 * Find all aspects involving a specific planet
 */
export function findPlanetAspects(
  astro: AstroData | undefined,
  planet: StandardPlanetName
): AspectInfo[] {
  if (!astro?.aspects || !Array.isArray(astro.aspects)) {return [];}

  const results: AspectInfo[] = [];

  for (const aspect of astro.aspects) {
    const from = extractAspectPlanetName(aspect.from);
    const to = extractAspectPlanetName(aspect.to);
    const type = normalizeAspectType(aspect.type);

    if (!from || !to || !type) {continue;}

    if (from === planet || to === planet) {
      results.push({
        planet1: from,
        planet2: to,
        type,
        orb: aspect.orb
      });
    }
  }

  return results;
}

/**
 * Find all harmonious aspects involving a planet
 */
export function findHarmoniousAspects(
  astro: AstroData | undefined,
  planet: StandardPlanetName
): AspectInfo[] {
  return findPlanetAspects(astro, planet)
    .filter(a => isHarmoniousAspect(a.type));
}

/**
 * Find all challenging aspects involving a planet
 */
export function findChallengingAspects(
  astro: AstroData | undefined,
  planet: StandardPlanetName
): AspectInfo[] {
  return findPlanetAspects(astro, planet)
    .filter(a => isChallengingAspect(a.type));
}

/**
 * Check if a specific aspect exists between two planets
 */
export function hasAspect(
  astro: AstroData | undefined,
  planet1: StandardPlanetName,
  planet2: StandardPlanetName,
  aspectTypes?: AspectType[]
): boolean {
  const aspect = findAspect(astro, planet1, planet2);
  if (!aspect) {return false;}

  if (aspectTypes && aspectTypes.length > 0) {
    return aspectTypes.includes(aspect.type);
  }

  return true;
}

/**
 * Check if harmonious aspect exists between two planets
 */
export function hasHarmoniousAspect(
  astro: AstroData | undefined,
  planet1: StandardPlanetName,
  planet2: StandardPlanetName
): boolean {
  return hasAspect(astro, planet1, planet2, HARMONIOUS_ASPECTS);
}

/**
 * Check if challenging aspect exists between two planets
 */
export function hasChallengingAspect(
  astro: AstroData | undefined,
  planet1: StandardPlanetName,
  planet2: StandardPlanetName
): boolean {
  return hasAspect(astro, planet1, planet2, CHALLENGING_ASPECTS);
}

// ========== Aspect Summary ==========

/**
 * Get aspect type description
 */
export function getAspectTypeDescription(type: AspectType, isKo: boolean): string {
  const descriptions: Record<AspectType, { ko: string; en: string }> = {
    conjunction: { ko: '합(0°)', en: 'Conjunction (0°)' },
    opposition: { ko: '충(180°)', en: 'Opposition (180°)' },
    square: { ko: '스퀘어(90°)', en: 'Square (90°)' },
    trine: { ko: '트라인(120°)', en: 'Trine (120°)' },
    sextile: { ko: '섹스타일(60°)', en: 'Sextile (60°)' },
    quincunx: { ko: '퀸컨스(150°)', en: 'Quincunx (150°)' },
    semisextile: { ko: '세미섹스타일(30°)', en: 'Semisextile (30°)' },
    semisquare: { ko: '세미스퀘어(45°)', en: 'Semisquare (45°)' },
    sesquiquadrate: { ko: '세스퀴쿼드레이트(135°)', en: 'Sesquiquadrate (135°)' },
  };

  return isKo ? descriptions[type].ko : descriptions[type].en;
}

/**
 * Count aspects by type for a planet
 */
export function countAspectsByType(
  astro: AstroData | undefined,
  planet: StandardPlanetName
): Record<AspectType, number> {
  const aspects = findPlanetAspects(astro, planet);
  const counts: Record<AspectType, number> = {
    conjunction: 0,
    opposition: 0,
    square: 0,
    trine: 0,
    sextile: 0,
    quincunx: 0,
    semisextile: 0,
    semisquare: 0,
    sesquiquadrate: 0,
  };

  for (const aspect of aspects) {
    counts[aspect.type]++;
  }

  return counts;
}

/**
 * Input validation utilities for saju and astro data
 * Provides type guards and validators to ensure data safety
 */

import type { ZodiacSign, HouseNumber, FiveElement, HeavenlyStem } from '../types/core';
import type { SajuData, AstroData } from '../types';

// ========== Zodiac Sign Validation ==========

const VALID_ZODIAC_SIGNS: readonly ZodiacSign[] = [
  'aries', 'taurus', 'gemini', 'cancer', 'leo', 'virgo',
  'libra', 'scorpio', 'sagittarius', 'capricorn', 'aquarius', 'pisces'
];

/**
 * Validates and normalizes a zodiac sign to standard lowercase format
 *
 * @param sign - Input sign string
 * @returns Normalized zodiac sign or null if invalid
 */
export function normalizeZodiacSign(sign: unknown): ZodiacSign | null {
  if (typeof sign !== 'string' || !sign.trim()) {return null;}

  const normalized = sign.toLowerCase().trim();

  if (VALID_ZODIAC_SIGNS.includes(normalized as ZodiacSign)) {
    return normalized as ZodiacSign;
  }

  return null;
}

/**
 * Type guard for zodiac sign
 */
export function isValidZodiacSign(sign: unknown): sign is ZodiacSign {
  return normalizeZodiacSign(sign) !== null;
}

// ========== House Number Validation ==========

/**
 * Validates house number is within valid range (1-12)
 *
 * @param house - Input house number
 * @returns Validated house number or null if invalid
 */
export function validateHouseNumber(house: unknown): HouseNumber | null {
  if (typeof house !== 'number') {return null;}
  if (!Number.isInteger(house)) {return null;}
  if (house < 1 || house > 12) {return null;}

  return house as HouseNumber;
}

/**
 * Type guard for house number
 */
export function isValidHouseNumber(house: unknown): house is HouseNumber {
  return validateHouseNumber(house) !== null;
}

// ========== Five Element Validation ==========

const VALID_ELEMENTS: readonly FiveElement[] = ['wood', 'fire', 'earth', 'metal', 'water'];

const ELEMENT_ALIASES: Record<string, FiveElement> = {
  // English
  'wood': 'wood', 'fire': 'fire', 'earth': 'earth', 'metal': 'metal', 'water': 'water',
  // Korean
  '목': 'wood', '화': 'fire', '토': 'earth', '금': 'metal', '수': 'water',
  // Alternative
  'tree': 'wood', 'soil': 'earth', 'ground': 'earth',
};

/**
 * Validates and normalizes five element
 */
export function normalizeFiveElement(element: unknown): FiveElement | null {
  if (typeof element !== 'string' || !element.trim()) {return null;}

  const normalized = element.toLowerCase().trim();
  return ELEMENT_ALIASES[normalized] || null;
}

/**
 * Type guard for five element
 */
export function isValidFiveElement(element: unknown): element is FiveElement {
  return normalizeFiveElement(element) !== null;
}

// ========== Heavenly Stem Validation ==========

const VALID_STEMS: readonly HeavenlyStem[] = ['갑', '을', '병', '정', '무', '기', '경', '신', '임', '계'];

const STEM_ALIASES: Record<string, HeavenlyStem> = {
  // Korean
  '갑': '갑', '을': '을', '병': '병', '정': '정', '무': '무',
  '기': '기', '경': '경', '신': '신', '임': '임', '계': '계',
  // Chinese
  '甲': '갑', '乙': '을', '丙': '병', '丁': '정', '戊': '무',
  '己': '기', '庚': '경', '辛': '신', '壬': '임', '癸': '계',
};

/**
 * Validates and normalizes heavenly stem
 */
export function normalizeHeavenlyStem(stem: unknown): HeavenlyStem | null {
  if (typeof stem !== 'string' || !stem.trim()) {return null;}

  const trimmed = stem.trim();
  return STEM_ALIASES[trimmed] || null;
}

/**
 * Type guard for heavenly stem
 */
export function isValidHeavenlyStem(stem: unknown): stem is HeavenlyStem {
  return normalizeHeavenlyStem(stem) !== null;
}

// ========== Saju Data Validation ==========

/**
 * Checks if saju data has minimum required fields for analysis
 */
export function validateSajuData(saju: unknown): saju is SajuData {
  if (!saju || typeof saju !== 'object') {return false;}

  const s = saju as Record<string, unknown>;

  // Minimum requirement: dayMaster or pillars with day
  const hasDayMaster = s.dayMaster && typeof s.dayMaster === 'object';
  const hasPillars = s.pillars && typeof s.pillars === 'object';
  const hasDayPillar = s.dayPillar && typeof s.dayPillar === 'object';

  return !!(hasDayMaster || hasPillars || hasDayPillar);
}

/**
 * Checks if saju has valid five elements data
 */
export function hasFiveElements(saju: SajuData | undefined): boolean {
  if (!saju) {return false;}

  const elements = saju.fiveElements || saju.elements;
  if (!elements || typeof elements !== 'object') {return false;}

  const entries = Object.entries(elements);
  return entries.length > 0 && entries.some(([, v]) => typeof v === 'number' && v > 0);
}

/**
 * Checks if saju has advanced analysis data
 */
export function hasAdvancedAnalysis(saju: SajuData | undefined): boolean {
  if (!saju?.advancedAnalysis) {return false;}

  const aa = saju.advancedAnalysis;
  return !!(aa.sibsin || aa.geokguk || aa.yongsin);
}

/**
 * Checks if saju has sinsal/shinsal data
 */
export function hasSinsalData(saju: SajuData | undefined): boolean {
  if (!saju) {return false;}

  const sinsal = saju.sinsal || (saju.advancedAnalysis as Record<string, unknown>)?.sinsal;
  if (!sinsal || typeof sinsal !== 'object') {return false;}

  const s = sinsal as Record<string, unknown>;
  return !!(
    (Array.isArray(s.luckyList) && s.luckyList.length > 0) ||
    (Array.isArray(s.unluckyList) && s.unluckyList.length > 0)
  );
}

// ========== Astro Data Validation ==========

/**
 * Checks if astro data has minimum required fields for analysis
 */
export function validateAstroData(astro: unknown): astro is AstroData {
  if (!astro || typeof astro !== 'object') {return false;}

  const a = astro as Record<string, unknown>;

  // Minimum: planets or houses
  const hasPlanets = a.planets && (Array.isArray(a.planets) || typeof a.planets === 'object');
  const hasHouses = Array.isArray(a.houses) && a.houses.length > 0;

  return !!(hasPlanets || hasHouses);
}

/**
 * Checks if astro has valid planet data
 */
export function hasPlanetData(astro: AstroData | undefined): boolean {
  if (!astro?.planets) {return false;}

  if (Array.isArray(astro.planets)) {
    return astro.planets.length > 0;
  }

  return Object.keys(astro.planets).length > 0;
}

/**
 * Checks if astro has aspect data
 */
export function hasAspectData(astro: AstroData | undefined): boolean {
  return Array.isArray(astro?.aspects) && astro.aspects.length > 0;
}

/**
 * Checks if astro has house data
 */
export function hasHouseData(astro: AstroData | undefined): boolean {
  return Array.isArray(astro?.houses) && astro.houses.length > 0;
}

/**
 * Checks if astro has extra points (Chiron, Lilith, etc.)
 */
export function hasExtraPoints(astro: AstroData | undefined): boolean {
  if (!astro) {return false;}

  return !!(
    astro.extraPoints?.chiron ||
    astro.extraPoints?.lilith ||
    astro.extraPoints?.vertex ||
    astro.extraPoints?.partOfFortune
  );
}

// ========== Combined Validation ==========

/**
 * Validates both saju and astro data for analysis
 * Returns info about what data is available
 */
export function validateAnalysisInput(
  saju: unknown,
  astro: unknown
): {
  isValid: boolean;
  hasSaju: boolean;
  hasAstro: boolean;
  sajuFeatures: {
    fiveElements: boolean;
    advancedAnalysis: boolean;
    sinsal: boolean;
  };
  astroFeatures: {
    planets: boolean;
    aspects: boolean;
    houses: boolean;
    extraPoints: boolean;
  };
} {
  const hasSaju = validateSajuData(saju);
  const hasAstro = validateAstroData(astro);

  const sajuData = hasSaju ? saju as SajuData : undefined;
  const astroData = hasAstro ? astro as AstroData : undefined;

  return {
    isValid: hasSaju || hasAstro,
    hasSaju,
    hasAstro,
    sajuFeatures: {
      fiveElements: hasFiveElements(sajuData),
      advancedAnalysis: hasAdvancedAnalysis(sajuData),
      sinsal: hasSinsalData(sajuData),
    },
    astroFeatures: {
      planets: hasPlanetData(astroData),
      aspects: hasAspectData(astroData),
      houses: hasHouseData(astroData),
      extraPoints: hasExtraPoints(astroData),
    },
  };
}

"use client";

import React from "react";
import { logger } from "@/lib/logger";
import { useCreditModal } from "@/contexts/CreditModalContext";
import { CHAT_I18N, detectCrisis, type LangKey } from "../chat-i18n";
import {
  CHAT_TIMINGS,
  CHAT_LIMITS,
  type Message,
  type ConnectionStatus,
} from "../chat-constants";
import {
  generateMessageId,
  getConnectionStatus,
  getErrorMessage,
  streamProcessor,
} from "../chat-utils";
import { generateFollowUpQuestions } from "../chat-followups";
import type { ChatProps, ChatPayload } from "../chat-types";

interface UseChatApiOptions {
  sessionIdRef: React.MutableRefObject<string>;
  messages: Message[];
  setMessages: React.Dispatch<React.SetStateAction<Message[]>>;
  profile: ChatProps["profile"];
  theme: string;
  lang: LangKey;
  saju?: ChatProps["saju"];
  astro?: ChatProps["astro"];
  advancedAstro?: ChatProps["advancedAstro"];
  predictionContext?: ChatProps["predictionContext"];
  userContext?: ChatProps["userContext"];
  cvText: string;
  ragSessionId?: string;
  autoScroll: boolean;
  messagesEndRef: React.RefObject<HTMLDivElement | null>;
  onSaveMessage?: (userMsg: string, assistantMsg: string) => void;
  setNotice: React.Dispatch<React.SetStateAction<string | null>>;
}

interface UseChatApiReturn {
  loading: boolean;
  retryCount: number;
  connectionStatus: ConnectionStatus;
  usedFallback: boolean;
  followUpQuestions: string[];
  setFollowUpQuestions: React.Dispatch<React.SetStateAction<string[]>>;
  handleSend: (directText?: string) => Promise<void>;
  showCrisisModal: boolean;
  setShowCrisisModal: React.Dispatch<React.SetStateAction<boolean>>;
}

export function useChatApi({
  sessionIdRef,
  messages,
  setMessages,
  profile,
  theme,
  lang,
  saju,
  astro,
  advancedAstro,
  predictionContext,
  userContext,
  cvText,
  ragSessionId,
  autoScroll,
  messagesEndRef,
  onSaveMessage,
  setNotice,
}: UseChatApiOptions): UseChatApiReturn {
  const effectiveLang = lang === "ko" ? "ko" : "en";
  const tr = CHAT_I18N[effectiveLang];
  const { showDepleted } = useCreditModal();

  const [loading, setLoading] = React.useState(false);
  const [retryCount, setRetryCount] = React.useState(0);
  const [connectionStatus, setConnectionStatus] = React.useState<ConnectionStatus>("online");
  const [usedFallback, setUsedFallback] = React.useState(false);
  const [followUpQuestions, setFollowUpQuestions] = React.useState<string[]>([]);
  const [showCrisisModal, setShowCrisisModal] = React.useState(false);

  // Helper: Update last assistant message content
  const updateLastAssistantMessage = React.useCallback((content: string) => {
    setMessages((prev) => {
      const updated = [...prev];
      const lastIdx = updated.length - 1;
      if (lastIdx >= 0 && updated[lastIdx].role === "assistant") {
        updated[lastIdx] = { ...updated[lastIdx], content };
      }
      return updated;
    });
  }, [setMessages]);

  // Make API request with retry logic
  const makeRequest = React.useCallback(async (payload: ChatPayload, attempt: number = 0): Promise<Response> => {
    const startTime = performance.now();
    logger.debug(`[Chat] Request started (attempt ${attempt + 1})`);

    try {
      const res = await fetch("/api/destiny-map/chat-stream", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-session-id": ragSessionId || sessionIdRef.current,
        },
        body: JSON.stringify(payload),
        signal: AbortSignal.timeout(CHAT_TIMINGS.REQUEST_TIMEOUT),
      });

      const responseTime = performance.now() - startTime;
      logger.debug(`[Chat] Response received: ${responseTime.toFixed(0)}ms`);

      setConnectionStatus(getConnectionStatus(responseTime));

      if (!res.ok) {
        // 402 Payment Required - 크레딧 부족
        if (res.status === 402) {
          logger.warn("[Chat] Insufficient credits (402)");
          showDepleted();
          throw new Error("INSUFFICIENT_CREDITS");
        }

        if (res.status >= 500 && attempt < CHAT_LIMITS.MAX_RETRY_ATTEMPTS) {
          logger.warn(`[Chat] Server error ${res.status}, retrying...`);
          setRetryCount(attempt + 1);
          await new Promise(resolve => setTimeout(resolve, CHAT_TIMINGS.RETRY_BASE_DELAY * (attempt + 1)));
          return makeRequest(payload, attempt + 1);
        }
        throw new Error(await res.text());
      }
      if (!res.body) {throw new Error("No response body");}

      setRetryCount(0);
      return res;
    } catch (error: unknown) {
      const err = error as Error & { name?: string };
      if (err.name === "AbortError" || err.name === "TimeoutError") {
        setConnectionStatus("slow");
        if (attempt < CHAT_LIMITS.MAX_RETRY_ATTEMPTS) {
          logger.warn(`[Chat] Request timeout, retrying...`);
          setRetryCount(attempt + 1);
          await new Promise(resolve => setTimeout(resolve, CHAT_TIMINGS.RETRY_BASE_DELAY * (attempt + 1)));
          return makeRequest(payload, attempt + 1);
        }
        throw new Error("Request timeout. Please check your connection.");
      }
      throw error;
    }
  }, [ragSessionId, sessionIdRef]);

  // Process SSE stream response using StreamProcessor
  const processStream = React.useCallback(async (
    res: Response,
    _assistantMsgId: string,
    userText: string
  ): Promise<void> => {
    let lastScrollTime = 0;
    const result = await streamProcessor.process(res, {
      onChunk: (_accumulated, cleaned) => {
        // Update message in real-time as chunks arrive
        updateLastAssistantMessage(cleaned);
        // Auto-scroll during streaming (throttled to every 100ms)
        const now = Date.now();
        if (autoScroll && now - lastScrollTime > 100) {
          lastScrollTime = now;
          messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
        }
      },
      onError: () => {
        setNotice(tr.error);
      },
    });

    // Process final content
    if (!result.content) {
      updateLastAssistantMessage(tr.noResponse);
    } else {
      updateLastAssistantMessage(result.content);

      // Set follow-up questions
      if (result.followUps.length >= CHAT_LIMITS.FOLLOWUP_DISPLAY_COUNT) {
        setFollowUpQuestions(result.followUps.slice(0, CHAT_LIMITS.FOLLOWUP_DISPLAY_COUNT));
      } else {
        setFollowUpQuestions(generateFollowUpQuestions(theme, userText, lang, CHAT_LIMITS.FOLLOWUP_DISPLAY_COUNT));
      }

      if (onSaveMessage) {
        onSaveMessage(userText, result.content);
      }
    }
  }, [updateLastAssistantMessage, autoScroll, messagesEndRef, tr.error, tr.noResponse, theme, lang, onSaveMessage, setNotice]);

  // Main send handler
  const handleSend = React.useCallback(async (directText?: string) => {
    const text = directText;
    if (!text || loading) {return;}

    // Crisis detection
    if (detectCrisis(text, lang)) {
      setShowCrisisModal(true);
    }

    setFollowUpQuestions([]);

    const userMsgId = generateMessageId("user");
    const nextMessages: Message[] = [...messages, { role: "user", content: text, id: userMsgId }];
    setLoading(true);
    setMessages(nextMessages);
    setNotice(null);
    setUsedFallback(false);

    const payload: ChatPayload = {
      name: profile.name,
      birthDate: profile.birthDate,
      birthTime: profile.birthTime,
      latitude: profile.latitude,
      longitude: profile.longitude,
      gender: profile.gender,
      city: profile.city,
      theme,
      lang,
      messages: nextMessages,
      cvText,
      saju,
      astro,
      advancedAstro,
      predictionContext,
      userContext,
    };

    try {
      const res = await makeRequest(payload);

      if (res.headers.get("x-fallback") === "1") {
        setUsedFallback(true);
      }

      const assistantMsgId = generateMessageId("assistant");
      setMessages((prev) => [...prev, { role: "assistant", content: "", id: assistantMsgId }]);
      setLoading(false);

      await processStream(res, assistantMsgId, text);
    } catch (e: unknown) {
      logger.error("[Chat] send error:", e);
      setConnectionStatus("offline");

      const errorMessage = getErrorMessage(e as Error, lang, tr);

      setMessages((prev) => [...prev, {
        role: "assistant",
        content: errorMessage,
        id: generateMessageId("error"),
      }]);
      setLoading(false);
      setRetryCount(0);
    }
  }, [loading, messages, setMessages, setNotice, profile, theme, lang, cvText, saju, astro, advancedAstro, predictionContext, userContext, makeRequest, processStream, tr]);

  return {
    loading,
    retryCount,
    connectionStatus,
    usedFallback,
    followUpQuestions,
    setFollowUpQuestions,
    handleSend,
    showCrisisModal,
    setShowCrisisModal,
  };
}

/**
 * useInlineTarotState - State management for InlineTarotModal
 *
 * Consolidates 19 useState hooks into a single manageable state object
 */

import { useState, useEffect, useMemo, useCallback } from 'react';
import { tarotThemes } from '@/lib/Tarot/tarot-spreads-data';
import type { DrawnCard, Spread, CardInsight } from '@/lib/Tarot/tarot.types';

export type Step = 'concern' | 'spread-select' | 'card-draw' | 'interpreting' | 'result';

export interface TarotState {
  // Flow state
  step: Step;
  concern: string;
  selectedSpread: Spread | null;
  selectedCategory: string;

  // Card state
  drawnCards: DrawnCard[];
  revealedCount: number;
  isDrawing: boolean;

  // Interpretation state
  overallMessage: string;
  cardInsights: CardInsight[];
  guidance: string;
  affirmation: string;

  // Save state
  isSaved: boolean;
  isSaving: boolean;

  // AI state
  isAnalyzing: boolean;
  aiReason: string;
}

const initialState: TarotState = {
  step: 'concern',
  concern: '',
  selectedSpread: null,
  selectedCategory: '',
  drawnCards: [],
  revealedCount: 0,
  isDrawing: false,
  overallMessage: '',
  cardInsights: [],
  guidance: '',
  affirmation: '',
  isSaved: false,
  isSaving: false,
  isAnalyzing: false,
  aiReason: '',
};

// Theme mapping: destiny-map theme -> tarot category
const themeToCategory: Record<string, string> = {
  focus_love: 'love-relationships',
  love: 'love-relationships',
  focus_career: 'career-work',
  career: 'career-work',
  focus_energy: 'well-being-health',
  health: 'well-being-health',
  wealth: 'money-finance',
  life: 'general-insight',
  life_path: 'general-insight',
  chat: 'general-insight',
};

interface UseInlineTarotStateOptions {
  isOpen: boolean;
  initialConcern: string;
  theme: string;
}

export function useInlineTarotState({ isOpen, initialConcern, theme }: UseInlineTarotStateOptions) {
  const [state, setState] = useState<TarotState>(() => ({
    ...initialState,
    concern: initialConcern,
    selectedCategory: themeToCategory[theme] || 'general-insight',
  }));

  // Get recommended spreads based on theme
  const recommendedSpreads = useMemo(() => {
    const categoryId = themeToCategory[theme] || 'general-insight';
    const category = tarotThemes.find((t) => t.id === categoryId);
    if (!category) {return [];}
    return [...category.spreads].sort((a, b) => a.cardCount - b.cardCount);
  }, [theme]);

  // Update category when theme changes
  useEffect(() => {
    setState((prev) => ({
      ...prev,
      selectedCategory: themeToCategory[theme] || 'general-insight',
    }));
  }, [theme]);

  // Reset state when modal opens
  useEffect(() => {
    if (isOpen) {
      setState({
        ...initialState,
        concern: initialConcern,
        selectedCategory: themeToCategory[theme] || 'general-insight',
      });
    }
  }, [isOpen, initialConcern, theme]);

  // Action creators
  const actions = useMemo(() => ({
    setStep: (step: Step) => setState((prev) => ({ ...prev, step })),
    setConcern: (concern: string) => setState((prev) => ({ ...prev, concern })),
    setSelectedSpread: (spread: Spread | null) => setState((prev) => ({ ...prev, selectedSpread: spread })),
    setSelectedCategory: (category: string) => setState((prev) => ({ ...prev, selectedCategory: category })),
    setDrawnCards: (cards: DrawnCard[]) => setState((prev) => ({ ...prev, drawnCards: cards })),
    incrementRevealedCount: () => setState((prev) => ({ ...prev, revealedCount: prev.revealedCount + 1 })),
    setIsDrawing: (isDrawing: boolean) => setState((prev) => ({ ...prev, isDrawing })),
    setOverallMessage: (message: string | ((prev: string) => string)) =>
      setState((prev) => ({
        ...prev,
        overallMessage: typeof message === 'function' ? message(prev.overallMessage) : message,
      })),
    setCardInsights: (insights: CardInsight[]) => setState((prev) => ({ ...prev, cardInsights: insights })),
    setGuidance: (guidance: string | ((prev: string) => string)) =>
      setState((prev) => ({
        ...prev,
        guidance: typeof guidance === 'function' ? guidance(prev.guidance) : guidance,
      })),
    setAffirmation: (affirmation: string | ((prev: string) => string)) =>
      setState((prev) => ({
        ...prev,
        affirmation: typeof affirmation === 'function' ? affirmation(prev.affirmation) : affirmation,
      })),
    setIsSaved: (isSaved: boolean) => setState((prev) => ({ ...prev, isSaved })),
    setIsSaving: (isSaving: boolean) => setState((prev) => ({ ...prev, isSaving })),
    setIsAnalyzing: (isAnalyzing: boolean) => setState((prev) => ({ ...prev, isAnalyzing })),
    setAiReason: (reason: string) => setState((prev) => ({ ...prev, aiReason: reason })),

    // Composite actions
    resetForDrawAgain: () => setState((prev) => ({
      ...prev,
      drawnCards: [],
      revealedCount: 0,
      overallMessage: '',
      cardInsights: [],
      guidance: '',
      affirmation: '',
      step: 'card-draw',
    })),

    selectSpreadAndProceed: (spread: Spread, reason?: string) => setState((prev) => ({
      ...prev,
      selectedSpread: spread,
      aiReason: reason || '',
      step: 'card-draw',
    })),
  }), []);

  return {
    state,
    actions,
    recommendedSpreads,
    themeToCategory,
  };
}

export type UseInlineTarotStateReturn = ReturnType<typeof useInlineTarotState>;

import React from 'react'
import { motion } from 'framer-motion'
import { buildSignInUrl } from '@/lib/auth/signInUrl'
import { UnifiedBirthForm, type BirthInfo } from '@/components/common/BirthForm'
import styles from './BirthInputPhase.module.css'

interface BirthInputPhaseProps {
  locale: string
  status: string
  birthDate: string
  birthTime: string
  gender: 'M' | 'F'
  birthCity: string
  showTimeInput: boolean
  showCityInput: boolean
  onSubmit: (birthInfo: BirthInfo) => void
  onSkip: () => void
}

const pageTransitionVariants = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0, transition: { duration: 0.5 } },
  exit: { opacity: 0, y: -20, transition: { duration: 0.3 } },
}

export function BirthInputPhase({
  locale,
  status,
  birthDate,
  birthTime,
  gender,
  birthCity,
  showTimeInput,
  showCityInput,
  onSubmit,
  onSkip,
}: BirthInputPhaseProps) {
  const signInUrl = buildSignInUrl()
  const isKo = locale === 'ko'

  const handleFormSubmit = (birthInfo: BirthInfo) => {
    onSubmit(birthInfo)
  }

  return (
    <motion.div
      key="birth-input"
      variants={pageTransitionVariants}
      initial="initial"
      animate="animate"
      exit="exit"
      className={styles.phaseContainer}
    >
      <div className={styles.pageHeader}>
        <div className={styles.iconWrapper}>
          <span className={styles.icon}>🌙</span>
        </div>
        <h1 className={styles.pageTitle}>{isKo ? '꿈 해몽' : 'Dream Interpretation'}</h1>
        <p className={styles.pageSubtitle}>
          {isKo
            ? '당신의 꿈에 담긴 메시지를 해석해드립니다'
            : 'Discover the hidden messages in your dreams'}
        </p>
      </div>

      <div className={styles.birthFormCard}>
        {/* UnifiedBirthForm with Dream-specific configuration */}
        <UnifiedBirthForm
          onSubmit={handleFormSubmit}
          locale={locale as 'ko' | 'en'}
          initialData={{
            birthDate,
            birthTime,
            gender,
            birthCity,
          }}
          includeProfileLoader={true}
          includeCity={false}
          includeCityToggle={true}
          allowTimeUnknown={true}
          genderFormat="short"
          submitButtonText={isKo ? '다음으로' : 'Continue'}
          submitButtonIcon="✨"
          showHeader={true}
          headerIcon="🎂"
          headerTitle={isKo ? '생년월일을 입력해주세요' : 'Enter Your Birth Info'}
          headerSubtitle={
            isKo ? '정확한 해석을 위해 필요한 정보입니다' : 'Optional, but improves accuracy'
          }
        />

        {/* Skip button - Dream-specific feature */}
        <div className={styles.skipBirthRow}>
          <button type="button" className={styles.skipBirthButton} onClick={onSkip}>
            {isKo ? '생년월일 없이 진행' : 'Skip for now'}
          </button>
          <p className={styles.skipBirthHint}>
            {isKo
              ? '생년월일 없이도 기본적인 해석은 가능합니다'
              : 'You can continue without birth info, but accuracy may drop.'}
          </p>
        </div>

        {/* Login hint - Dream-specific feature */}
        {status === 'unauthenticated' && (
          <div className={styles.loginHint}>
            <p>
              {isKo
                ? '로그인하면 정보가 저장되어 더 편리하게 이용할 수 있어요'
                : 'Log in to save your info for a better experience'}
            </p>
            <a href={signInUrl} className={styles.loginLink}>
              {isKo ? '로그인하기' : 'Log in'}
            </a>
          </div>
        )}
      </div>
    </motion.div>
  )
}

import { DestinyMapDetail } from './DestinyMapDetail';
import { CalendarDetail } from './CalendarDetail';
import { TarotDetail } from './TarotDetail';
import { IChingDetail } from './IChingDetail';
import { NumerologyDetail } from './NumerologyDetail';
import { ICPDetail } from './ICPDetail';
import { CompatibilityDetail } from './CompatibilityDetail';
import { MatrixDetail } from './MatrixDetail';
import type {
  ServiceRecord,
  DestinyMapContent,
  CalendarContent,
  TarotContent,
  IChingContent,
  NumerologyContent,
  ICPContent,
  PersonalityCompatibilityContent,
  DestinyMatrixContent,
} from '@/app/myjourney/history/lib/types';
import styles from './DetailModal.module.css';

type DetailContent =
  | DestinyMapContent
  | CalendarContent
  | TarotContent
  | IChingContent
  | NumerologyContent
  | ICPContent
  | PersonalityCompatibilityContent
  | DestinyMatrixContent;

type DetailModalWrapperProps = {
  record: ServiceRecord | null;
  destinyMapDetail: DestinyMapContent | null;
  calendarDetail: CalendarContent | null;
  tarotDetail: TarotContent | null;
  ichingDetail: IChingContent | null;
  numerologyDetail: NumerologyContent | null;
  icpDetail: ICPContent | null;
  compatibilityDetail: PersonalityCompatibilityContent | null;
  matrixDetail: DestinyMatrixContent | null;
  loading: boolean;
  onClose: () => void;
};

export function DetailModalWrapper({
  record,
  destinyMapDetail,
  calendarDetail,
  tarotDetail,
  ichingDetail,
  numerologyDetail,
  icpDetail,
  compatibilityDetail,
  matrixDetail,
  loading,
  onClose,
}: DetailModalWrapperProps) {
  if (!record) {return null;}

  return (
    <div className={styles.modalOverlay} onClick={onClose}>
      <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <button className={styles.closeButton} onClick={onClose} aria-label="Close">
          ✕
        </button>

        {loading ? (
          <div className={styles.modalLoading}>
            <div className={styles.spinner}></div>
            <p>불러오는 중...</p>
          </div>
        ) : destinyMapDetail ? (
          <DestinyMapDetail detail={destinyMapDetail} />
        ) : calendarDetail ? (
          <CalendarDetail detail={calendarDetail} />
        ) : tarotDetail ? (
          <TarotDetail detail={tarotDetail} recordDate={record.date} />
        ) : ichingDetail ? (
          <IChingDetail detail={ichingDetail} />
        ) : numerologyDetail ? (
          <NumerologyDetail detail={numerologyDetail} />
        ) : icpDetail ? (
          <ICPDetail detail={icpDetail} />
        ) : compatibilityDetail ? (
          <CompatibilityDetail detail={compatibilityDetail} />
        ) : matrixDetail ? (
          <MatrixDetail detail={matrixDetail} />
        ) : (
          <div className={styles.modalError}>
            <p>Failed to load reading details</p>
          </div>
        )}
