'use client'

import React, { useState, useRef, useEffect, useCallback, useMemo, memo } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import styles from './AdvisorChat.module.css'
import { logger } from '@/lib/logger'
import { Spinner } from '@/components/ui'

interface Message {
  id: string
  role: 'user' | 'assistant'
  content: string
  timestamp: Date
}

interface AdvisorChatProps {
  // 예측 컨텍스트
  predictionContext: {
    question: string
    eventType: string
    results: Array<{
      startDate: string
      endDate: string
      score: number
      grade: string
      reasons: string[]
    }>
    birthDate: string
    gender: 'M' | 'F'
  }
  locale?: 'ko' | 'en'
  onClose?: () => void
}

// 메시지 컴포넌트 메모이제이션
const MessageItem = memo(
  ({ message, styles }: { message: Message; styles: Record<string, string> }) => (
    <div key={message.id} className={`${styles.message} ${styles[message.role]}`}>
      {message.role === 'assistant' && <span className={styles.avatar}>🔮</span>}
      <div className={styles.messageContent}>
        <p>{message.content}</p>
      </div>
    </div>
  )
)
MessageItem.displayName = 'MessageItem'

// 로딩 인디케이터 메모이제이션
const LoadingIndicator = memo(({ styles }: { styles: Record<string, string> }) => (
  <div className={`${styles.message} ${styles.assistant}`}>
    <span className={styles.avatar}>🔮</span>
    <div className={styles.messageContent}>
      <div className={styles.typing}>
        <span></span>
        <span></span>
        <span></span>
      </div>
    </div>
  </div>
))
LoadingIndicator.displayName = 'LoadingIndicator'

function AdvisorChatComponent({ predictionContext, locale = 'ko', onClose }: AdvisorChatProps) {
  const [messages, setMessages] = useState<Message[]>([])
  const [input, setInput] = useState('')
  const [isLoading, setIsLoading] = useState(false)
  const [isExpanded, setIsExpanded] = useState(false)
  const messagesEndRef = useRef<HTMLDivElement>(null)
  const inputRef = useRef<HTMLInputElement>(null)

  // 초기 인사 메시지 메모이제이션
  const greetingMessage = useMemo(
    () => ({
      id: 'greeting',
      role: 'assistant' as const,
      content:
        locale === 'ko'
          ? `안녕하세요! 예측 결과에 대해 궁금한 점이 있으시면 편하게 물어보세요. "${predictionContext.question}"에 대한 분석 결과를 바탕으로 더 자세한 조언을 드릴 수 있어요.`
          : `Hello! Feel free to ask me anything about your prediction results. I can provide more detailed advice based on your question "${predictionContext.question}".`,
      timestamp: new Date(),
    }),
    [locale, predictionContext.question]
  )

  // 초기 인사 메시지
  useEffect(() => {
    if (messages.length === 0) {
      setMessages([greetingMessage])
    }
  }, [greetingMessage, messages.length])

  // 스크롤 자동 이동
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' })
  }, [messages])

  // 메시지 전송
  const sendMessage = useCallback(
    async (overrideInput?: string) => {
      const messageText = (overrideInput ?? input).trim()
      if (!messageText || isLoading) {
        return
      }

      const userMessage: Message = {
        id: `user-${Date.now()}`,
        role: 'user',
        content: messageText,
        timestamp: new Date(),
      }

      setMessages((prev) => [...prev, userMessage])
      setInput('')
      setIsLoading(true)

      try {
        const response = await fetch('/api/life-prediction/advisor-chat', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            message: userMessage.content,
            context: predictionContext,
            history: messages.slice(-6).map((m) => ({
              role: m.role,
              content: m.content,
            })),
            locale,
          }),
        })

        const data = await response.json()

        if (data.success && data.reply) {
          const assistantMessage: Message = {
            id: `assistant-${Date.now()}`,
            role: 'assistant',
            content: data.reply,
            timestamp: new Date(),
          }
          setMessages((prev) => [...prev, assistantMessage])
        } else {
          throw new Error(data.error || 'Failed to get response')
        }
      } catch (error) {
        logger.error('[AdvisorChat] Chat error:', error)

        // 에러 메시지 파싱
        let errorContent =
          locale === 'ko'
            ? '죄송합니다. 응답을 가져오는 중 오류가 발생했습니다. 다시 시도해주세요.'
            : 'Sorry, there was an error getting a response. Please try again.'

        // 크레딧 부족 에러 처리
        if (error instanceof Error && error.message.includes('credit')) {
          errorContent =
            locale === 'ko'
              ? '🎫 AI 상담을 이용하려면 크레딧이 필요합니다. 로그인 후 크레딧을 충전해주세요.'
              : '🎫 Credits required to use AI counseling. Please log in and recharge your credits.'
        }
        // 인증 에러 처리
        else if (
          error instanceof Error &&
          (error.message.includes('auth') || error.message.includes('401'))
        ) {
          errorContent =
            locale === 'ko'
              ? '🔐 AI 상담을 이용하려면 로그인이 필요합니다.'
              : '🔐 Please log in to use AI counseling.'
        }

        const errorMessage: Message = {
          id: `error-${Date.now()}`,
          role: 'assistant',
          content: errorContent,
          timestamp: new Date(),
        }
        setMessages((prev) => [...prev, errorMessage])
      } finally {
        setIsLoading(false)
      }
    },
    [input, isLoading, messages, predictionContext, locale]
  )

  // 빠른 질문 버튼 메모이제이션
  const quickQuestions = useMemo(
    () =>
      locale === 'ko'
        ? ['이 시기에 주의할 점은?', '더 좋은 시기가 있을까요?', '구체적으로 어떻게 준비해야 해요?']
        : [
            'What should I be careful about?',
            'Is there a better timing?',
            'How should I prepare specifically?',
          ],
    [locale]
  )

  const handleQuickQuestion = useCallback(
    (question: string) => {
      setInput(question)
      sendMessage(question)
    },
    [sendMessage]
  )

  // 헤더 토글 핸들러 메모이제이션
  const handleToggleExpanded = useCallback(() => {
    setIsExpanded((prev) => !prev)
  }, [])

  const handleKeyDownToggle = useCallback((e: React.KeyboardEvent) => {
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault()
      setIsExpanded((prev) => !prev)
    }
  }, [])

  return (
    <motion.div
      className={`${styles.container} ${isExpanded ? styles.expanded : ''}`}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.3 }}
    >
      {/* 헤더 */}
      <div
        className={styles.header}
        role="button"
        tabIndex={0}
        onClick={handleToggleExpanded}
        onKeyDown={handleKeyDownToggle}
        aria-expanded={isExpanded}
        aria-label={
          isExpanded
            ? locale === 'ko'
              ? 'AI 상담사 접기'
              : 'Collapse advisor chat'
            : locale === 'ko'
              ? 'AI 상담사 펼치기'
              : 'Expand advisor chat'
        }
      >
        <div className={styles.headerLeft}>
          <span className={styles.advisorIcon}>🔮</span>
          <div className={styles.headerText}>
            <h3>{locale === 'ko' ? 'AI 운세 상담사' : 'AI Fortune Advisor'}</h3>
            <p>{locale === 'ko' ? '결과에 대해 더 물어보세요' : 'Ask more about your results'}</p>
          </div>
        </div>
        <button className={styles.expandBtn} aria-hidden="true" tabIndex={-1}>
          {isExpanded ? '▼' : '▲'}
        </button>
      </div>

      <AnimatePresence>
        {isExpanded && (
          <motion.div
            className={styles.chatArea}
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            transition={{ duration: 0.3 }}
          >
            {/* 메시지 영역 */}
            <div className={styles.messages}>
              {messages.map((message) => (
                <MessageItem key={message.id} message={message} styles={styles} />
              ))}

              {isLoading && <LoadingIndicator styles={styles} />}

              <div ref={messagesEndRef} />
            </div>

            {/* 빠른 질문 */}
            {messages.length <= 2 && (
              <div className={styles.quickQuestions}>
                {quickQuestions.map((q, idx) => (
                  <button
                    key={idx}
                    className={styles.quickBtn}
                    onClick={() => handleQuickQuestion(q)}
                    disabled={isLoading}
                  >
                    {q}
                  </button>
                ))}
              </div>
            )}

            {/* 입력 영역 */}
            <div className={styles.inputArea}>
              <input
                ref={inputRef}
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
                placeholder={locale === 'ko' ? '궁금한 점을 물어보세요...' : 'Ask me anything...'}
                disabled={isLoading}
                className={styles.input}
              />
              <button
                onClick={() => sendMessage()}
                disabled={!input.trim() || isLoading}
                className={styles.sendBtn}
              >
                {isLoading ? <Spinner size="sm" variant="white" label="답변 생성 중..." /> : '→'}
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  )
}

// 메모이제이션된 컴포넌트 export
export const AdvisorChat = memo(AdvisorChatComponent)
export default AdvisorChat

// components/numerology/LuckyNumbers.tsx
'use client';

import { useState, useMemo, useCallback, memo } from 'react';
import { motion } from 'framer-motion';
import DateTimePicker from '@/components/ui/DateTimePicker';
import { useI18n } from '@/i18n/I18nProvider';
import styles from './LuckyNumbers.module.css';

interface LuckyNumbersResult {
  lifePath: number;
  expression?: number;
  soulUrge?: number;
  personality?: number;
  luckyNumbers: number[];
  interpretation: string;
}

export default function LuckyNumbers() {
  const { t, locale } = useI18n();
  const [birthDate, setBirthDate] = useState('');
  const [name, setName] = useState('');
  const [count, setCount] = useState(10);
  const [isLoading, setIsLoading] = useState(false);
  const [result, setResult] = useState<LuckyNumbersResult | null>(null);
  const [error, setError] = useState<string | null>(null);
  const apiLocale = locale === 'ko' ? 'ko' : 'en';

  const generateLuckyNumbers = useCallback((lifePath: number, expression?: number, soulUrge?: number, personality?: number, targetCount: number = 10) => {
    const numbers = new Set<number>();

    // Core numbers
    numbers.add(lifePath);
    if (expression) {numbers.add(expression);}
    if (soulUrge) {numbers.add(soulUrge);}
    if (personality) {numbers.add(personality);}

    // Multiples and combinations - 최적화: O(n²) → O(n)
    const coreNums = [lifePath, expression, soulUrge, personality].filter(n => n) as number[];

    // First pass: Add multiples (O(n))
    coreNums.forEach(num => {
      for (let i = 1; i <= 4; i++) {
        const multiple = num * i;
        if (multiple <= 45) {numbers.add(multiple);}
      }
    });

    // Second pass: Add sums without nested loop (O(n))
    for (let i = 0; i < coreNums.length; i++) {
      for (let j = i; j < coreNums.length; j++) {
        const sum = coreNums[i] + coreNums[j];
        if (sum <= 45 && sum > 0) {numbers.add(sum);}
      }
    }

    // Ensure we have enough numbers
    const today = new Date();
    const dayOfYear = Math.floor((today.getTime() - new Date(today.getFullYear(), 0, 0).getTime()) / 86400000);

    while (numbers.size < targetCount + 5) {
      const derived = ((lifePath + dayOfYear + numbers.size) % 45) + 1;
      numbers.add(derived);
    }

    return Array.from(numbers).sort((a, b) => a - b).slice(0, targetCount);
  }, []);

  const handleGenerate = async (e?: React.FormEvent) => {
    e?.preventDefault();

    if (!birthDate) {
      setError(t('numerology.lucky.errorBirthdate', 'Please enter your birth date.'));
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/numerology', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'analyze',
          birthDate,
          englishName: name || undefined,
          locale: apiLocale
        }),
      });

      if (!response.ok) {
        throw new Error(t('numerology.lucky.errorRequest', 'Failed to analyze.'));
      }

      const data = await response.json();

      const luckyNums = generateLuckyNumbers(
        data.lifePath?.number,
        data.expression?.number,
        data.soulUrge?.number,
        data.personality?.number,
        count
      );

      setResult({
        lifePath: data.lifePath?.number,
        expression: data.expression?.number,
        soulUrge: data.soulUrge?.number,
        personality: data.personality?.number,
        luckyNumbers: luckyNums,
        interpretation: t('numerology.lucky.interpretation', `Lucky numbers generated based on your Life Path ${data.lifePath?.number}. These numbers resonate with your energy.`)
      });

    } catch (err) {
      setError(err instanceof Error ? err.message : t('numerology.lucky.errorGeneral', 'An error occurred. Please try again.'));
    } finally {
      setIsLoading(false);
    }
  };

  const copyToClipboard = useCallback((numbers: number[]) => {
    const text = numbers.join(', ');
    navigator.clipboard.writeText(text);
    // Show toast notification
    const toast = document.createElement('div');
    toast.textContent = t('numerology.lucky.copied', 'Numbers copied!');
    toast.style.cssText = `
      position: fixed;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      background: rgba(0, 0, 0, 0.9);
      color: white;
      padding: 16px 24px;
      border-radius: 8px;
      z-index: 9999;
      animation: fadeOut 2s ease-out forwards;
    `;
    document.body.appendChild(toast);
    setTimeout(() => toast.remove(), 2000);
  }, [t]);

  return (
    <div className={styles.container}>
      <form onSubmit={handleGenerate} className={styles.form}>
        <div className={styles.inputGroup}>
          <DateTimePicker
            value={birthDate}
            onChange={setBirthDate}
            label={t('numerology.birthdateLabel', 'Birthdate')}
            required
            locale={apiLocale}
          />
        </div>

        <div className={styles.inputGroup}>
          <label htmlFor="name" className={styles.label}>
            {t('numerology.lucky.nameLabel', 'Name (English, Optional)')}
          </label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder={locale === 'ko' ? '홍길동' : 'John Smith'}
            className={styles.input}
          />
          <span className={styles.hint}>{t('numerology.lucky.nameHint', 'Enter your name for more accurate lucky numbers')}</span>
        </div>

        <div className={styles.inputGroup}>
          <label htmlFor="count" className={styles.label}>
            {t('numerology.lucky.countLabel', 'Number of lucky numbers to generate')}
          </label>
          <input
            type="number"
            id="count"
            value={count}
            onChange={(e) => setCount(Math.min(15, Math.max(1, parseInt(e.target.value) || 10)))}
            min="1"
            max="15"
            className={styles.input}
          />
          <span className={styles.hint}>{t('numerology.lucky.countHint', 'Select up to 15 numbers')}</span>
        </div>

        {error && (
          <div className={styles.error}>
            {error}
          </div>
        )}

        <motion.button
          type="submit"
          className={styles.submitBtn}
          disabled={isLoading}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          {isLoading ? (
            <span className={styles.loading}>{t('common.loading', 'Loading...')}</span>
          ) : (
            t('numerology.lucky.generateButton', 'Generate Lucky Numbers')
          )}
        </motion.button>
      </form>

      {result && (
        <motion.div
          className={styles.results}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <div className={styles.resultsHeader}>
            <h3 className={styles.resultsTitle}>✨ {t('numerology.lucky.yourNumbers', 'Your Lucky Numbers')}</h3>
            <p className={styles.interpretation}>{result.interpretation}</p>
          </div>

          {/* Core Numbers Display */}
          <div className={styles.coreNumbers}>
            <div className={styles.coreNumberItem}>
              <span className={styles.coreLabel}>{t('numerologyDetail.lifePath', 'Life Path')}</span>
              <span className={styles.coreValue}>{result.lifePath}</span>
            </div>
            {result.expression && (
              <div className={styles.coreNumberItem}>
                <span className={styles.coreLabel}>{t('numerologyDetail.expression', 'Expression')}</span>
                <span className={styles.coreValue}>{result.expression}</span>
              </div>
            )}
            {result.soulUrge && (
              <div className={styles.coreNumberItem}>
                <span className={styles.coreLabel}>{t('numerologyDetail.soulUrge', 'Soul Urge')}</span>
                <span className={styles.coreValue}>{result.soulUrge}</span>
              </div>
            )}
            {result.personality && (
              <div className={styles.coreNumberItem}>
                <span className={styles.coreLabel}>{t('numerologyDetail.personality', 'Personality')}</span>
                <span className={styles.coreValue}>{result.personality}</span>
              </div>
            )}
          </div>

          {/* Lucky Numbers Grid */}
          <div className={styles.numbersGrid}>
            {result.luckyNumbers.map((num, index) => (
              <motion.div
                key={num}
                className={styles.numberBall}
                initial={{ opacity: 0, scale: 0 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: index * 0.1, type: 'spring' }}
              >
                {num}
              </motion.div>
            ))}
          </div>

          {/* Action Buttons */}
          <div className={styles.actions}>
            <motion.button
              className={styles.actionBtn}
              onClick={() => copyToClipboard(result.luckyNumbers)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              📋 {t('numerology.lucky.copy', 'Copy Numbers')}
            </motion.button>
            <motion.button
              className={styles.actionBtn}
              onClick={handleGenerate}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
