import { useState, useEffect } from "react";
import type { Message } from "../types";
import { logger } from "@/lib/logger";

const isDev = process.env.NODE_ENV === 'development';
const devError = (...args: unknown[]) => logger.error('[useMessages]', ...args);

/**
 * Hook for managing chat messages with localStorage persistence
 *
 * @param storageKey - Key for localStorage
 * @returns Messages state and setter
 */
export function useMessages(storageKey: string): [Message[], React.Dispatch<React.SetStateAction<Message[]>>] {
  // Load messages from localStorage on mount
  const [messages, setMessages] = useState<Message[]>(() => {
    if (typeof window === 'undefined') {return [];}
    try {
      const stored = localStorage.getItem(storageKey);
      return stored ? JSON.parse(stored) : [];
    } catch {
      return [];
    }
  });

  // Save messages to localStorage whenever they change
  useEffect(() => {
    if (typeof window !== 'undefined' && messages.length > 0) {
      try {
        localStorage.setItem(storageKey, JSON.stringify(messages));
      } catch (e) {
        devError('Failed to save messages:', e);
      }
    }
  }, [messages, storageKey]);

  return [messages, setMessages];
}

/**
 * Question engine hook for generating suggested questions
 * Extracted from TarotChat.tsx lines 209-323, 406-423
 */

import { useMemo, useCallback, useState } from "react";
import type { DrawnCard } from "@/lib/Tarot/tarot.types";
import type { Message, ReadingResponse } from "../types";
import { SPREAD_QUESTIONS, CATEGORY_QUESTIONS, generateDynamicQuestions } from "../data";
import type { LangKey } from "../data";
import { generateTopicBasedQuestions } from "../utils/topicDetector";
import { generateContextualQuestions } from "../utils/contextAnalyzer";

/**
 * Hook for managing suggested questions based on context, cards, and conversation
 *
 * @param readingResult - The reading result
 * @param categoryName - Category name
 * @param spreadId - Spread ID
 * @param userTopic - User's topic
 * @param language - Language code
 * @param messages - Current conversation messages
 * @returns Question engine state and functions
 */
export function useQuestionEngine(
  readingResult: ReadingResponse,
  categoryName: string,
  spreadId: string,
  userTopic: string | undefined,
  language: LangKey,
  messages: Message[]
) {
  const [usedQuestionIndices, setUsedQuestionIndices] = useState<Set<number>>(new Set());

  // Generate dynamic questions based on actual drawn cards
  const dynamicQuestions = useMemo(
    () => generateDynamicQuestions(readingResult.drawnCards, language),
    [readingResult.drawnCards, language]
  );

  // Generate topic-based questions from userTopic
  const topicQuestions = useMemo(
    () => generateTopicBasedQuestions(userTopic, language),
    [userTopic, language]
  );

  // Get spread/category questions
  const spreadQuestions = useMemo(
    () =>
      SPREAD_QUESTIONS[spreadId]?.[language] ||
      CATEGORY_QUESTIONS[categoryName]?.[language] ||
      CATEGORY_QUESTIONS.default[language],
    [spreadId, categoryName, language]
  );

  // Combine all suggested questions in priority order
  const allSuggestedQuestions = useMemo(
    () =>
      [
        ...topicQuestions.slice(0, 3), // Top 3 topic-specific questions (highest priority)
        ...dynamicQuestions.slice(0, 4), // Top 4 card-specific questions
        ...spreadQuestions.slice(0, 3) // Top 3 spread-specific questions
      ].slice(0, 10), // Max 10 total
    [topicQuestions, dynamicQuestions, spreadQuestions]
  );

  // Generate contextual questions based on last response
  const getContextualQuestions = useCallback(
    (lastResponse: string): string[] => {
      return generateContextualQuestions(lastResponse, readingResult.drawnCards, language);
    },
    [readingResult.drawnCards, language]
  );

  // Get next 2 questions - prioritize contextual questions from last response
  const getNextSuggestions = useCallback((): string[] => {
    const lastAssistantMessage = messages.filter(m => m.role === 'assistant').pop();

    if (lastAssistantMessage) {
      const contextual = getContextualQuestions(lastAssistantMessage.content);
      if (contextual.length > 0) {
        // Mix contextual with unused general questions
        const available = allSuggestedQuestions.filter((_, idx) => !usedQuestionIndices.has(idx));
        const mixed = [...contextual.slice(0, 2), ...available.slice(0, 1)];
        return mixed.slice(0, 2);
      }
    }

    // Fallback to general questions
    const available = allSuggestedQuestions.filter((_, idx) => !usedQuestionIndices.has(idx));
    return available.slice(0, 2);
  }, [messages, getContextualQuestions, allSuggestedQuestions, usedQuestionIndices]);

  /**
   * Mark a question as used
   */
  const markQuestionUsed = useCallback((question: string) => {
    const suggestionIndex = allSuggestedQuestions.indexOf(question);
    if (suggestionIndex !== -1) {
      setUsedQuestionIndices(prev => new Set([...prev, suggestionIndex]));
    }
  }, [allSuggestedQuestions]);

  return {
    allSuggestedQuestions,
    getNextSuggestions,
    markQuestionUsed
  };
}

/**
 * Tarot streaming API hook
 * Extracted from TarotChat.tsx lines 520-710
 */

import { useState, useCallback } from "react";
import type { Message } from "../types";
import type { PersistedContext } from "../types/storage";
import { apiFetch } from "@/lib/api";
import { logger } from "@/lib/logger";
import { useCreditModal } from "@/contexts/CreditModalContext";
import { parseSSEStream, isSSEStream } from "../utils/streamingParser";
import { buildContextWithNewCard } from "../utils/contextBuilder";

const isDev = process.env.NODE_ENV === 'development';
const devLog = (...args: unknown[]) => isDev && logger.debug('[useTarotStreaming]', ...args);
const devWarn = (...args: unknown[]) => isDev && logger.warn('[useTarotStreaming]', ...args);
const devError = (...args: unknown[]) => logger.error('[useTarotStreaming]', ...args);

interface UseTarotStreamingParams {
  messages: Message[];
  setMessages: React.Dispatch<React.SetStateAction<Message[]>>;
  buildContext: () => PersistedContext;
  language: 'ko' | 'en';
  categoryName: string;
  counselorId?: string;
  counselorStyle?: string;
  errorMessage: string;
  onLoadingStart: () => void;
  onLoadingStop: () => void;
}

/**
 * Hook for handling tarot chat streaming with card drawing
 *
 * @param params - Hook parameters
 * @returns Streaming state and send function
 */
export function useTarotStreaming(params: UseTarotStreamingParams) {
  const {
    messages,
    setMessages,
    buildContext,
    language,
    categoryName,
    counselorId,
    counselorStyle,
    errorMessage,
    onLoadingStart,
    onLoadingStop
  } = params;

  const { showDepleted } = useCreditModal();
  const [loading, setLoading] = useState(false);
  const [streamingContent, setStreamingContent] = useState<string>("");
  const [usedFallback, setUsedFallback] = useState(false);

  /**
   * Send a message and handle streaming response
   */
  const handleSend = useCallback(async (messageText: string) => {
    if (!messageText || loading) {return;}
    setUsedFallback(false);

    const nextMessages: Message[] = [...messages, { role: "user", content: messageText }];
    setLoading(true);
    setMessages(nextMessages);
    setStreamingContent("");
    onLoadingStart();

    // Log counselor info for debugging
    devLog('Sending request with counselor:', counselorId, 'style:', counselorStyle);

    try {
      // Step 1: Draw a new card for this question (타로집 스타일)
      let newDrawnCard = null;
      try {
        const drawResponse = await apiFetch("/api/tarot", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            categoryId: categoryName,
            spreadId: "quick-reading", // 1장 뽑기
            cardCount: 1,
            userTopic: messageText
          })
        });

        if (!drawResponse.ok && drawResponse.status === 402) {
          devWarn("Insufficient credits when drawing card (402)");
          showDepleted();
          throw new Error("INSUFFICIENT_CREDITS");
        }

        if (drawResponse.ok) {
          const drawData = await drawResponse.json();
          if (drawData.drawnCards && drawData.drawnCards.length > 0) {
            newDrawnCard = drawData.drawnCards[0];
            devLog('Drew new card for question:', newDrawnCard.card.name);
          }
        }
      } catch (drawErr) {
        // 크레딧 부족 에러는 상위로 전파
        if (drawErr instanceof Error && drawErr.message === "INSUFFICIENT_CREDITS") {
          throw drawErr;
        }
        devWarn('Failed to draw new card, using existing context:', drawErr);
      }

      // Build context with new card added to cards array (backend processes cards array)
      const baseContext = buildContext();
      const contextWithNewCard = newDrawnCard
        ? buildContextWithNewCard(baseContext, newDrawnCard, language)
        : baseContext;

      // Step 2: Try streaming endpoint with the new card context
      const response = await apiFetch("/api/tarot/chat/stream", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          messages: nextMessages,
          context: contextWithNewCard,
          language,
          counselor_id: counselorId,
          counselor_style: counselorStyle
        })
      });

      if (!response.ok) {
        // 402 Payment Required - 크레딧 부족
        if (response.status === 402) {
          devWarn("Insufficient credits (402)");
          showDepleted();
          throw new Error("INSUFFICIENT_CREDITS");
        }
        throw new Error(`Stream failed: ${response.status}`);
      }

      if (response.headers.get("x-fallback") === "1") {
        setUsedFallback(true);
      }

      if (isSSEStream(response)) {
        // Handle SSE streaming
        const reader = response.body!.getReader();

        // Stop loading messages once streaming starts
        onLoadingStop();

        const accumulatedContent = await parseSSEStream(reader, (content) => {
          setStreamingContent(content);
        });

        // If no done signal received, still add the message
        if (accumulatedContent && !messages.find(m => m.content === accumulatedContent)) {
          setMessages(prev => {
            // Check if last message is already this content
            const last = prev[prev.length - 1];
            if (last?.role === "assistant" && last?.content === accumulatedContent) {
              return prev;
            }
            return [...prev, { role: "assistant", content: accumulatedContent }];
          });
          setStreamingContent("");
        }
      } else {
        // Fallback to JSON response
        onLoadingStop();
        const data = await response.json();
        setMessages(prev => [...prev, {
          role: "assistant",
          content: data.reply || errorMessage
        }]);
      }
    } catch (error) {
      devError("Streaming error, falling back:", error);
      onLoadingStop();

      // Fallback to non-streaming endpoint
      try {
        const fallbackResponse = await apiFetch("/api/tarot/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            messages: nextMessages,
            context: buildContext(),
            language,
            counselor_id: counselorId,
            counselor_style: counselorStyle
          })
        });

        if (fallbackResponse.ok) {
          const data = await fallbackResponse.json();
          setUsedFallback(true);
          setMessages(prev => [...prev, {
            role: "assistant",
            content: data.reply || errorMessage
          }]);
        } else {
          throw new Error("Fallback also failed");
        }
      } catch (fallbackError) {
        devError("Fallback error:", fallbackError);
        setUsedFallback(true);
        setMessages(prev => [...prev, {
          role: "assistant",
          content: errorMessage
        }]);
      }
    } finally {
      setLoading(false);
      onLoadingStop();
      setStreamingContent("");
    }
  }, [
    loading,
    messages,
    setMessages,
    buildContext,
    language,
    categoryName,
    counselorId,
    counselorStyle,
    errorMessage,
    onLoadingStart,
    onLoadingStop
  ]);

  return {
    loading,
    streamingContent,
    usedFallback,
    handleSend
  };
}

/**
 * Context building and merging utilities
 * Extracted from TarotChat.tsx lines 463-498
 */

import type { DrawnCard } from "@/lib/Tarot/tarot.types";
import type { InterpretationResult, ReadingResponse } from "../types";
import type { PersistedContext, PersistedCard } from "../types/storage";

/**
 * Build context from reading result and interpretation
 *
 * @param readingResult - The tarot reading result
 * @param interpretation - AI interpretation (optional)
 * @param categoryName - Category name
 * @param persistedContext - Previously persisted context for merging
 * @param language - Language code for card data
 * @returns Built context object
 */
export function buildContext(
  readingResult: ReadingResponse,
  interpretation: InterpretationResult | null,
  categoryName: string,
  persistedContext: PersistedContext | null,
  language: 'ko' | 'en'
): PersistedContext {
  const cards = readingResult.drawnCards.map((dc, idx) => ({
    position: readingResult.spread.positions[idx]?.title || `Card ${idx + 1}`,
    name: dc.card.name,
    is_reversed: dc.isReversed, // snake_case for backend compatibility
    meaning: dc.isReversed ? dc.card.reversed.meaning : dc.card.upright.meaning,
    keywords: dc.isReversed
      ? (dc.card.reversed.keywordsKo || dc.card.reversed.keywords)
      : (dc.card.upright.keywordsKo || dc.card.upright.keywords)
  }));

  const base: PersistedContext = {
    spread_title: readingResult.spread.title,
    category: categoryName,
    cards,
    overall_message: interpretation?.overall_message || "",
    guidance: interpretation?.guidance || ""
  };

  // If we have a persisted context with cards, merge to keep continuity
  const persistedCards = persistedContext?.cards;
  if (persistedCards && persistedCards.length) {
    const merged = [...persistedCards];
    for (const c of cards) {
      const dup = merged.find(
        (p) =>
          p.name === c.name &&
          (p.position === c.position || !p.position || !c.position)
      );
      if (!dup) {merged.push(c);}
    }
    return { ...base, cards: merged };
  }

  return base;
}

/**
 * Build context with a newly drawn card added to the existing context
 *
 * @param baseContext - Base context to extend
 * @param newCard - Newly drawn card
 * @param language - Language code for card data
 * @returns Context with new card prepended
 */
export function buildContextWithNewCard(
  baseContext: PersistedContext,
  newCard: DrawnCard,
  language: 'ko' | 'en'
): PersistedContext {
  const newCardData: PersistedCard = {
    position: language === 'ko' ? '이번 질문에 대한 카드' : 'Card for this question',
    name: language === 'ko' ? (newCard.card.nameKo || newCard.card.name) : newCard.card.name,
    is_reversed: newCard.isReversed,
    meaning: newCard.isReversed
      ? (language === 'ko' ? newCard.card.reversed.meaningKo || newCard.card.reversed.meaning : newCard.card.reversed.meaning)
      : (language === 'ko' ? newCard.card.upright.meaningKo || newCard.card.upright.meaning : newCard.card.upright.meaning),
    keywords: newCard.isReversed
      ? (language === 'ko' ? newCard.card.reversed.keywordsKo || newCard.card.reversed.keywords : newCard.card.reversed.keywords)
      : (language === 'ko' ? newCard.card.upright.keywordsKo || newCard.card.upright.keywords : newCard.card.upright.keywords)
  };

  return {
    ...baseContext,
    cards: [
      // Add new card at the beginning
      newCardData,
      // Keep original cards as reference context
      ...(baseContext.cards || [])
    ]
  };
}

'use client';

import Link from "next/link";
import React, { Component, ErrorInfo, ReactNode } from 'react';
import styles from './ErrorBoundary.module.css';
import { logger } from '@/lib/logger';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
}

interface State {
  hasError: boolean;
  error: Error | null;
}

class ErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error: Error): State {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    logger.error('ErrorBoundary caught an error', error);
  }

  handleReset = () => {
    this.setState({ hasError: false, error: null });
  };

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback;
      }

      return (
        <div className={styles.errorContainer} role="alert">
          <div className={styles.errorCard}>
            <div className={styles.iconWrapper}>
              <span className={styles.errorIcon}>⚠️</span>
            </div>
            <h2 className={styles.title}>Oops! Something went wrong</h2>
            <p className={styles.message}>
              {this.state.error?.message || 'An unexpected error occurred'}
            </p>
            <div className={styles.actions}>
              <button
                onClick={this.handleReset}
                className={styles.retryButton}
                type="button"
              >
                Try Again
              </button>
              <Link
                href="/"
                className={styles.homeButton}
              >
                Go Home
              </Link>
            </div>
            {process.env.NODE_ENV === 'development' && this.state.error && (
              <details className={styles.errorDetails}>
                <summary className={styles.detailsSummary}>Error Details</summary>
                <pre className={styles.errorStack}>
                  {this.state.error.stack}
                </pre>
              </details>
            )}
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;

