"use client";

import { createContext, useContext, useState, useEffect, ReactNode, useCallback } from "react";
import { useSession } from "next-auth/react";
import { useToast } from "@/components/ui/Toast";
import { logger } from "@/lib/logger";

export type NotificationType = "like" | "comment" | "reply" | "mention" | "system";

export interface Notification {
  id: string;
  type: NotificationType;
  title: string;
  message: string;
  link?: string;
  avatar?: string;
  read: boolean;
  createdAt: number;
}

interface NotificationContextType {
  notifications: Notification[];
  unreadCount: number;
  addNotification: (notification: Omit<Notification, "id" | "read" | "createdAt">) => void;
  markAsRead: (id: string) => void;
  markAllAsRead: () => void;
  deleteNotification: (id: string) => void;
  clearAll: () => void;
}

const NotificationContext = createContext<NotificationContextType | undefined>(undefined);

export function NotificationProvider({ children }: { children: ReactNode }) {
  const { data: session } = useSession();
  const toast = useToast();
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [eventSource, setEventSource] = useState<EventSource | null>(null);

  // Load notifications from localStorage
  useEffect(() => {
    const stored = localStorage.getItem("notifications");
    if (stored) {
      try {
        setNotifications(JSON.parse(stored));
      } catch (e) {
        logger.error("[NotificationContext] Failed to load notifications", e);
      }
    }
  }, []);

  // Save to localStorage whenever notifications change
  useEffect(() => {
    localStorage.setItem("notifications", JSON.stringify(notifications));
  }, [notifications]);

  // Cleanup SSE on unmount (SSE feature disabled - can be re-enabled later)
  useEffect(() => {
    if (eventSource) {
      eventSource.close();
      setEventSource(null);
    }
  }, [eventSource]);

  const addNotification = useCallback((notif: Omit<Notification, "id" | "read" | "createdAt">) => {
    const newNotification: Notification = {
      ...notif,
      id: Math.random().toString(36).substring(2, 11),
      read: false,
      createdAt: Date.now(),
    };

    setNotifications((prev) => [newNotification, ...prev].slice(0, 50)); // Keep last 50

    // Show toast for new notifications
    toast.info(notif.title, 5000);

    // Request permission and show browser notification
    if ("Notification" in window && Notification.permission === "granted") {
      new Notification(notif.title, {
        body: notif.message,
        icon: "/icon.png",
        badge: "/badge.png",
      });
    }
  }, [toast]);

  const markAsRead = useCallback((id: string) => {
    setNotifications((prev) =>
      prev.map((n) => (n.id === id ? { ...n, read: true } : n))
    );
  }, []);

  const markAllAsRead = useCallback(() => {
    setNotifications((prev) => prev.map((n) => ({ ...n, read: true })));
  }, []);

  const deleteNotification = useCallback((id: string) => {
    setNotifications((prev) => prev.filter((n) => n.id !== id));
  }, []);

  const clearAll = useCallback(() => {
    setNotifications([]);
  }, []);

  const unreadCount = notifications.filter((n) => !n.read).length;

  return (
    <NotificationContext.Provider
      value={{
        notifications,
        unreadCount,
        addNotification,
        markAsRead,
        markAllAsRead,
        deleteNotification,
        clearAll,
      }}
    >
      {children}
    </NotificationContext.Provider>
  );
}

export function useNotifications() {
  const context = useContext(NotificationContext);
  if (!context) {
    throw new Error("useNotifications must be used within NotificationProvider");
  }
  return context;
}

// Helper to request notification permission
export async function requestNotificationPermission() {
  if (!("Notification" in window)) {
    logger.warn("[NotificationContext] This browser does not support notifications");
    return false;
  }

  if (Notification.permission === "granted") {
    return true;
  }

  if (Notification.permission !== "denied") {
    const permission = await Notification.requestPermission();
    return permission === "granted";
  }

  return false;
}

/**
 * Synchronous blog posts loader (for compatibility)
 *
 * This file provides synchronous access to blog posts by reading markdown files
 * at build time. For runtime async loading, use src/data/blog/loader.ts
 */
import { readFileSync } from 'fs'
import path from 'path'
import blogMetadata from './blog/metadata/blog-metadata.json'
import { generateCurrentYearFortuneBlogPost } from './yearly-fortune-generator'

export interface BlogPost {
  slug: string
  title: string
  titleKo: string
  excerpt: string
  excerptKo: string
  content: string
  contentKo: string
  category: string
  categoryKo: string
  icon: string
  date: string
  readTime: number
  featured?: boolean
}

const POSTS_DIR = path.join(process.cwd(), 'src/data/blog/posts')

/**
 * Load all blog posts synchronously
 */
function loadBlogPostsSync(): BlogPost[] {
  return blogMetadata.map((metadata) => {
    const enPath = path.join(POSTS_DIR, `${metadata.slug}.en.md`)
    const koPath = path.join(POSTS_DIR, `${metadata.slug}.ko.md`)

    let content = ''
    let contentKo = ''

    try {
      content = readFileSync(enPath, 'utf-8')
    } catch (error) {
      console.warn(`Missing English content for ${metadata.slug}`)
    }

    try {
      contentKo = readFileSync(koPath, 'utf-8')
    } catch (error) {
      // Korean content is optional
    }

    return {
      ...metadata,
      content,
      contentKo,
    }
  })
}

// Load posts once at module initialization
let cachedPosts: BlogPost[] | null = null

export const blogPosts: BlogPost[] = (() => {
  if (cachedPosts) return cachedPosts

  try {
    cachedPosts = loadBlogPostsSync()

    // Add the generated yearly fortune post
    const yearlyPost = generateCurrentYearFortuneBlogPost()
    if (yearlyPost) {
      cachedPosts.unshift(yearlyPost)
    }

    return cachedPosts
  } catch (error) {
    console.error('Error loading blog posts:', error)
    return []
  }
})()

import { useEffect, RefObject } from 'react';

// Calculate moon phase based on date
function getMoonPhase(date: Date): number {
  const year = date.getFullYear();
  const month = date.getMonth() + 1;
  const day = date.getDate();

  let c = 0;
  let e = 0;
  let jd = 0;
  let b = 0;

  if (month < 3) {
    const yearAdjust = year - 1;
    const monthAdjust = month + 12;
    c = Math.floor(yearAdjust / 100);
    jd = Math.floor(365.25 * yearAdjust) + Math.floor(30.6001 * (monthAdjust + 1)) + day + 1720994.5;
  } else {
    c = Math.floor(year / 100);
    jd = Math.floor(365.25 * year) + Math.floor(30.6001 * (month + 1)) + day + 1720994.5;
  }

  b = 2 - c + Math.floor(c / 4);
  jd = jd + b;

  const daysSinceNew = jd - 2451549.5;
  const newMoons = daysSinceNew / 29.53;
  const phase = (newMoons - Math.floor(newMoons)) * 29.53;

  return phase / 29.53; // 0 = new moon, 0.5 = full moon
}

export function useCanvasAnimation(canvasRef: RefObject<HTMLCanvasElement>, birthDate?: string) {
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) {return;}

    const ctx = canvas.getContext('2d');
    if (!ctx) {return;}

    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    let animationId: number | null = null;
    let time = 0;
    let isRunning = false;
    let lastFrame = 0;
    const frameInterval = 1000 / 30;

    // Calculate moon phase if birthDate is provided
    const moonPhase = birthDate ? getMoonPhase(new Date(birthDate)) : getMoonPhase(new Date());

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    const drawFrame = () => {
      const width = canvas.width;
      const height = canvas.height;
      const starCount = width < 640 ? 50 : width < 1024 ? 65 : 80;

      // Deep blue/indigo gradient for dream theme
      const gradient = ctx.createLinearGradient(0, 0, width, height);
      gradient.addColorStop(0, 'rgba(10, 8, 24, 1)');
      gradient.addColorStop(0.5, 'rgba(20, 15, 45, 1)');
      gradient.addColorStop(1, 'rgba(8, 12, 30, 1)');

      ctx.fillStyle = gradient;
      ctx.fillRect(0, 0, width, height);

      // Stars with cyan/pink dream colors
      for (let i = 0; i < starCount; i++) {
        const x = (Math.sin(time * 0.3 + i * 1.5) * 0.5 + 0.5) * width;
        const y = (Math.cos(time * 0.2 + i * 0.9) * 0.5 + 0.5) * height;
        const opacity = 0.15 + Math.sin(time * 2 + i) * 0.1;
        const hue = 180 + Math.sin(time + i) * 30; // Cyan to pink range

        ctx.beginPath();
        ctx.arc(x, y, 2, 0, Math.PI * 2);
        ctx.fillStyle = `hsla(${hue}, 70%, 80%, ${opacity})`;
        ctx.fill();
      }

      // Moon with phase-based position and appearance
      const moonX = width * (0.7 + moonPhase * 0.2); // Moon moves across sky based on phase
      const moonY = height * (0.15 + Math.abs(moonPhase - 0.5) * 0.1); // Arc movement
      const baseRadius = width < 640 ? 40 : 60;
      const moonRadius = baseRadius + Math.sin(time) * 5;

      // Moon glow - brighter during full moon
      const glowIntensity = 0.1 + (1 - Math.abs(moonPhase - 0.5) * 2) * 0.15;
      const moonGradient = ctx.createRadialGradient(moonX, moonY, 0, moonX, moonY, moonRadius * 2);
      moonGradient.addColorStop(0, `rgba(168, 237, 234, ${glowIntensity})`);
      moonGradient.addColorStop(0.5, `rgba(168, 237, 234, ${glowIntensity * 0.3})`);
      moonGradient.addColorStop(1, 'transparent');

      ctx.beginPath();
      ctx.arc(moonX, moonY, moonRadius * 2, 0, Math.PI * 2);
      ctx.fillStyle = moonGradient;
      ctx.fill();

      // Draw moon body
      ctx.beginPath();
      ctx.arc(moonX, moonY, moonRadius, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(220, 240, 250, ${0.85 + Math.sin(time * 0.5) * 0.1})`;
      ctx.fill();

      // Moon phase shadow
      if (moonPhase < 0.48 || moonPhase > 0.52) {
        ctx.save();
        ctx.beginPath();
        ctx.arc(moonX, moonY, moonRadius, 0, Math.PI * 2);
        ctx.clip();

        const shadowOffset = (moonPhase - 0.5) * moonRadius * 2;
        ctx.beginPath();
        ctx.arc(moonX + shadowOffset, moonY, moonRadius, 0, Math.PI * 2);
        ctx.fillStyle = 'rgba(10, 8, 24, 0.7)';
        ctx.fill();
        ctx.restore();
      }
    };

    const animate = (timestamp = 0) => {
      if (!isRunning) {return;}
      if (timestamp - lastFrame >= frameInterval) {
        lastFrame = timestamp;
        time += 0.002;
        drawFrame();
      }
      animationId = requestAnimationFrame(animate);
    };

    const stop = () => {
      if (animationId !== null) {
        cancelAnimationFrame(animationId);
        animationId = null;
      }
      isRunning = false;
    };

    const start = () => {
      if (isRunning) {return;}
      if (mediaQuery.matches || document.hidden) {
        drawFrame();
        return;
      }
      isRunning = true;
      lastFrame = 0;
      animate();
    };

    const handleVisibility = () => {
      if (mediaQuery.matches || document.hidden) {
        stop();
        drawFrame();
        return;
      }
      start();
    };

    const handleResize = () => {
      resizeCanvas();
      if (!isRunning) {
        drawFrame();
      }
    };

    resizeCanvas();
    handleVisibility();

    window.addEventListener('resize', handleResize);
    document.addEventListener('visibilitychange', handleVisibility);
    mediaQuery.addEventListener('change', handleVisibility);

    return () => {
      stop();
      window.removeEventListener('resize', handleResize);
      document.removeEventListener('visibilitychange', handleVisibility);
      mediaQuery.removeEventListener('change', handleVisibility);
    };
  }, [canvasRef, birthDate]);
}

'use client'

import { createContext, useContext, useEffect, useMemo, useState } from 'react'
import { allExtensions } from '@/lib/i18n/extensions'

type Locale = 'en' | 'ko'
type DictValue = Record<string, unknown>

// Cache for loaded dictionaries
const dictsCache: Partial<Record<Locale, DictValue>> = {}
let dictsInitialized = false

// Async function to load dictionaries for a specific locale
async function loadLocaleDict(locale: Locale): Promise<DictValue> {
  // Return cached if available
  if (dictsCache[locale]) {
    return dictsCache[locale]!
  }

  // Dynamically import all translation files for the locale
  const modules = await Promise.all([
    import(`./locales/${locale}/common.json`),
    import(`./locales/${locale}/landing.json`),
    import(`./locales/${locale}/chat.json`),
    import(`./locales/${locale}/services.json`),
    import(`./locales/${locale}/tarot.json`),
    import(`./locales/${locale}/calendar.json`),
    import(`./locales/${locale}/personality.json`),
    import(`./locales/${locale}/dream.json`),
    import(`./locales/${locale}/numerology.json`),
    import(`./locales/${locale}/iching.json`),
    import(`./locales/${locale}/pastlife.json`),
    import(`./locales/${locale}/compatibility.json`),
    import(`./locales/${locale}/destinymap.json`),
    import(`./locales/${locale}/destinyMatch.json`),
    import(`./locales/${locale}/features.json`),
    import(`./locales/${locale}/misc.json`),
  ])

  // Deep merge helper for combining translation modules with overlapping namespaces
  const deepMerge = (target: DictValue, source: DictValue): DictValue => {
    for (const key of Object.keys(source)) {
      if (
        target[key] &&
        typeof target[key] === 'object' &&
        !Array.isArray(target[key]) &&
        typeof source[key] === 'object' &&
        !Array.isArray(source[key])
      ) {
        target[key] = deepMerge({ ...(target[key] as DictValue) }, source[key] as DictValue)
      } else {
        target[key] = source[key]
      }
    }
    return target
  }

  // Merge all modules into single dictionary (deep merge to preserve overlapping namespaces)
  const dict = modules.reduce(
    (acc, mod) => deepMerge(acc, mod.default as DictValue),
    {} as DictValue
  )

  // Merge extensions
  if (allExtensions[locale]) {
    for (const [ns, translations] of Object.entries(allExtensions[locale])) {
      dict[ns] = { ...(dict[ns] || {}), ...translations }
    }
  }

  // Cache the result
  dictsCache[locale] = dict
  return dict
}

// Fill missing translations with English fallback
function fillMissing(base: Record<string, unknown>, target: Record<string, unknown>) {
  for (const [k, v] of Object.entries(base)) {
    if (!(k in target)) {
      target[k] = v
      continue
    }
    if (
      v &&
      typeof v === 'object' &&
      !Array.isArray(v) &&
      target[k] &&
      typeof target[k] === 'object'
    ) {
      fillMissing(v as Record<string, unknown>, target[k] as Record<string, unknown>)
    }
  }
}

// Initialize dictionaries (load English by default, Korean on demand)
async function initializeDicts() {
  if (dictsInitialized) return
  dictsInitialized = true

  // Load English as the default/fallback
  await loadLocaleDict('en')
}

type I18nContextType = {
  locale: Locale
  language: Locale
  setLocale: (l: Locale) => void
  t: (path: string, fallback?: string) => string
  translate: (path: string, fallback?: string) => string
  dir: 'ltr' | 'rtl'
  hydrated: boolean
}

// Export for backward compatibility (will be empty initially, populated on demand)
export const DICTS = dictsCache

const I18nContext = createContext<I18nContextType | null>(null)

// Only support en/ko
export const SUPPORTED_LOCALES: Locale[] = ['en', 'ko']
const isRtl = (_l: Locale) => false // No RTL languages supported

export function I18nProvider({ children }: { children: React.ReactNode }) {
  const [locale, setLocale] = useState<Locale>('en')
  const [hydrated, setHydrated] = useState(false)
  const [dictsLoaded, setDictsLoaded] = useState(false)

  // Initialize dictionaries on mount
  useEffect(() => {
    initializeDicts().then(() => setDictsLoaded(true))
  }, [])

  useEffect(() => {
    setHydrated(true)
    try {
      const stored = localStorage.getItem('locale') as Locale | null
      if (stored && SUPPORTED_LOCALES.includes(stored)) {
        setLocale(stored)
        // Preload the stored locale
        loadLocaleDict(stored)
        return
      }
    } catch {}
    try {
      const nav2 = navigator.language?.slice(0, 2) as Locale | undefined
      if (nav2 && SUPPORTED_LOCALES.includes(nav2)) {
        setLocale(nav2)
        loadLocaleDict(nav2)
      }
    } catch {}
  }, [])

  // Load locale dictionary when locale changes
  useEffect(() => {
    if (!dictsLoaded) return

    loadLocaleDict(locale).then((dict) => {
      // Fill missing translations with English fallback
      if (locale !== 'en' && dictsCache.en) {
        fillMissing(dictsCache.en, dict)
      }
      // Force re-render after loading
      setDictsLoaded(true)
    })
  }, [locale, dictsLoaded])

  useEffect(() => {
    try {
      localStorage.setItem('locale', locale)
    } catch {}
    if (typeof document !== 'undefined') {
      document.documentElement.lang = locale
      document.documentElement.dir = isRtl(locale) ? 'rtl' : 'ltr'
    }
  }, [locale])

  const t = useMemo(() => {
    const getter = (obj: unknown, path: string) => {
      if (!path) {
        return undefined
      }
      const parts = path.split('.')
      let cur: unknown = obj
      for (const k of parts) {
        if (cur !== null && typeof cur === 'object' && k in (cur as Record<string, unknown>)) {
          cur = (cur as Record<string, unknown>)[k]
        } else {
          return undefined
        }
      }
      return cur
    }

    const isLikelyCorrupted = (value: string) =>
      /[\u0400-\u04FF]/.test(value) || value.includes('�')

    return (path: string, fallback?: string) => {
      const currentDict = dictsCache[locale]
      if (!currentDict) {
        // Return fallback or key if dict not loaded yet
        return fallback || path.split('.').pop() || path
      }

      const got = getter(currentDict, path)
      if (typeof got === 'string') {
        // Some locale files were corrupted (Cyrillic/�). If detected, fall back to English.
        if (locale === 'ko' && isLikelyCorrupted(got)) {
          const fb = getter(dictsCache.en, path)
          if (typeof fb === 'string') {
            return fb
          }
          if (fallback) {
            return fallback
          }
          return path.split('.').pop() || path
        }
        return got
      }

      const fb = getter(dictsCache.en, path)
      if (typeof fb === 'string') {
        return fb
      }

      if (fallback) {
        return fallback
      }

      return path.split('.').pop() || path
    }
  }, [locale, dictsLoaded])

  const value = useMemo<I18nContextType>(
    () => ({
      locale,
      language: locale,
      setLocale,
      t,
      translate: t,
      dir: isRtl(locale) ? 'rtl' : 'ltr',
      hydrated,
    }),
    [locale, t, hydrated]
  )

  return <I18nContext.Provider value={value}>{children}</I18nContext.Provider>
}

export function useI18n() {
  const ctx = useContext(I18nContext)
  if (!ctx) {
    throw new Error('useI18n must be used within I18nProvider')
  }
  return ctx
}

/**
 * @file Comprehensive Astrology Compatibility Analysis
 * 종합 점성학 궁합 분석 (기본 + 확장)
 */

import type { AstrologyProfile } from '../cosmicCompatibility';
import type { AspectAnalysis, SynastryAnalysis } from './types';
import { analyzeAspects, analyzeSynastry } from './basic-analysis';
import { analyzeCompositeChart, analyzeHouseOverlays, type CompositeChartAnalysis, type HouseOverlayAnalysis } from './composite-house';
import { analyzeMercuryAspects, analyzeJupiterAspects, analyzeSaturnAspects, type MercuryAspectAnalysis, type JupiterAspectAnalysis, type SaturnAspectAnalysis } from './planet-analysis';
import { analyzeOuterPlanets, type OuterPlanetAnalysis } from './outer-planets';
import { analyzeNodes, analyzeLilith, type NodeAnalysis, type LilithAnalysis } from './nodes-lilith';
import { analyzeDavisonChart, analyzeProgressedChart, type DavisonChartAnalysis, type ProgressedChartAnalysis } from './davison-progressed';
import { analyzeDegreeBasedAspects, type DegreeAspectAnalysis } from './degree-aspects';

// ============================================================
// Basic Comprehensive Analysis
// ============================================================

export interface ComprehensiveAstrologyCompatibility {
  aspects: AspectAnalysis;
  synastry: SynastryAnalysis;
  compositeChart: CompositeChartAnalysis;
  houseOverlays: HouseOverlayAnalysis;
  overallScore: number;
  grade: 'S+' | 'S' | 'A' | 'B' | 'C' | 'D' | 'F';
  summary: string;
  detailedInsights: string[];
}

export function performComprehensiveAstrologyAnalysis(
  p1: AstrologyProfile,
  p2: AstrologyProfile
): ComprehensiveAstrologyCompatibility {
  const aspects = analyzeAspects(p1, p2);
  const synastry = analyzeSynastry(p1, p2);
  const compositeChart = analyzeCompositeChart(p1, p2);
  const houseOverlays = analyzeHouseOverlays(p1, p2);

  // 종합 점수
  const overallScore = Math.round(
    aspects.overallHarmony * 0.3 +
    synastry.compatibilityIndex * 0.4 +
    compositeChart.longevityPotential * 0.3
  );

  let grade: ComprehensiveAstrologyCompatibility['grade'];
  if (overallScore >= 95) {grade = 'S+';}
  else if (overallScore >= 85) {grade = 'S';}
  else if (overallScore >= 75) {grade = 'A';}
  else if (overallScore >= 65) {grade = 'B';}
  else if (overallScore >= 50) {grade = 'C';}
  else if (overallScore >= 35) {grade = 'D';}
  else {grade = 'F';}

  // 요약
  let summary = '';
  if (grade === 'S+' || grade === 'S') {
    summary = '점성학적으로 매우 이상적인 궁합입니다. Aspects, Synastry, Composite Chart 모두 조화롭습니다.';
  } else if (grade === 'A' || grade === 'B') {
    summary = '점성학적으로 좋은 궁합입니다. 자연스러운 흐름 속에서 관계가 발전합니다.';
  } else if (grade === 'C') {
    summary = '점성학적으로 보통의 궁합입니다. 노력으로 좋은 관계를 만들 수 있습니다.';
  } else {
    summary = '점성학적으로 도전적인 궁합입니다. 깊은 이해와 조율이 필요합니다.';
  }

  // 상세 인사이트
  const detailedInsights = [
    ...aspects.keyInsights,
    ...synastry.strengths,
    `관계 목적: ${compositeChart.relationshipPurpose}`,
    `핵심 테마: ${compositeChart.coreTheme}`,
  ];

  return {
    aspects,
    synastry,
    compositeChart,
    houseOverlays,
    overallScore,
    grade,
    summary,
    detailedInsights,
  };
}

// ============================================================
// Extended Astrology Profile
// ============================================================

export interface ExtendedAstrologyProfile extends AstrologyProfile {
  mercury?: { sign: string; element: string; degree?: number };
  jupiter?: { sign: string; element: string };
  saturn?: { sign: string; element: string };
  uranus?: { sign: string; element: string };
  neptune?: { sign: string; element: string };
  pluto?: { sign: string; element: string };
  northNode?: { sign: string; element: string };
  southNode?: { sign: string; element: string };
  lilith?: { sign: string; element: string };
}

// ============================================================
// Extended Comprehensive Analysis
// ============================================================

export interface ExtendedAstrologyCompatibility extends ComprehensiveAstrologyCompatibility {
  degreeBasedAspects?: DegreeAspectAnalysis;
  mercuryAnalysis?: MercuryAspectAnalysis;
  jupiterAnalysis?: JupiterAspectAnalysis;
  saturnAnalysis?: SaturnAspectAnalysis;
  outerPlanetsAnalysis?: OuterPlanetAnalysis;
  nodeAnalysis?: NodeAnalysis;
  lilithAnalysis?: LilithAnalysis;
  davisonChart?: DavisonChartAnalysis;
  progressedChart?: ProgressedChartAnalysis;
  extendedScore: number;
  extendedGrade: 'S+' | 'S' | 'A' | 'B' | 'C' | 'D' | 'F';
  extendedSummary: string;
  extendedInsights: string[];
}

export function performExtendedAstrologyAnalysis(
  p1: ExtendedAstrologyProfile,
  p2: ExtendedAstrologyProfile,
  yearsInRelationship: number = 0
): ExtendedAstrologyCompatibility {
  // 기본 분석
  const baseAnalysis = performComprehensiveAstrologyAnalysis(p1, p2);

  // 확장 분석
  let degreeBasedAspects: DegreeAspectAnalysis | undefined;
  let mercuryAnalysis: MercuryAspectAnalysis | undefined;
  let jupiterAnalysis: JupiterAspectAnalysis | undefined;
  let saturnAnalysis: SaturnAspectAnalysis | undefined;
  let outerPlanetsAnalysis: OuterPlanetAnalysis | undefined;
  let nodeAnalysis: NodeAnalysis | undefined;
  let lilithAnalysis: LilithAnalysis | undefined;
  let davisonChart: DavisonChartAnalysis | undefined;
  let progressedChart: ProgressedChartAnalysis | undefined;

  const scores: number[] = [baseAnalysis.overallScore];
  const extendedInsights: string[] = [...baseAnalysis.detailedInsights];

  // Degree-based Aspects
  const p1Planets: { name: string; sign: string; degree?: number }[] = [
    { name: 'Sun', sign: p1.sun.sign },
    { name: 'Moon', sign: p1.moon.sign },
    { name: 'Venus', sign: p1.venus.sign },
    { name: 'Mars', sign: p1.mars.sign },
  ];
  const p2Planets: { name: string; sign: string; degree?: number }[] = [
    { name: 'Sun', sign: p2.sun.sign },
    { name: 'Moon', sign: p2.moon.sign },
    { name: 'Venus', sign: p2.venus.sign },
    { name: 'Mars', sign: p2.mars.sign },
  ];

  if (p1.mercury) {p1Planets.push({ name: 'Mercury', sign: p1.mercury.sign, degree: p1.mercury.degree });}
  if (p2.mercury) {p2Planets.push({ name: 'Mercury', sign: p2.mercury.sign, degree: p2.mercury.degree });}

  degreeBasedAspects = analyzeDegreeBasedAspects(p1Planets, p2Planets);
  scores.push(degreeBasedAspects.overallBalance);

  if (degreeBasedAspects.tightestAspect) {
    extendedInsights.push(`가장 강한 Aspect: ${degreeBasedAspects.tightestAspect.planet1}-${degreeBasedAspects.tightestAspect.planet2} ${degreeBasedAspects.tightestAspect.aspectType}`);
  }

  // Mercury Analysis
  if (p1.mercury && p2.mercury) {
    mercuryAnalysis = analyzeMercuryAspects(p1.mercury, p2.mercury, p1.sun, p2.sun);
    scores.push(mercuryAnalysis.mercuryCompatibility);
    extendedInsights.push(`소통 스타일: ${mercuryAnalysis.communicationStyle}`);
  }

  // Jupiter Analysis
  if (p1.jupiter && p2.jupiter) {
    jupiterAnalysis = analyzeJupiterAspects(p1.jupiter, p2.jupiter, p1.sun, p2.sun);
    scores.push(jupiterAnalysis.expansionCompatibility);
    extendedInsights.push(`공유 신념: ${jupiterAnalysis.sharedBeliefs}`);
  }

  // Saturn Analysis
  if (p1.saturn && p2.saturn) {
    saturnAnalysis = analyzeSaturnAspects(p1.saturn, p2.saturn, p1.sun, p2.sun, p1.moon, p2.moon);
    scores.push(saturnAnalysis.longTermPotential);
    extendedInsights.push(`카르마 교훈: ${saturnAnalysis.karmicLesson}`);
  }

  // Outer Planets Analysis
  outerPlanetsAnalysis = analyzeOuterPlanets(
    { uranus: p1.uranus, neptune: p1.neptune, pluto: p1.pluto },
    { uranus: p2.uranus, neptune: p2.neptune, pluto: p2.pluto },
    p1.sun, p2.sun
  );
  scores.push(outerPlanetsAnalysis.overallTranscendentScore);

  if (outerPlanetsAnalysis.generationalThemes.length > 0) {
    extendedInsights.push(`세대적 테마: ${outerPlanetsAnalysis.generationalThemes[0]}`);
  }

  // Node Analysis
  nodeAnalysis = analyzeNodes(
    p1.northNode, p1.southNode,
    p2.northNode, p2.southNode,
    p1.sun, p2.sun, p1.moon, p2.moon
  );
  scores.push(nodeAnalysis.northNodeConnection.compatibility);
  extendedInsights.push(`카르마 관계 유형: ${nodeAnalysis.karmicRelationshipType}`);
  extendedInsights.push(`진화적 목적: ${nodeAnalysis.evolutionaryPurpose}`);

  // Lilith Analysis
  lilithAnalysis = analyzeLilith(p1.lilith, p2.lilith, p1.sun, p2.sun, p1.mars, p2.mars, p1.venus, p2.venus);
  scores.push(lilithAnalysis.magneticAttraction);

  if (lilithAnalysis.shadowDynamics) {
    extendedInsights.push(`그림자 역학: ${lilithAnalysis.shadowDynamics}`);
  }

  // Davison Chart
  davisonChart = analyzeDavisonChart(p1, p2);
  extendedInsights.push(`관계 정체성: ${davisonChart.relationshipIdentity}`);
  extendedInsights.push(`관계 목적: ${davisonChart.relationshipPurpose}`);

  // Progressed Chart
  progressedChart = analyzeProgressedChart(p1, p2, yearsInRelationship);
  extendedInsights.push(`현재 관계 테마: ${progressedChart.currentRelationshipTheme}`);

  // 종합 확장 점수
  const extendedScore = Math.round(scores.reduce((a, b) => a + b, 0) / scores.length);

  let extendedGrade: ExtendedAstrologyCompatibility['extendedGrade'];
  if (extendedScore >= 95) {extendedGrade = 'S+';}
  else if (extendedScore >= 85) {extendedGrade = 'S';}
  else if (extendedScore >= 75) {extendedGrade = 'A';}
  else if (extendedScore >= 65) {extendedGrade = 'B';}
  else if (extendedScore >= 50) {extendedGrade = 'C';}
  else if (extendedScore >= 35) {extendedGrade = 'D';}
  else {extendedGrade = 'F';}

  // 확장 요약
  let extendedSummary = baseAnalysis.summary;
  if (nodeAnalysis.karmicRelationshipType === 'soulmate') {
    extendedSummary += ' 노드 분석에서 소울메이트 연결이 발견되었습니다.';
  } else if (nodeAnalysis.karmicRelationshipType === 'karmic') {
    extendedSummary += ' 전생의 인연이 있을 가능성이 높습니다.';
  }

  if (lilithAnalysis.magneticAttraction >= 80) {
    extendedSummary += ' 강한 자기적 끌림이 존재합니다.';
  }

  if (saturnAnalysis && saturnAnalysis.longTermPotential >= 80) {
    extendedSummary += ' 장기적 안정성이 높은 관계입니다.';
  }

  return {
    ...baseAnalysis,
    degreeBasedAspects,
    mercuryAnalysis,
    jupiterAnalysis,
    saturnAnalysis,
    outerPlanetsAnalysis,
    nodeAnalysis,
    lilithAnalysis,
    davisonChart,
    progressedChart,
    extendedScore,
    extendedGrade,
    extendedSummary,
    extendedInsights,
  };
}
